INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(83, 'Gìn giữ, vun đắp mối quan hệ đoàn kết đặc biệt Việt Nam-Lào', 'gin-giu-vun-dap-moi-quan-he-doan-ket-dac-biet-viet-nam-lao', '<p>Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng v&agrave; Tổng B&iacute; thư, Chủ tịch nước L&agrave;o Bounnhang Vorachith chiều nay (13/8) đ&atilde; c&oacute; cuộc điện đ&agrave;m để trao đổi về t&igrave;nh h&igrave;nh v&agrave; quan hệ Việt Nam-L&agrave;o.</p>', '<p>Tổng B&iacute; thư, Chủ tịch nước L&agrave;o Bounnhang Vorachith b&agrave;y tỏ vui mừng được điện đ&agrave;m với Tổng B&iacute; thư, Chủ tịch nước&nbsp;<a href=\"https://vietnamnet.vn/nguyen-phu-trong-tag41000.html\" target=\"_blank\">Nguyễn Ph&uacute; Trọng</a>; gửi lời chia buồn tới Đảng, Nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n Việt Nam về việc nguy&ecirc;n Tổng B&iacute; thư L&ecirc; Khả Phi&ecirc;u từ trần.</p>\r\n\r\n<p>L&atilde;nh đạo&nbsp;L&agrave;o ch&uacute;c mừng những th&agrave;nh tựu to lớn m&agrave; nh&acirc;n d&acirc;n Việt Nam đ&atilde; đạt được trong thời gian qua; b&agrave;y tỏ tin tưởng dưới sự l&atilde;nh đạo của Đảng Cộng sản Việt Nam, nh&acirc;n d&acirc;n Việt Nam sẽ tiếp tục gi&agrave;nh được nhiều th&agrave;nh tựu mới, to lớn hơn nữa....</p>\r\n\r\n<p>Tổng B&iacute; thư, Chủ tịch nước L&agrave;o đ&aacute;nh gi&aacute; cao c&aacute;c nỗ lực, kinh nghiệm của Việt Nam trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống v&agrave; kiểm so&aacute;t dịch Covid-19 vừa qua v&agrave; b&agrave;y tỏ tin tưởng Việt Nam sẽ tiếp tục ngăn chặn v&agrave; kiểm so&aacute;t tốt dịch bệnh Covid-19.&nbsp;Cảm ơn Việt Nam đ&atilde; chia sẻ kinh nghiệm, hỗ trợ L&agrave;o v&agrave; mong muốn hai nước tăng cường trao đổi th&ocirc;ng tin, phối hợp v&agrave; hỗ trợ nhau trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch Covid-19.</p>\r\n\r\n<p>Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng đ&aacute;nh gi&aacute; cao &yacute; nghĩa của cuộc điện đ&agrave;m, cảm ơn Đảng, Quốc hội, Ch&iacute;nh phủ v&agrave;&nbsp;UB TƯ Mặt trận L&agrave;o X&acirc;y dựng đất nước đ&atilde; gửi điện chia buồn về việc nguy&ecirc;n Tổng B&iacute; thư L&ecirc; Khả Phi&ecirc;u từ trần, thể hiện s&acirc;u đậm mối quan hệ gắn b&oacute; thủy chung, vui buồn c&oacute; nhau giữa hai Đảng, hai Nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n hai nước.</p>\r\n\r\n<p>Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng ch&uacute;c mừng những th&agrave;nh tựu m&agrave; L&agrave;o đ&atilde; đạt được trong thời gian qua v&agrave; b&agrave;y tỏ tin tưởng, dưới sự l&atilde;nh đạo của Đảng Nh&acirc;n d&acirc;n C&aacute;ch mạng L&agrave;o, nh&acirc;n d&acirc;n L&agrave;o sẽ tiếp tục gi&agrave;nh được nhiều th&agrave;nh tựu mới...</p>\r\n\r\n<p>Tổng B&iacute; thư, Chủ tịch nước&nbsp;<a href=\"https://vietnamnet.vn/tong-bi-thu-nguyen-phu-trong-tag58752.html\" target=\"_blank\">Nguyễn Ph&uacute; Trọng&nbsp;</a>ch&acirc;n th&agrave;nh cảm ơn Tổng B&iacute; thư, Chủ tịch nước L&agrave;o đ&atilde; thăm hỏi v&agrave; chia sẻ về t&igrave;nh h&igrave;nh ph&ograve;ng, chống dịch Covid-19; cảm ơn L&agrave;o đ&atilde; phối hợp chặt chẽ với Việt Nam trong khu&ocirc;n khổ song phương cũng như khu&ocirc;n khổ hợp t&aacute;c ASEAN.&nbsp;</p>\r\n\r\n<p>Đề nghị hai nước tiếp tục hợp t&aacute;c chặt chẽ, siết chặt tay nhau, chung sức chung l&ograve;ng, kề vai s&aacute;t c&aacute;nh c&ugrave;ng chống v&agrave; chiến thắng đại dịch Covid-19 để hai nước c&ugrave;ng sớm ổn định t&igrave;nh h&igrave;nh, tiếp tục ph&aacute;t triển KT-XH v&agrave; n&acirc;ng cao đời sống nh&acirc;n d&acirc;n.</p>\r\n\r\n<p>Hai nh&agrave; l&atilde;nh đạo&nbsp;đ&atilde; th&ocirc;ng b&aacute;o kh&aacute;i qu&aacute;t cho nhau về t&igrave;nh h&igrave;nh mỗi Đảng, mỗi nước; trao đổi về quan hệ hợp t&aacute;c hai b&ecirc;n v&agrave; t&igrave;nh h&igrave;nh thế giới, khu vực m&agrave; hai b&ecirc;n c&ugrave;ng quan t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai nh&agrave; l&atilde;nh đạo đ&aacute;nh gi&aacute; cao về mối quan hệ hợp t&aacute;c giữa hai Đảng, hai nước trong thời gian qua kh&ocirc;ng ngừng được củng cố v&agrave; ph&aacute;t triển s&acirc;u rộng tr&ecirc;n tất cả c&aacute;c lĩnh vực, g&oacute;p phần giữ vững ổn định ch&iacute;nh trị v&agrave; ph&aacute;t triển&nbsp;KT-XH ở mỗi nước. Khẳng định hai b&ecirc;n c&ugrave;ng nhau giữ g&igrave;n, vun đắp, ph&aacute;t huy v&agrave; truyền tiếp cho c&aacute;c thế hệ mai sau về mối quan hệ đo&agrave;n kết đặc biệt&nbsp;<a href=\"https://vietnamnet.vn/viet-lao-tag148872.html\" target=\"_blank\">Việt Nam-L&agrave;o</a>.</p>\r\n\r\n<p>Hai b&ecirc;n đ&aacute;nh gi&aacute; cao sự nỗ lực của c&aacute;c bộ, ban, ng&agrave;nh, địa phương của hai nước đ&atilde; c&oacute; nhiều cố gắng trong việc phối hợp thực hiện c&aacute;c thỏa thuận giữa l&atilde;nh đạo cấp cao hai Đảng, c&aacute;c hiệp định, thỏa thuận hợp t&aacute;c giữa hai nước.</p>\r\n\r\n<p>Phối hợp tổ chức th&agrave;nh c&ocirc;ng Đại hội to&agrave;n quốc của mỗi Đảng; tăng cường hợp t&aacute;c về QP-AN; n&acirc;ng cao hiệu quả hợp t&aacute;c tr&ecirc;n c&aacute;c lĩnh vực kinh tế, văn h&oacute;a, gi&aacute;o dục v&agrave; khoa học-kỹ thuật; t&iacute;ch cực chuẩn bị tốt cho Kỳ họp lần thứ 43 Ủy ban li&ecirc;n Ch&iacute;nh phủ hai nước. C&oacute; c&aacute;c giải ph&aacute;p cụ thể để th&aacute;o gỡ những kh&oacute; khăn vướng mắc đưa hợp t&aacute;c kinh tế hai nước ph&aacute;t triển theo hướng bền vững.</p>\r\n\r\n<p>Hai nh&agrave; l&atilde;nh đạo khẳng định tiếp tục chia sẻ th&ocirc;ng tin kịp thời v&agrave; phối hợp chặt chẽ, hiệu quả, thiết thực, ủng hộ nhau trong c&aacute;c vấn đề quốc tế v&agrave; khu vực, nhất l&agrave; trong khu&ocirc;n khổ hợp t&aacute;c ASEAN, Li&ecirc;n hợp quốc, WTO, ASEM v&agrave; c&aacute;c cơ chế hợp t&aacute;c Tiểu v&ugrave;ng Mekong.</p>\r\n\r\n<p>Phối hợp với Campuchia thực hiện c&oacute; hiệu quả thỏa thuận giữa ba Thủ tướng về khu vực Tam gi&aacute;c ph&aacute;t triển giữa ba nước, đồng thời phối hợp với c&aacute;c nước trong việc quản l&yacute; v&agrave; sử dụng bền vững, hiệu quả nguồn nước s&ocirc;ng Mekong v&igrave; mục ti&ecirc;u ph&aacute;t triển bền vững trong khu vực.</p>', 'ibuK_tong-bi-thu-chu-tich-nuoc-nguyen-phu-trong-dien-dam-voi-tong-bi-thu-chu-tich-nuoc-lao-1.jpg', 0, 0, 3, '2020-08-18', '2020-08-14'),
(84, 'Ông Vương Đình Huệ: Đưa Hà Nội thành địa phương đứng đầu về thương mại điện tử', 'ong-vuong-dinh-hue-dua-ha-noi-thanh-dia-phuong-dung-dau-ve-thuong-mai-dien-tu', '<p>H&agrave; Nội đề nghị Bộ C&ocirc;ng Thương hỗ trợ trong triển khai thương mại điện tử để TP trở th&agrave;nh địa phương đứng đầu cả nước về lĩnh vực n&agrave;y.</p>', '<p>Chiều nay (13/8), Thường trực Th&agrave;nh uỷ H&agrave; Nội c&oacute; cuộc l&agrave;m việc với Ban C&aacute;n sự Đảng Bộ C&ocirc;ng Thương về kết quả phối hợp c&ocirc;ng t&aacute;c giữa Bộ C&ocirc;ng Thương v&agrave; UBND TP H&agrave; Nội.</p>\r\n\r\n<p>Ph&aacute;t biểu mở đầu, B&iacute; thư Th&agrave;nh ủy Vương Đ&igrave;nh Huệ nhấn mạnh, thời gian qua, lĩnh vực c&ocirc;ng nghiệp v&agrave; thương mại của Thủ đ&ocirc; đ&atilde; đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o tốc độ tăng trưởng kinh tế của H&agrave; Nội cũng như cả nước.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo &ocirc;ng Huệ, b&ecirc;n cạnh những thuận lợi, H&agrave; Nội cũng đang gặp phải nhiều kh&oacute; khăn như vấn đề kết nối v&agrave; ph&aacute;t triển thương mại hiện đại, vấn đề xử l&yacute; r&aacute;c thải&hellip;</p>\r\n\r\n<p>L&atilde;nh đạo Th&agrave;nh uỷ mong muốn, sau buổi l&agrave;m việc với Ban C&aacute;n sự đảng Bộ C&ocirc;ng Thương, H&agrave; Nội sẽ ph&aacute;t huy được những tiềm năng, thế mạnh trong lĩnh vực c&ocirc;ng nghiệp, thương mại. Đồng thời, giải quyết được những vấn đề kh&oacute; khăn, tồn tại m&agrave; Thủ đ&ocirc; cần sớm t&igrave;m ra hướng giải quyết hiệu quả.</p>\r\n\r\n<p>Bộ trưởng Bộ C&ocirc;ng Thương Trần Tuấn Anh b&agrave;y tỏ, định hướng ph&aacute;t triển kinh tế của TP H&agrave; Nội sẽ tạo điều kiện cho c&aacute;c doanh nghiệp khai th&aacute;c cơ hội, dư địa của TP về ph&aacute;t triển thương mại nội địa, xuất khẩu.</p>\r\n\r\n<p>Về lĩnh vực thương mại điện tử v&agrave; chuyển đổi số mặc d&ugrave; c&ograve;n tồn tại, bất cập về thể chế, ch&iacute;nh s&aacute;ch nhưng thương mại điện tử đang ph&aacute;t triển mạnh mẽ, gắn với hạ tầng thương mại truyền thống. V&igrave; vậy, Bộ C&ocirc;ng Thương mong H&agrave; Nội tăng cường đầu tư ph&aacute;t triển hạ tầng cho loại h&igrave;nh thương mại n&agrave;y.</p>\r\n\r\n<p>Bộ trưởng C&ocirc;ng Thương cho hay, chiến lược ph&aacute;t triển của c&aacute;c c&ocirc;ng ty b&aacute;n lẻ trực tuyến to&agrave;n cầu lớn... thường chọn nơi c&oacute; c&aacute;c trung t&acirc;m lớn để l&agrave;m &quot;bệ đỡ&quot;. Bởi theo t&iacute;nh to&aacute;n, họ sẽ tiết kiệm được khoảng 40-50% chi ph&iacute; trong tổng doanh thu nếu c&oacute; c&aacute;c trung t&acirc;m logistic lớn đi k&egrave;m, bổ trợ.</p>\r\n\r\n<p>&Ocirc;ng cho rằng, H&agrave; Nội rất cần x&acirc;y dựng 1-2 trung t&acirc;m logistic lớn để thu h&uacute;t c&aacute;c tập đo&agrave;n đa quốc gia trong lĩnh vực thương mại điện tử tới đầu tư.</p>\r\n\r\n<p>Chưa kể, c&aacute;c trung t&acirc;m n&agrave;y sẽ gi&uacute;p đ&aacute;p ứng xuất khẩu tại chỗ, phục vụ nhu cầu sản phẩm h&agrave;ng ho&aacute;, dịch vụ của người d&acirc;n Thủ đ&ocirc;.</p>\r\n\r\n<p>&quot;Đ&acirc;y n&ecirc;n l&agrave; chiến lược để tạo đột ph&aacute; ph&aacute;t triển thương mại điện tử ở H&agrave; Nội thời gian tới&quot;, &ocirc;ng Trần Tuấn Anh n&ecirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đẩy mạnh hợp t&aacute;c</p>\r\n\r\n<p>Kết luận cuộc l&agrave;m việc, B&iacute; thư Th&agrave;nh ủy Vương Đ&igrave;nh Huệ thống nhất với những nội dung hai b&ecirc;n sẽ đẩy mạnh, tiếp tục phối hợp trong thời gian tới.</p>\r\n\r\n<p>Hai b&ecirc;n sẽ phối hợp v&agrave; hỗ trợ lẫn nhau trong x&uacute;c tiến đầu tư FDI v&agrave; c&aacute;c cụm tiểu thủ c&ocirc;ng nghiệp, khu c&ocirc;ng nghệ cao Ho&agrave; Lạc. Th&ocirc;ng qua tham t&aacute;n thương mại gi&uacute;p TP tổ chức c&aacute;c đo&agrave;n x&uacute;c tiến thương mại, giới thiệu tiềm năng đầu tư, kinh doanh.</p>\r\n\r\n<p>Trong lĩnh vực thương mại, B&iacute; thư H&agrave; Nội đề nghị Bộ C&ocirc;ng Thương hỗ trợ H&agrave; Nội x&acirc;y dựng hệ thống thương mại văn minh, hiện đại, đ&aacute;p ứng y&ecirc;u cầu hội nhập v&agrave; phục vụ c&aacute;c sự kiện lớn của TP.</p>\r\n\r\n<p>Hỗ trợ trong triển khai c&oacute; hiệu quả đề &aacute;n quản l&yacute; v&agrave; ph&aacute;t triển hoạt động logistic tr&ecirc;n địa b&agrave;n đến năm 2025.</p>\r\n\r\n<p>Ngo&agrave;i ra, Bộ tiếp tục hỗ trợ TP trong triển khai đề &aacute;n ph&aacute;t triển trung t&acirc;m cung ứng n&ocirc;ng sản hiện đại, chợ đầu mối tại Y&ecirc;n Thường (Gia L&acirc;m) v&agrave; c&aacute;c chợ đầu mối kh&aacute;c tr&ecirc;n địa b&agrave;n TP.</p>\r\n\r\n<p>Đặc biệt, hỗ trợ TP trong triển khai thương mại điện tử để H&agrave; Nội trở th&agrave;nh địa phương đứng đầu trong cả nước về lĩnh vực n&agrave;y...</p>', 'Bg5q_ong-vuong-dinh-hue-xay-dung-ha-noi-thanh-dia-phuong-dung-dau-ve-thuong-mai-dien-tu.jpg', 0, 0, 3, '2020-08-14', '2020-08-14'),
(85, 'Nhiều nước gửi điện chia buồn nguyên Tổng Bí thư Lê Khả Phiêu từ trần', 'nhieu-nuoc-gui-dien-chia-buon-nguyen-tong-bi-thu-le-kha-phieu-tu-tran', '<p>L&atilde;nh đạo c&aacute;c nước, c&aacute;c Đảng đ&atilde; gửi điện, thư chia buồn tới l&atilde;nh đạo Đảng, Nh&agrave; nước, nh&acirc;n d&acirc;n Việt Nam v&agrave; gia quyến&nbsp;nguy&ecirc;n Tổng B&iacute; thư&nbsp;L&ecirc; Khả Phi&ecirc;u.</p>', '<p>Trong điện chia buồn gửi tới BCH Trung ương Đảng ta, BCH Trung ương Đảng Lao động Triều Ti&ecirc;n b&agrave;y tỏ chia buồn s&acirc;u sắc, khẳng định&nbsp;nguy&ecirc;n Tổng B&iacute; thư&nbsp;L&ecirc; Khả Phi&ecirc;u l&agrave; nh&agrave; c&aacute;ch mạng l&atilde;o th&agrave;nh, nh&agrave; hoạt động ch&iacute;nh trị lỗi lạc, cống hiến cả đời cho sự nghiệp ph&aacute;t triển, thống nhất đất nước v&agrave; sự nghiệp x&acirc;y dựng&nbsp;CNXH của Việt Nam.</p>\r\n\r\n<p>Trong điện chia buồn gửi tới Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng, thay mặt Ch&iacute;nh phủ v&agrave; nh&acirc;n d&acirc;n H&agrave;n Quốc, Tổng thống Moon Jae-in đ&atilde; gửi lời chia buồn tới nh&acirc;n d&acirc;n Việt Nam v&agrave; gia quyến nguy&ecirc;n Tổng B&iacute; thư<a href=\"https://vietnamnet.vn/le-kha-phieu-tag33350.html\" target=\"_blank\">&nbsp;L&ecirc; Khả Phi&ecirc;u</a>.</p>\r\n\r\n<p>Tổng thống H&agrave;n Quốc&nbsp;nhấn mạnh: nguy&ecirc;n Tổng B&iacute; thư L&ecirc; Khả Phi&ecirc;u đ&atilde; c&oacute; nhiều cống hiến quan trọng cho sự ph&aacute;t triển của Việt Nam v&agrave; sự ph&aacute;t triển của quan hệ hai nước H&agrave;n Quốc - Việt Nam.</p>\r\n\r\n<p>Thủ tướng Vương quốc Th&aacute;i Lan Prayut Chan-o-cha đ&atilde; gửi điện chia buồn tới Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c; Bộ trưởng Ngoại giao Vương quốc Th&aacute;i Lan Don Pramudwinai đ&atilde; gửi Điện chia buồn tới Ph&oacute; Thủ tướng, Bộ trưởng Ngoại giao Phạm B&igrave;nh Minh.</p>\r\n\r\n<p>L&atilde;nh đạo Th&aacute;i Lan gửi lời chia buồn s&acirc;u sắc nhất, đ&aacute;nh gi&aacute; cao những đ&oacute;ng g&oacute;p to lớn của nguy&ecirc;n Tổng B&iacute; thư L&ecirc; Khả Phi&ecirc;u cho sự nghiệp ph&aacute;t triển v&agrave; c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a đất nước Việt Nam, được nh&acirc;n d&acirc;n Việt Nam v&agrave; to&agrave;n khu vực ghi nhận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tổng B&iacute; thư Đảng mặt trận giải ph&oacute;ng d&acirc;n tộc Sandino, Tổng thống Cộng h&ograve;a Nicaragua Daniel Ortega Saavedra v&agrave; Ph&oacute; Tổng thống Cộng h&ograve;a Nicaragua Rosario Murillo đ&atilde; đồng gửi thư chia buồn tới Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng.</p>\r\n\r\n<p>Chủ tịch Đảng Tổ chức Nh&acirc;n d&acirc;n T&acirc;y Nam Phi, Tổng thống Cộng ho&agrave; Nam-mi-bi-<br />\r\nHage Gottfried Geingob;&nbsp;Tổng B&iacute; thư Đảng Mặt trận Nh&acirc;n d&acirc;n Giải ph&oacute;ng T&acirc;y Xa-ha-ra, Tổng thống Cộng ho&agrave; Ả-rập Xa-ra-uy D&acirc;n chủ Brahim Ghali đ&atilde; gửi thư chia buồn đến&nbsp;Tổng B&iacute; thư, Chủ tịch nước Nguyễn Ph&uacute; Trọng.</p>\r\n\r\n<p>Ngo&agrave;i ra, l&atilde;nh đạo c&aacute;c Đảng ở c&aacute;c nước cũng đ&atilde; c&oacute; điện chia buồn gửi tới l&atilde;nh đạo Đảng, Nh&agrave; nước, nh&acirc;n d&acirc;n Việt Nam v&agrave; gia quyến&nbsp;nguy&ecirc;n Tổng B&iacute; thư&nbsp;L&ecirc; Khả Phi&ecirc;u.</p>\r\n\r\n<p>Th&agrave;nh Nam</p>', '4BDd_lanh-dao-cac-nuoc-chia-buon-nguyen-tong-bi-thu-le-kha-phieu-tu-tran.jpg', 0, 0, 3, '2020-08-15', '2020-08-14'),
(86, 'Ban Bí thư Trung ương Đảng chỉ định nhân sự mới', 'ban-bi-thu-trung-uong-dang-chi-dinh-nhan-su-moi', '<p>S&aacute;ng nay (13/8), hội nghị Ban Chấp h&agrave;nh Đảng bộ tỉnh Nghệ An đ&atilde; c&ocirc;ng bố quyết định của Ban B&iacute; thư chỉ định Ủy vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ, Ban Thường vụ Tỉnh ủy nhiệm kỳ 2015 - 2020.</p>', '<p>&Ocirc;ng Th&aacute;i Thanh Qu&yacute;, Ủy vi&ecirc;n dự khuyết Trung ương Đảng, B&iacute; thư Tỉnh ủy đ&atilde; trao quyết định về việc chỉ định Đại t&aacute; V&otilde; Trọng Hải, Gi&aacute;m đốc C&ocirc;ng an Nghệ An tham gia l&agrave;m Ủy vi&ecirc;n BCH Đảng bộ, Ban Thường vụ Tỉnh ủy Nghệ An, nhiệm kỳ 2015 - 2020.</p>\r\n\r\n<p>Ch&uacute;c mừng Đại t&aacute; V&otilde; Trọng Hải, B&iacute; thư Tỉnh ủy Th&aacute;i Thanh Qu&yacute; đề nghị Gi&aacute;m đốc C&ocirc;ng an tỉnh tr&ecirc;n cương vị c&ocirc;ng t&aacute;c mới, lu&ocirc;n ph&aacute;t huy năng lực, phẩm chất, đo&agrave;n kết c&ugrave;ng tập thể ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ được giao, đ&oacute;ng g&oacute;p v&agrave;o c&ocirc;ng cuộc ph&aacute;t triển kinh tế, x&atilde; hội của tỉnh.</p>\r\n\r\n<p>Ph&aacute;t biểu nhận nhiệm vụ, Đại t&aacute; V&otilde; Trọng Hải b&agrave;y tỏ đ&acirc;y l&agrave; vinh dự, tự h&agrave;o, cũng l&agrave; tr&aacute;ch nhiệm lớn lao trước Đảng bộ v&agrave; nh&acirc;n d&acirc;n tỉnh nh&agrave; v&agrave; hứa sẽ kh&ocirc;ng ngừng nỗ lực, cố gắng hết m&igrave;nh c&ugrave;ng tập thể Ban Thường vụ Tỉnh ủy, BCH Đảng bộ tỉnh l&atilde;nh đạo, chỉ đạo để Đảng bộ ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ ch&iacute;nh trị, x&acirc;y dựng Nghệ An ng&agrave;y c&agrave;ng ph&aacute;t triển.</p>\r\n\r\n<p>Trước đ&oacute;, Bộ trưởng C&ocirc;ng an đ&atilde; điều động Đại t&aacute; V&otilde; Trọng Hải, Gi&aacute;m đốc C&ocirc;ng an H&agrave; Tĩnh đến nhận c&ocirc;ng t&aacute;c v&agrave; bổ nhiệm giữ chức vụ Gi&aacute;m đốc C&ocirc;ng an tỉnh Nghệ An từ ng&agrave;y 1/7.</p>', 'crFY_NgheAn.jpg', 0, 0, 3, '2020-08-14', '2020-08-14'),
(87, 'Ông Bùi Thanh Sơn giữ chức Bí thư Đảng ủy Bộ Ngoại giao', 'ong-bui-thanh-son-giu-chuc-bi-thu-dang-uy-bo-ngoai-giao', '<p>Đại hội đại biểu Đảng bộ Bộ Ngoại giao lần thứ 28, nhiệm kỳ 2020-2025 khai mạc s&aacute;ng nay (11/8) tại H&agrave; Nội.</p>', '<p>Tham dự Đại hội c&ograve;n c&oacute; c&aacute;c l&atilde;nh đạo, nguy&ecirc;n l&atilde;nh đạo Đảng ủy Bộ v&agrave; 196 đại biểu đại diện cho c&aacute;c tổ chức đảng v&agrave; đảng vi&ecirc;n trong to&agrave;n Đảng bộ.</p>\r\n\r\n<p>Đại hội c&oacute; chủ đề &ldquo;N&acirc;ng cao năng lực l&atilde;nh đạo, sức chiến đấu của tổ chức cơ sở đảng v&agrave; chất lượng đội ngũ c&aacute;n bộ, đảng vi&ecirc;n g&oacute;p phần x&acirc;y dựng ng&agrave;nh ngoại giao năng động, s&aacute;ng tạo, hiệu quả, chủ động v&agrave; t&iacute;ch cực hội nhập quốc tế, đ&oacute;ng g&oacute;p v&agrave;o sự nghiệp x&acirc;y dựng v&agrave; bảo vệ Tổ quốc&rdquo;.</p>\r\n\r\n<p>Đại hội bầu BCH Đảng bộ Bộ Ngoại giao với số lượng 35 th&agrave;nh vi&ecirc;n,&nbsp;được bầu từ danh s&aacute;ch gồm 40 người. Trong đ&oacute;, c&oacute; 3 th&agrave;nh vi&ecirc;n l&agrave; l&atilde;nh đạo Bộ, giới thiệu tham gia Ban Thường vụ. 29 th&agrave;nh vi&ecirc;n đại diện cho c&aacute;c đơn vị, c&aacute;c cấp ủy trực thuộc, C&ocirc;ng đo&agrave;n, Đo&agrave;n thanh ni&ecirc;n. 3 th&agrave;nh vi&ecirc;n c&ograve;n lại l&agrave; ủy vi&ecirc;n chuy&ecirc;n tr&aacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/18/ong-bui-thanh-son-giu-chuc-bi-thu-dang-uy-bo-ngoai-giao-3.jpg\" style=\"height:417px; width:660px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>C&aacute;c đại biểu bầu BCH kh&oacute;a mới</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&Ocirc;ng B&ugrave;i Thanh Sơn, Uỷ vi&ecirc;n Trung ương Đảng, Thứ trưởng Thường trực Bộ Ngoại giao, B&iacute; thư Đảng uỷ Bộ Ngoại giao t&aacute;i đắc cử&nbsp; B&iacute; thư Đảng uỷ Bộ Ngoại giao nhiệm kỳ 2020-2025</p>\r\n\r\n<p>Đại hội cũng bầu đo&agrave;n đại biểu dự Đại hội đại biểu Đảng bộ Khối c&aacute;c cơ quan Trung ương lần thứ 13, nhiệm kỳ 2020-2025.</p>\r\n\r\n<p>Ph&aacute;t biểu chỉ đạo, Ph&oacute; Thủ tướng, Bộ trưởng Ngoại giao Phạm B&igrave;nh Minh ghi nhận những th&agrave;nh t&iacute;ch, kết quả m&agrave; Đảng bộ Bộ Ngoại giao đ&atilde; đạt được trong nhiệm kỳ&nbsp;qua. Nhiệm kỳ tới, trong bối cảnh t&igrave;nh h&igrave;nh thế giới v&agrave; khu vực tiếp tục biến động phức tạp, kh&oacute; lường, đất nước đang ở trong giai đoạn hội nhập s&acirc;u rộng, to&agrave;n diện, nhiệm vụ đặt ra cho ng&agrave;nh ngoại giao nặng nề hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/18/ong-bui-thanh-son-giu-chuc-bi-thu-dang-uy-bo-ngoai-giao-2.jpg\" style=\"height:440px; width:660px\" /></p>\r\n\r\n<p>Đảng bộ Bộ Ngoại giao đảm tr&aacute;ch nhiệm vụ c&ocirc;ng t&aacute;c đảng ngo&agrave;i nước, Đại hội cần tập trung tr&iacute; tuệ t&igrave;m giải ph&aacute;p n&acirc;ng cao năng lực l&atilde;nh đạo v&agrave; sức chiến đấu của tổ chức cơ sở đảng, chất lượng đội ngũ c&aacute;n bộ, đảng vi&ecirc;n; đổi mới phương thức l&atilde;nh đạo, chỉ đạo v&agrave; c&aacute;ch thức tổ chức hoạt động ph&ugrave; hợp với điều kiện hoạt động ở cả trong v&agrave; ngo&agrave;i nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhiệm vụ ch&iacute;nh trị đối ngoại c&agrave;ng lớn, kh&oacute; khăn, c&ocirc;ng t&aacute;c x&acirc;y dựng Đảng c&agrave;ng cần phải ch&uacute; trọng, vững v&agrave;ng về ch&iacute;nh trị tư tưởng, thống nhất về &yacute; ch&iacute; v&agrave; h&agrave;nh động, chặt chẽ về tổ chức để l&atilde;nh đạo to&agrave;n ng&agrave;nh ho&agrave;n th&agrave;nh thắng lợi nhiệm vụ.</p>', 'wCdS_7-240x160.jpg', 0, 0, 3, '2020-08-14', '2020-08-14'),
(88, 'Chiếm 3.000 m2 đất công, Chủ tịch xã ở Thanh Hóa bị khởi tố', 'chiem-3-000-m2-dat-cong-chu-tich-xa-o-thanh-hoa-bi-khoi-to', '<p><span dir=\"rtl\" lang=\"ar\">C&ocirc;ng an Thanh H&oacute;a vừa khởi tố bị can đối với Chủ tịch, c&aacute;n bộ địa ch&iacute;nh UBND x&atilde; H&agrave; Vinh, huyện H&agrave; Trung (Thanh H&oacute;a) v&agrave; một trưởng th&ocirc;n về h&agrave;nh vi &ldquo;Lợi dụng chức vụ quyền hạn trong thi h&agrave;nh c&ocirc;ng vụ&rdquo;.</span></p>', '<p>Thông tin từ Công an Thanh Hóa cho biết, vừa ra quyết định khởi tố bị can, áp dụng biện pháp ngăn chặn cấm đi khỏi nơi cư trú đối với Lê Xuân Thảo, nguyên Chủ tịch UBND xã Hà Vinh; Ngô Văn Dũng, nguyên cán bộ địa chính xã và Ngô Văn Lưu, nguyên trưởng thôn Quý Vinh về hành vi \"Lợi dụng chức vụ quyền hạn trong khi thi hành công vụ\".\r\n\r\nTheo kết quả điều tra, năm 2013, ông Lê Xuân Thảo là chủ tịch xã đã chỉ đạo ông Ngô Xuân Dũng lúc đó là cán bộ địa chính lập khống hồ sơ 3.000 m2 đất công ích ở thôn 2 để đề nghị UBND huyện Hà Trung cấp giấy chứng nhận quyền sử dụng đất (GCNQSDĐ) ở và đất trồng cây lâu năm cho gia đình ông Thảo.\r\n\r\nĐể qua mặt cơ quan chức năng và nhân dân, ông Thảo nhờ Ngô Văn Lưu là trưởng thôn 2 làm giấy tờ để ghép diện tích đất trên vào thửa đất hiện tại nhà mình.\r\n</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/20/chiem-3000m2-dat-cong-chu-tich-xa-o-thanh-hoa-bi-khoi-to.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p>Ngày 13/9/2013, UBND huyện Hà Trung đã cấp GCNQSDĐ cho hộ ông Ngô Văn Lưu với diện tích 3.000 m2, bao gồm cả diện tích đất ở và đất trồng cây lâu năm.\r\nNgày 18/4/2014, ông Lưu đã làm thủ tục sang nhượng tên mảnh đất nói trên cho ông Lê Xuân Thảo như đã thỏa thuận ban đầu.\r\nĐến ngày 13/5/2014, UBND huyện Hà Trung đã cấp GCNQSDĐ cho gia đình ông Thảo trên mảnh đất công ích của xã Hà Vinh.\r\nUBND huyện Hà Trung cấp GCNQSDĐ cho hộ ông Ngô Văn Lưu trên cơ sở số hồ sơ khống, sau này sang tên cho ông Lê Xuân Thảo gây thiệt cho nhà nước hàng trăm triệu đồng.</p>', 'kAjt_chiem-3000m2-dat-cong-chu-tich-xa-o-thanh-hoa-bi-khoi-to-1.jpg', 0, 0, 5, '2020-08-14', '2020-08-14'),
(89, 'Khởi động trang thông tin điện tử Năm Chủ tịch AIPA 2020', 'khoi-dong-trang-thong-tin-dien-tu-nam-chu-tich-aipa-2020', '<p>Trang th&ocirc;ng tin điện tử hỗ trợ&nbsp;Quốc hội Việt Nam tổ chức Đại hội đồng AIPA 41 theo h&igrave;nh thức trực tuyến.</p>', '<p>S&aacute;ng 13/8, tại Nh&agrave; Quốc hội, Chủ tịch Quốc hội Nguyễn Thị Kim Ng&acirc;n,&nbsp;c&aacute;c ủy&nbsp;vi&ecirc;n UB Thường vụ Quốc hội... c&ugrave;ng đại diện l&atilde;nh đạo Bộ TT&amp;TT, Bộ VH-TT&amp;DL đ&atilde;&nbsp;ấn n&uacute;t khởi động trang th&ocirc;ng tin điện tử, ứng dụng tr&ecirc;n thiết bị di động của Năm Chủ tịch AIPA (Hội đồng Li&ecirc;n Nghị viện Hiệp hội c&aacute;c quốc gia Đ&ocirc;ng Nam &Aacute;) 2020 tại&nbsp;website: http://aipa2020.vn.</p>\r\n\r\n<p>Tiểu ban Th&ocirc;ng tin, Tuy&ecirc;n truyền cho biết, việc x&acirc;y dựng v&agrave; vận h&agrave;nh website&nbsp;nhằm th&ocirc;ng tin đến c&ocirc;ng ch&uacute;ng v&agrave; Nghị viện c&aacute;c nước th&agrave;nh vi&ecirc;n AIPA về bộ nhận diện, Trang th&ocirc;ng tin điện tử, ứng dụng tr&ecirc;n thiết bị di động Năm Chủ tịch AIPA 2020.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/qp-11.jpg\" style=\"height:377px; width:594px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Chủ tịch Quốc hội Nguyễn Thị Kim Ng&acirc;n v&agrave; c&aacute;c đại biểu bấm n&uacute;t khởi động trang th&ocirc;ng tin điện tử, ứng dụng tr&ecirc;n thiết bị di động của Năm Chủ tịch AIPA 2020</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Qua đ&oacute; quảng b&aacute; h&igrave;nh ảnh đất nước, con người Việt Nam đến bạn b&egrave; quốc tế; c&ocirc;ng bố nguồn cung cấp th&ocirc;ng tin ch&iacute;nh thức tr&ecirc;n m&ocirc;i trường mạng internet về c&aacute;c hoạt động của Năm Quốc hội Việt Nam đảm nhiệm Chủ tịch AIPA.</p>\r\n\r\n<p>Việc n&agrave;y cũng tạo điều kiện thuận lợi trong việc đăng k&yacute;, trao đổi t&agrave;i liệu, th&ocirc;ng b&aacute;o trực tuyến giữa ban tổ chức, ban thư k&yacute; quốc gia với c&aacute;c nước th&agrave;nh vi&ecirc;n; tạo dấu ấn của Quốc hội Việt Nam trong vai tr&ograve; Chủ tịch AIPA 2020 v&agrave; Đại hội đồng AIPA41 với chủ đề &ldquo;Ngoại giao nghị viện v&igrave; Cộng đồng ASEAN gắn kết v&agrave; chủ động th&iacute;ch ứng&rdquo;.</p>\r\n\r\n<p>Ph&aacute;t biểu tại buổi lễ, Chủ tịch Quốc hội, Chủ tịch AIPA Nguyễn Thị Kim Ng&acirc;n đ&aacute;nh gi&aacute; cao sự chủ động, t&iacute;ch cực của Ban Tổ chức Đại hội đồng AIPA 41 v&agrave; Tiểu ban Th&ocirc;ng tin, Tuy&ecirc;n truyền trong việc x&acirc;y dựng trang th&ocirc;ng tin điện tử, ứng dụng tr&ecirc;n thiết bị di động v&agrave; bộ nhận diện của Năm Chủ tịch AIPA 2020.</p>\r\n\r\n<p>Theo b&agrave; Ng&acirc;n, năm 2020, Việt Nam đảm nhận vai tr&ograve; ủy vi&ecirc;n kh&ocirc;ng thường trực Hội đồng Bảo an Li&ecirc;n hợp quốc, Chủ tịch ASEAN v&agrave; Chủ tịch AIPA lần thứ 41. C&aacute;c hoạt động trong khu&ocirc;n khổ Năm Chủ tịch ASEAN, Chủ tịch AIPA tổ chức từ đầu năm đến nay đ&atilde; th&agrave;nh c&ocirc;ng tốt đẹp, th&iacute;ch ứng kịp thời với diễn biến phức tạp của đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chutichqh.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Chủ tịch Quốc hội Nguyễn Thị Kim Ng&acirc;n</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;Chủ tịch Quốc hội nhấn mạnh, nhiệm vụ quan trọng của Quốc hội Việt Nam l&agrave; tổ chức th&agrave;nh c&ocirc;ng Đại hội đồng AIPA lần thứ 41. Đ&acirc;y l&agrave; điểm nhấn quan trọng trong c&ocirc;ng t&aacute;c đối ngoại của Quốc hội Việt Nam nhiệm kỳ kh&oacute;a 14, g&oacute;p phần l&agrave;m nổi bật th&agrave;nh tựu ngoại giao Nh&agrave; nước v&agrave; ngoại giao Nghị viện, đồng thời, thể hiện vai tr&ograve; v&agrave; tr&aacute;ch nhiệm của Quốc hội Việt Nam trong AIPA.</p>\r\n\r\n<p>Trước t&igrave;nh h&igrave;nh đại dịch Covid-19 diễn biến phức tạp, để ph&aacute;t huy kết quả c&aacute;c hội nghị trực tuyến trong khu&ocirc;n khổ ASEAN v&agrave; AIPA trong thời gian qua, Quốc hội Việt Nam sẽ tổ chức Đại hội đồng AIPA 41 theo h&igrave;nh thức trực tuyến (từ ng&agrave;y 8 - 10/9 tại Trung t&acirc;m Hội nghị Quốc tế, số 11, L&ecirc; Hồng Phong, H&agrave; Nội). Đ&acirc;y cũng l&agrave; lần đầu ti&ecirc;n trong lịch sử Đại hội đồng AIPA được tổ chức bằng h&igrave;nh thức trực tuyến.</p>\r\n\r\n<p>Để tổ chức th&agrave;nh c&ocirc;ng Đại hội đồng AIPA 41, Chủ tịch Quốc hội đề nghị, đẩy mạnh c&aacute;c hoạt động th&ocirc;ng tin, tuy&ecirc;n truyền ở mức cao nhất Năm Chủ tịch AIPA v&agrave; Đại hội đồng AIPA 41 tr&ecirc;n c&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng (b&aacute;o h&igrave;nh, b&aacute;o n&oacute;i, b&aacute;o viết, b&aacute;o điện tử). Trong đ&oacute;, ch&uacute; trọng về nội dung th&ocirc;ng tin, tuy&ecirc;n truyền trước, trong, sau Đại hội đồng AIPA 41 ph&ugrave; hợp v&agrave; th&iacute;ch ứng với h&igrave;nh thức trực tuyến.</p>\r\n\r\n<p>Chủ tịch Quốc hội cũng y&ecirc;u cầu phối hợp chặt chẽ với Ban Thư k&yacute; Quốc gia AIPA 41, c&aacute;c tiểu ban kh&aacute;c khẩn trương cập nhật th&ocirc;ng tin, t&agrave;i liệu đăng tải tr&ecirc;n website AIPA 41, trong đ&oacute; nhấn mạnh đến những đ&oacute;ng g&oacute;p, s&aacute;ng kiến của Quốc hội Việt Nam trong Năm Chủ tịch AIPA 2020; việc tổ chức cuộc họp kh&ocirc;ng ch&iacute;nh thức đầu ti&ecirc;n của Nghị sĩ trẻ AIPA&hellip;</p>', '8Dq7_khoi-dong-trang-thong-tin-dien-tu-nam-chu-tich-aipa-2020-360x240.jpg', 0, 0, 6, '2020-08-14', '2020-08-14'),
(91, 'Nữ tài xế tông 8 xe máy ở Sài Gòn khai đạp nhầm chân ga', 'nu-tai-xe-tong-8-xe-may-o-sai-gon-khai-dap-nham-chan-ga', '<p>Cơ quan chức năng xác định, cô gái lái ô tô Camry tông loạt xe máy ở Sài Gòn không sử dụng bia, ma túy.</p>', '<p>Sáng nay, Công an quận Bình Thạnh (TP.HCM) cho hay, đang điều tra nguyên nhân vụ tai nạn ô tô Camry tông loạt xe máy làm nhiều người bị thương xảy ra trên địa bàn tối 13/8.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/co-gai-lai-o-to-camry-tong-loat-xe-may-o-sai-gon-khai-gi-voi-cong-an-1.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Hiện trường vụ tai nạn&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước đầu, vụ tai nạn làm 10 người bị thương, ít nhất 6 phương tiện xe gắn máy hư hỏng.&nbsp;</p>\r\n\r\n<p>Về nguyên nhân, nữ tài xế tên N.T.Q (23 tuổi, quê Lâm Đồng) khai, cô đạp nhầm chân ga khi đến giao lộ đừng đèn đỏ thay vì đạp chân thắng (phanh). Kết quả kiểm tra nhanh, cô gái không sử dụng bia trước khi lái xe và không dương tính với ma túy.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/co-gai-lai-o-to-camry-tong-loat-xe-may-o-sai-gon-khai-gi-voi-cong-an-2.jpg\" style=\"height:417px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Nhiều người bị thương được chuyển đi bệnh viện cấp cứu</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Như VietNamNet thông tin, khoảng 20h tối 13/8, cô gái lái ô tô Camry trên đường Xô Viết Nghệ Tĩnh hướng từ Thanh Đa về đường Ung Văn Khiêm.</p>\r\n\r\n<p>Khi đến khu vực giao lộ Đài liệt sĩ thì bất ngờ tông hàng loạt xe máy.</p>\r\n\r\n<p>Xe&nbsp;Camry tiếp tục húc đổ một biển báo giao thông rồi lao lên bồn cây xanh Đài liệt sĩ, đâm trúng trụ điện, biến dạng phần đầu mới chịu dừng lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vụ việc khiến nhiều xe máy bị hư hỏng và nhiều người bị thương, nằm la liệt giữa đường.</p>', 'ez55_co-gai-lai-o-to-camry-tong-loat-xe-may-o-sai-gon-khai-gi-voi-cong-an-4-360x240.jpg', 0, 0, 7, '2020-08-14', '2020-08-14'),
(92, 'Miền Bắc mưa lớn diện rộng nhiều ngày tới', 'mien-bac-mua-lon-dien-rong-nhieu-ngay-toi', '<p>Đợt mưa diện rộng ở Bắc Bộ có khả năng kéo dài đến ngày 18-19/8, khu vực Tây Bắc và Việt Bắc có thể mưa to đến rất to.</p>', '<p>Do ảnh hưởng của vùng hội tụ gió lên đến 5.000m hoạt động mạnh dần lên nên trong chiều tối và đêm nay (12/8), ở Bắc Bộ có mưa rào và giông diện rộng, khu vực Tây Bắc và Việt Bắc có mưa vừa, mưa to, có nơi mưa rất to (lượng mưa phổ biến 20-50mm/12h, có nơi trên 80mm/12h). Trong mưa giông có khả năng xảy ra lốc, sét và gió giật mạnh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/12/15/mien-bac-mua-lon-dien-rong-nhieu-ngay-toi.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Miền Bắc có mưa diện rộng nhiều ngày tới</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đợt mưa diện rộng ở Bắc Bộ có khả năng kéo dài đến ngày 18-19/8, khu vực Tây Bắc và Việt Bắc có khả năng xảy ra mưa to đến rất to.</p>\r\n\r\n<p>Các tỉnh Tây Nguyên và Nam Bộ có mưa rào và giông, cục bộ có mưa to đến rất to (thời gian mưa tập trung vào khoảng chiều và tối). Trong mưa giông có khả năng xảy ra lốc, sét và gió giật mạnh. &nbsp;&nbsp;</p>\r\n\r\n<p>Ngày mai, do ảnh hưởng của rìa Đông Nam vùng áp thấp nóng phía Tây kết hợp với hiệu ứng phơn nên ở khu vực Trung Bộ có nắng nóng với nhiệt độ cao nhất phổ biến 35-36 độ, có nơi trên 36 độ.</p>\r\n\r\n<p>Từ ngày 14/8, nắng nóng diện rộng kết thúc ở khu vực này.</p>\r\n\r\n<p>Chỉ số tia UV ở Đà Nẵng có giá trị từ 7-9 ứng với mức ảnh hưởng nguy cơ gây hại cao đến rất cao đối với cơ thể con người khi tiếp xúc trực tiếp với ánh nắng.&nbsp; &nbsp;</p>\r\n\r\n<p>Dự báo thời tiết các vùng đêm 12 và ngày 13/8:</p>\r\n\r\n<p>Phía&nbsp;Tây Bắc Bộ&nbsp;chiều tối và đêm có mưa vừa, mưa to, có nơi mưa rất to và giông rải rác; ngày có mưa rào và giông vài nơi. Trong cơn giông có khả năng xảy ra lốc, sét và gió giật mạnh.</p>\r\n\r\n<p>Nhiệt độ thấp nhất 23-26 độ, có nơi dưới 23 độ; cao nhất 30-33 độ.</p>\r\n\r\n<p>Phía&nbsp;Đông Bắc Bộ&nbsp;chiều tối và đêm có mưa rào và giông rải rác, vùng núi có mưa vừa, mưa to, có nơi mưa rất to; ngày nắng. Trong cơn giông có khả năng xảy ra lốc, sét và gió giật mạnh. Nhiệt độ thấp nhất 23-26 độ; cao nhất 30-33 độ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/thoi-tiet-tag55931.html\">Thời tiết</a>&nbsp;Hà Nội&nbsp;chiều tối, đêm và sáng có mưa rào và giông rải rác, cục bộ có mưa vừa, mưa to; ngày nắng. Trong cơn giông có khả năng xảy ra lốc, sét và gió giật mạnh. Nhiệt độ thấp nhất 24-26 độ; cao nhất 30-33 độ.</p>\r\n\r\n<p>Thanh Hóa - Thừa Thiên Huế&nbsp;có mưa rào và giông vài nơi về chiều tối và đêm, phía Bắc có mưa rào và giông rải rác; ngày nắng nóng. Nhiệt độ thấp nhất 25-28 độ; cao nhất từ 34-36 độ, có nơi trên 36 độ.</p>\r\n\r\n<p>Đà Nẵng đến Bình Thuận&nbsp;chiều tối có mưa rào và giông rải rác, đêm có mưa rào và giông vài nơi; ngày nắng, phía Bắc có nắng nóng. Nhiệt độ thấp nhất 24-27 độ; cao nhất 33-36 độ, phía Bắc có nơi trên 36 độ.</p>\r\n\r\n<p>Tây Nguyên&nbsp;và&nbsp;Nam Bộ&nbsp;chiều và đêm có mưa rào và giông rải rác; ngày có mưa rào và giông vài nơi. Trong cơn giông có khả năng xảy ra lốc, sét và gió giật mạnh.</p>\r\n\r\n<p>Nhiệt độ thấp nhất tại Tây Nguyên là 20-23 độ; cao nhất 28-31 độ.</p>\r\n\r\n<p>Tại Nam Bộ nền nhiệt thấp nhất 23-26 độ; cao nhất 30-33 độ, có nơi trên 33 độ.</p>', 'UhrV_mien-bac-mua-lon-dien-rong-nhieu-ngay-toi-360x240.jpg', 0, 0, 8, '2020-08-14', '2020-08-14'),
(93, 'Phó Thủ tướng: Bộ đội Biên phòng kiên trì bám chốt, đánh thắng “giặc Covid”', 'pho-thu-tuong-bo-doi-bien-phong-kien-tri-bam-chot-danh-thang-giac-covid-', '<p>Thường trực Ban Chỉ đạo quốc gia phòng chống dịch bệnh Covid-19&nbsp;hôm nay (11/8) đã có cuộc&nbsp;làm việc với Bộ Tư lệnh Bộ đội Biên phòng về công tác quản lý bảo vệ biên giới và phòng, chống dịch bệnh.&nbsp;</p>', '<p>Phó Thủ tướng Vũ Đức Đam, Trưởng Ban Chỉ đạo chủ trì buổi làm việc.</p>\r\n\r\n<p>Thiếu tướng Lê Đức Thái, Phó Tư lệnh, Tham mưu trưởng, phụ trách Tư lệnh Bộ đội Biên phòng (BĐBP) báo cáo,&nbsp;BĐBP đã triển khai thực hiện các giải pháp phòng, chống dịch theo tinh thần “<a href=\"https://vietnamnet.vn/covid-19-tag260671.html\" target=\"_blank\">chống dịch</a>&nbsp;như chống giặc”.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/21/anh-3-1.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Thiếu tướng Lê Đức Thái báo cáo tại cuộc họp</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngay sau khi có ca bệnh 416 tại Đà Nẵng và thông tin 21 đối tượng người Trung Quốc có dấu hiệu nhập cảnh trái phép, ngày 26/7, Bộ Tư lệnh BĐBP đã tổ chức họp khẩn cấp, kích hoạt toàn bộ quy trình phòng, chống dịch.&nbsp;</p>\r\n\r\n<p>Về công tác quản lý, bảo vệ biên giới; kiểm soát xuất nhập cảnh, Bộ Tư lệnh BĐBP đã phát hiện, xử lý&nbsp;<a href=\"https://vietnamnet.vn/vn/thoi-su/quoc-phong/bo-doi-bien-phong-ngan-chan-hon-16-000-nguoi-vuot-bien-trai-phep-662489.html\" target=\"_blank\">16.530 người</a>&nbsp;nhập cảnh trái phép, bàn giao cho địa phương cách ly theo quy định. Chỉ tính riêng từ ngày 1/7 đến 7/8 đã phát hiện, bắt giữ, bàn giao cho cơ quan chức năng 3.485 người nhập cảnh trái phép.</p>\r\n\r\n<p>Phối hợp với các lực lượng giải quyết nhập cảnh cho các trường hợp là người nước ngoài vào Việt Nam với mục đích ngoại giao, công vụ, các trường hợp đặc biệt khác...</p>\r\n\r\n<p>8 tháng qua các chiến sĩ&nbsp;BĐBP&nbsp;luôn là những người trên tuyến đầu. Vượt qua mọi khó khăn, gian khổ, không quản hy sinh, thực hiện tốt nhiệm vụ bảo vệ biên cương, quản lý tốt hơn 5.000 km biên giới đường bộ và hơn 3.200 km bờ biển; tiếp tục củng cố, giao lưu hữu nghị với các nước láng giềng...</p>\r\n\r\n<p>Bộ đội Biên phòng không quản vất vả, hy sinh lợi ích riêng</p>\r\n\r\n<p>Phát biểu tại buổi làm việc, Phó Thủ tướng&nbsp;<a href=\"https://vietnamnet.vn/vu-duc-dam-tag64857.html\" target=\"_blank\">Vũ Đức Đam</a>&nbsp;điểm lại quá trình&nbsp;phòng chống, dịch Covid-19. Bước vào đợt dịch thứ 3, chúng ta phải đối mặt với sức ép rất lớn. Chúng ta không thể \"bế quan tỏa cảng\", mà phải thực hiện mục tiêu kép trong trạng thái bình thường mới.</p>\r\n\r\n<p>Đến thời điểm hiện tại, dù&nbsp;cả nước&nbsp;đang phải tập trung khống chế đợt dịch thứ 3, có những bệnh nhân không qua khỏi, nhưng có thể nói, Việt Nam vẫn là một trong không nhiều nước chống dịch hiệu quả.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/21/anh-1-1.jpg\" style=\"height:378px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Phó Thủ tướng Vũ Đức Đam</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Toàn lực lượng BĐBP có hàng chục&nbsp;cán bộ:&nbsp;bố mất, mẹ mất không về chịu tang, vợ sinh con không về, hoãn đám cưới, gian khổ nằm rừng ngủ lán phòng, chống dịch. Phó Thủ tướng khẳng định, BĐBP không quản khó khăn vất vả, hy sinh hết lợi ích riêng giữ biên giới trong phòng, chống đại dịch.</p>\r\n\r\n<p>Kể từ ngày đầu tiên có dịch và lực lượng cuối cùng phòng chống dịch, thậm chí hết dịch rồi, lực lượng Biên phòng vẫn phải chiến đấu vì không chỉ có dịch này và bất kỳ lúc nào có dịch khác, không chỉ có dịch mà rất nhiều vấn đề liên quan đến bảo vệ biên giới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>\"Tôi tin rằng mọi người dân Việt Nam đều ghi ơn và luôn tin tưởng vào cán bộ, chiến sĩ BĐBP ở nhiệm vụ nào cũng hoàn thành, khó khăn nào cũng vượt qua, kẻ thù nào cũng đánh thắng, trước mắt là đánh thắng \"<a href=\"https://vietnamnet.vn/virus-corona-tag257346.html\" target=\"_blank\">giặc Covid-19</a>\", Phó Thủ tướng chia sẻ.</p>\r\n\r\n<p>Tới đây, dịch bệnh còn kéo dài, chúng ta phải xác định tinh thần “chung sống an toàn với dịch”, Phó Thủ tướng yêu cầu Bộ Tư lệnh BĐBP tiếp tục động viên cán bộ, chiến sĩ tiếp tục bám trụ biên giới phòng, chống dịch.</p>\r\n\r\n<p>Phó Thủ tướng đề nghị Bộ Quốc phòng xem xét báo cáo phương án đảm bảo cho cán bộ, chiến sĩ với các vật&nbsp;dụng&nbsp;liên quan chống dịch, đảm bảo điều kiện tốt để phòng, chống dịch trong thời gian dài.</p>\r\n\r\n<p>Trong thời gian tới, thời tiết chuyển mưa rét, cần có phương án bán kiên cố một số điểm chốt trên biên giới để cán bộ, chiến sĩ yên tâm làm tốt công tác.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/21/anh-2.JPG\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p>Đề nghị tất cả UBND các tỉnh biên giới phối hợp chặt chẽ với lực lượng BĐBP tổ chức các tổ, nhóm, lực lượng tạo thành thế trận nhiều lớp quản lý cho thật chặt đường biên.</p>\r\n\r\n<p>Bộ Công an tiếp tục chỉ đạo công an các tỉnh kiểm soát chặt chẽ người xuất nhập cảnh và nhân dân cư trú trên địa bàn để đảm bảo tất cả những đối tượng nhập cảnh phải thực hiện các biện pháp kiểm dịch đúng quy định, qua đó phát hiện những trường hợp lợi dụng để đưa người nhập cảnh trái phép để thực hiện các mục đích khác…</p>', '41Hv_anh-1-1-1-360x240.jpg', 0, 0, 3, '2020-08-14', '2020-08-14'),
(94, 'Donald Trump lệnh cấm: Tập đoàn Trung Quốc gặp khó, đại gia Mỹ được lợi', 'donald-trump-lenh-cam-tap-doan-trung-quoc-gap-kho-dai-gia-my-duoc-loi', '<p>Thị trường chứng khoán Mỹ thể hiện sức mạnh và bứt phá lên các đỉnh cao mới. Nền kinh tế số 1 thế giới vẫn ghi nhận những tín hiệu tích cực, đặc biệt trên thị trường lao động</p>', '<p>Kết thúc phiên giao dịch 13/8 trên thị trường Mỹ (rạng sáng 14/8), thị trường&nbsp;<a href=\"https://vietnamnet.vn/chung-khoan-my-tag231590.html\">chứng khoán</a>&nbsp;(TTCK) Mỹ vẫn giữ được sức mạnh. Chỉ số công nghệ Nasdaq Composite tiếp tục tăng điểm, trong khi chỉ số tầm rộng S&amp;P 500 có lúc đã ghi nhận kỷ lục cao lịch sử mới, trước khi đóng cửa hạ nhiệt đôi chút.</p>\r\n\r\n<p>Chứng khoán Mỹ tiếp tục diễn biến theo chiều hướng tích cực, bất chấp nước Mỹ vẫn bế tắc về gói cứu trợ mới.</p>\r\n\r\n<p>Sở dĩ các chỉ số chứng khoán Mỹ đứng vững ở sát các đỉnh cao kỷ lục là bởi nền kinh tế Mỹ ghi nhận những tín hiệu tích cực.</p>\r\n\r\n<p>Theo Bộ Lao động Mỹ, số người lần đầu nộp đơn xin trợ cấp thất nghiệp hàng tuần tại Mỹ đã giảm xuống còn 963.000 người, thấp hơn dự báo 1,1 triệu người của Dow Jones đưa ra trước đó. Lần đầu tiên kể từ tháng 3/2020, số đơn xin trợ cấp thất nghiệp xuống dưới ngưỡng 1 triệu người - tín hiệu cho thấy sự cải thiện thị trường lao động Mỹ.</p>\r\n\r\n<p>Trong tháng 7, chính quyền ông&nbsp;<a href=\"http://vietnamnet.vn/donald-trump-tag20457.html\">Donald Trump</a>&nbsp;cho biết, nền kinh tế nước này đã tạo ra gần 1,8 triệu việc làm và tỷ lệ thất nghiệp giảm xuống còn 10,2%.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/tik-tok-2.jpg\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<p>Giới đầu tư đang dồn sự chú ý vào các cuộc thảo luận về gói cứu trợ mới cho các hộ gia đình và doanh nghiệp Mỹ bị tổn thương vì đại dịch Covid-19. Đã có những tranh luận gay gắt và những tuyên bố thiếu tích cực về triển vọng của gói này, nhưng nhiều nhà đầu tư tin rằng một thỏa thuận chắc chắn vẫn sẽ xảy ra.</p>\r\n\r\n<p>Cho dù Đảng Dân chủ và Đảng Cộng hòa của Mỹ đang có những tranh luận nảy lửa nhưng gói cứu trợ mới nhiều khả năng sẽ được thông qua, nếu không Hoa Kỳ có thể sẽ rơi vào suy thoái, người dân và doanh nghiệp sẽ bế tắc và xã hội sẽ bất ổn,... như cảnh báo của nhiều chuyên gia, trong đó có đại diện từ Cục dự trữ liên bang Mỹ (Fed).</p>\r\n\r\n<p>Trong phiên liền trước, TTCK Mỹ đã có một phiên bứt phá ấn tượng trong bối cảnh giới đầu tư lạc quan về vaccine phòng Covid-19. Tổng thống Nga Vladimir Putin tuyên bố Nga đã đăng ký vaccine Covid đầu tiên trên thế giới và triển khai sản xuất thương mại. Tổng thống Mỹ Donald Trump cũng đặt hàng cả tỷ USD cho một doanh nghiệp Mỹ sản xuất vaccine cho nước này.</p>\r\n\r\n<p>Đại gia công nghệ Mỹ tăng tốc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong vài phiên gần đây, nhóm cổ phiếu công nghệ Mỹ có dấu hiệu chùng lại sau một đợt tăng kéo dài, song vẫn là động lực lớn giữ chứng khoán Mỹ ở trên đỉnh cao lịch sử. Các cổ phiếu tăng ấn tượng thời gian qua là Facebook, Amazon, Netflix, Alphabet, Microsoft, Apple,...</p>\r\n\r\n<p>Nhờ cổ phiếu tăng giá mạnh, trong tuần này, Tim Cook đã trở thành tỷ phú USD (theo bảng xếp hạng của Bloomberg) sau 9 năm làm CEO Apple. Vốn hóa của Apple gần chạm mốc 2.000 tỷ USD, tăng khoảng 55% trong năm nay.</p>\r\n\r\n<p>Tuần trước, tài sản của Mark Zuckerberg, CEO Facebook cũng lần đầu vượt ngưỡng 100 tỷ USD nhờ giá cổ phiếu Facebook lập đỉnh mới khi đối thủ đến từ&nbsp;<a href=\"http://vietnamnet.vn/trung-quoc-tag60811.html\">Trung Quốc</a>&nbsp;TikTok gặp khó.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/huawei-3.jpg\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Căng thẳng Mỹ-Trung leo thang.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Cuộc chiến giữa ông Donald Trump với Trung Quốc đã giúp giới đầu tư nhận ra rằng, các công ty công nghệ của Mỹ vẫn có sức mạnh vượt trội so với Trung Quốc. Bên cạnh đó, những đòn tấn công của ông Trump nhắm vào các công ty công nghệ hàng đầu của Trung Quốc đã tạo ra cơ hội lớn cho các doanh nghiệp Mỹ.</p>\r\n\r\n<p>Hôm 6/8 (giờ Mỹ), ông Trump đã ký sắc lệnh cấm người dân Mỹ làm ăn với TikTok (của ByteDance) và WeChat (của Tencent Holdings) hoặc với những chủ sở hữu Trung Quốc của hai ứng dụng này sau 45 ngày vì lý do rủi ro an ninh quốc gia.</p>\r\n\r\n<p>Trước đó, ông Trump thúc đẩy ByteDance bán TikTok cho một công ty Mỹ.</p>\r\n\r\n<p>Mỹ có 5 công ty công nghệ lớn gồm Apple, Amazon, Alphabet (công ty mẹ của Google), Facebook và Microsoft Corp. Năm công ty này có tổng vốn hóa thị trường tương đương 30% GDP nước Mỹ, cao gần gần đôi so với mức cuối năm 2018.</p>\r\n\r\n<p>Chính quyền ông Trump gần đây phát đi tín hiệu cho thấy họ sẽ tiếp tục mạnh tay với các doanh nghiệp Trung Quốc. Trước khi ra tối hậu thư cho TikTok phải ngừng hoạt động tại Mỹ hoặc bán mảng hoạt động tại nước này cho một công ty của Mỹ như Microsoft, chính quyền ông Trump đã trừng phạt Huawei và ZTE, khiến hai ông lớn công nghệ Trung Quốc này lao đao.</p>', 'qMjd_suc-manh-donald-trump-tang-kho-tin-cuoc-chien-my-trung-nguy-co-leo-thang-360x240.jpg', 0, 0, 10, '2020-08-14', '2020-08-14');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(95, 'Đầu tư tự phát là nguyên nhân hạ tầng công nghệ tụt hậu', 'dau-tu-tu-phat-la-nguyen-nhan-ha-tang-cong-nghe-tut-hau', '<p>Nhà nước cần đóng vai trò nhiều hơn vào việc đầu tư cho hạ tầng công nghệ thông tin, không giao phó hoàn toàn cho doanh nghiệp.</p>', '<p>Xếp hạng viễn thông công nghệ thông tin của Việt Nam đã từng giữ vị trí 35 thế giới. Tuy nhiên 10 năm gần đây đã bị tụt xuống thứ 108. Để trở lại top 35 thế giới, Nhà nước cần đóng vai trò nhiều hơn vào việc đầu tư cho hạ tầng công nghệ thông tin, không giao phó hoàn toàn cho doanh nghiệp.</p>\r\n\r\n<p>Ngày 12/8, Bộ Kế hoạch và Đầu tư đã tổ chức buổi làm việc với Bộ Thông tin và Truyền thông liên quan đến kế hoạch đầu tư công trung hạn 2021-2025.</p>\r\n\r\n<p>Bộ trưởng Thông tin và Truyền thông Nguyễn Mạnh Hùng cho rằng: Trong những năm vừa qua, Bộ Thông tin và Truyền thông được bố trí vốn đầu tư rất nhỏ bé so với tổng vốn đầu tư của toàn ngành công nghệ, thông tin truyền thông. Mỗi năm, ngành công nghệ, thông tin truyền thông đầu tư 4 - 5 tỷ USD, chủ yếu từ nguồn vốn đầu tư của doanh nghiệp. Điều này dẫn đến tình trạng doanh nghiệp đầu tư tự phát, khiến hạ tầng công nghệ thông tin truyền thông ngày càng tụt hậu.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/07/dau-tu-tu-phat-la-nguyen-nhan-ha-tang-cong-nghe-tut-hau.jpg\" style=\"height:256px; width:512px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Hạ tầng công nghệ Việt Nam đang bị tụt hậu</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>“Năm 2009, xếp hạng viễn thông công nghệ thông tin của Việt Nam lọt top 35 thế giới. Đó là giai đoạn Nhà nước đầu tư mạnh mẽ cho công nghệ, thông tin truyền thông. Những năm gần đây, đầu tư chủ yếu dựa vào khu vực tư nhân. Điều này khiến cho xếp hạng viễn thông của Việt Nam bị tụt xuống thứ 108 chỉ trong vòng hơn 10 năm”.</p>\r\n\r\n<p>Bộ trưởng Nguyễn Mạnh Hùng đề nghị Nhà nước cần đóng vai trò nhiều hơn vào việc đầu tư cho hạ tầng công nghệ thông tin, không giao phó hoàn toàn cho doanh nghiệp. Nhà nước chỉ cần bỏ ra 1% vốn mồi, để kích thích nguồn lực từ xã hội đầu tư 99% còn lại. Mục tiêu là đưa xếp hạng Việt Nam từ mức 108 này quay về top 35 như đã từng đạt được.</p>\r\n\r\n<p>Theo Bộ trưởng, đất nước ta như một đoàn tàu, đầu tiên phải có cách để kéo đầu tàu đi lên, để đoàn tàu đi nhanh hơn. Để làm được điều đó, Bộ Thông tin và Truyền thông muốn làm cho “bộ não của đoàn tàu” thông minh hơn. Muốn vậy, cần tập trung vào Chính phủ số, cần phải có một tổng công trình sư thiết kế tổng thể với vai trò chủ đạo. Sau đó Bộ Thông tin và Truyền thông sẽ định hướng cụ thể cho các địa phương với những mục tiêu cụ thể.</p>\r\n\r\n<p>Phía đuôi đoàn tàu, tức toa tàu cuối cùng cũng cần những cú hích. Bộ Thông tin và Truyền thông muốn tạo một nền tảng (platform) để “cả dân tộc này đứng trên nền&nbsp;<a href=\"https://vietnamnet.vn/vn/thong-tin-truyen-thong/10-kien-nghi-chuyen-doi-so-cua-bo-tt-tt-voi-cac-tinh-thanh-663196.html\">tảng công nghệ</a>&nbsp;đó”. Bộ sẽ đứng ra làm phần việc này, trong đó tập trung vào việc xây dựng những công nghệ cốt lõi, chuyển hạ tầng viễn thông sang hạ tầng nền kinh tế. Đó là cách nhanh nhất để dân tộc ta vượt lên.</p>\r\n\r\n<p>Trong giai đoạn 2021-2025, một trong những điều Bộ Thông tin và Truyền thông mong muốn là có nguồn vốn đầu tư trung tâm dữ liệu đám mây, thay cho những máy chủ manh mún đặt ở các bộ, ngành địa phương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngoài ra, Bộ cũng muốn xây dựng hệ thống kiểm định, phục vụ kiểm định và nghiên cứu phát triển. Phòng đo này có hai chức năng là đo kiểm định đánh giá và hỗ trợ doanh nghiệp nghiên cứu, phát triển.</p>\r\n\r\n<p>Bộ Thông tin và Truyền thông cũng lên kế hoạch xây dựng nền tảng định danh và xác thực số để phục vụ nhu cầu xã hội như thương mại điện tử,&nbsp;<a href=\"https://vietnamnet.vn/vn/thong-tin-truyen-thong/nhieu-chuong-trinh-lon-da-ket-tinh-trong-chuong-trinh-chuyen-doi-so-quoc-gia-661067.html\">dịch vụ công trực tuyến</a>…</p>\r\n\r\n<p>Ngành Thông tin và truyền thông đang thị trường hóa rất mạnh, mạnh đến nỗi Nhà nước quên việc đầu tư thích đáng cho lĩnh vực này. Nhưng giai đoạn tới, đầu tư của Nhà nước là không thể thiếu trong việc tạo ra “vốn mồi” để kích thích toàn ngành phát triển, chỉ cần 1% vốn đầu tư của Nhà nước có thể kích thích 99% nguồn vốn của xã hội.</p>\r\n\r\n<p>Cơ bản ủng hộ những vấn đề Bộ Thông tin và Truyền thông đưa ra, Bộ trưởng Bộ Kế hoạch và Đầu tư Nguyễn Chí Dũng cho rằng việc có “tổng công trình sư” cầm trịch quản lý đồng bộ, thống nhất lĩnh vực viễn thông, công nghệ thông tin truyền thông là điều quan trọng để ngành này phát triển nhanh, hiệu quả.</p>\r\n\r\n<p>“Tôi rất ủng hộ những dự án cụ thể được Bộ Thông tin và Truyền thông đưa ra như xây dựng trung tâm dữ liệu điện toán đám mây, Hệ thống kiểm định, phục vụ kiểm định và nghiên cứu phát triển, nền tảng định danh và xác thực số…”.</p>\r\n\r\n<p>Theo Bộ trưởng Nguyễn Chí Dũng, đây là các nội dung rất cụ thể và dễ nhận được sự ủng hộ. Điều quan trọng là Bộ Thông tin và Truyền thông cần đưa ra thứ tự ưu tiên của từng dự án. Dự án nào quan trọng, cấp bách cần ưu tiên làm trước, những dự án nào làm sau. Trên cơ sở thứ tự ưu tiên đó, Bộ Kế hoạch và Đầu tư sẽ “liệu cơm gắp mắm” để xác định nguồn vốn cho dự án, không nên thực hiện dàn trải.</p>\r\n\r\n<p>Trước đề xuất của Bộ Thông tin và Truyền thông về hợp tác chia sẻ dữ liệu, Bộ trưởng Nguyễn Chí Dũng bày tỏ sự ủng hộ. Ông cho rằng Bộ Kế hoạch và Đầu tư là đơn vị có cơ sở dữ liệu mạnh về doanh nghiệp, đăng ký kinh doanh, đầu tư công, đầu tư nước ngoài, chưa kể hệ thống dữ liệu riêng của Tổng cục Thống kê. Việc chia sẻ, kết nối dữ liệu vì mục tiêu chung, vì đất nước là điều cần thiết.</p>', 'chFe_dau-tu-tu-phat-la-nguyen-nhan-ha-tang-cong-nghe-tut-hau-240x160.jpg', 0, 0, 9, '2020-08-14', '2020-08-14'),
(96, 'Sự tàn bạo đẫm máu sau bát súp giá 2 triệu đồng', 'su-tan-bao-dam-mau-sau-bat-sup-gia-2-trieu-dong', '<p>Sau khi bắt con vật này lấy đi một số bộ phận trên cơ thể để nấu súp rồi thả nó trở lại môi trường sống để nó chết dần khiến món ăn này bị lên án là dã man, tàn bạo.</p>', '<p><a href=\"https://vietnamnet.vn/vi-ca-map-tag63627.html\">Vây cá mập</a>&nbsp;- thứ nguyên liệu nấu súp vi cá mập nổi tiếng đắt đỏ nhất nhì thế giới, được xem là một trong những biểu tượng của đẳng cấp và sự giàu sang - luôn có giá cực kỳ cao, 1kg cũng có giá vài ngàn đôla. Nó là món hàng có giá thứ 3 trên thị trường \"chợ đen\", sau vàng, súng và xếp trên cả ngà voi.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/sup-vay-ca-map-1.jpg\" style=\"height:373px; width:600px\" /></p>\r\n\r\n<p>Vi cá mập được tiêu thụ chủ yếu tại Singapore, Hong Kong, Đài Loan và Trung Quốc. Từ xa xưa, người Trung Quốc đã coi súp vi cá mập là biểu tượng của sự giàu sang và lòng hiếu khách nên thường sử dụng trong những dịp đặc biệt như đám cưới hay tiệc chiêu đãi. Giá của một bát súp trên thị trường vào khoảng 2 triệu đồng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/vay-ca-map-1.jpg\" style=\"height:383px; width:622px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Ngư dân châu Á phơi vây cá mập để làm món vi cá.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tuy nhiên, ít ai biết rằng đằng sau món ăn bổ dưỡng, sang trọng đó là cuộc tàn sát của loài người với cá mập. Hàng triệu con cá mập với đủ loại kích cỡ bị đánh bắt chỉ để lấy vây.</p>\r\n\r\n<p>Theo đó, từng con cá mập sẽ được câu lên bờ, chúng sẽ cắt hết hoàn toàn vây, bao gồm 2 vây lưng, 2 vây ngực, 3 vây cận đuôi và vây đuôi dưới. Vì chỉ cần đến vây cá nên những gì còn sót lại được xem là vô giá trị.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/vay-ca-map-2.jpg\" style=\"height:396px; width:328px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Xác một con cá mập sau khi bị cắt vây.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Dù cá còn sống hay đã chết, chúng đều sẽ bị ném xuống biển. Lúc này, số phận của cá mập cũng rất bi thảm. Bị thương rất nặng, cũng không thể bơi, chúng chỉ có thể chờ chết vì mất máu hoặc bị xâu xé bởi các loài cá khác.</p>\r\n\r\n<p>Không kể đến việc phung phí, thì hành vi này được nhiều tổ chức và người yêu động vật cho là dã man. Việc giết một con vật để ăn là chuyện không thể tránh khỏi, tuy nhiên để nhân đạo, người ăn phải khiến con vật ra đi thanh thản, không để thừa.</p>', '3c8x_sup-vay-ca-map-1-360x240.jpg', 0, 0, 11, '2020-08-14', '2020-08-14'),
(97, '\'Giang hồ mạng\' Phú Lê và những nhập nhèm trong hoạt động kinh doanh', 'giang-ho-mang-phu-le-va-nhung-nhap-nhem-trong-hoat-dong-kinh-doanh', '<p>Công ty TNHH Kiều Thiên Phát của vợ chồng Phú Lê từng bị cơ quan chức năng phạt 50 triệu đồng, thu hồi giấy phép hoạt động vì sản xuất 4 tấn sản phẩm không công bố chất lượng trước khi lưu thông trên thị trường.</p>', '<p>Phú Lê - \"<a href=\"https://vietnamnet.vn/giang-ho-mang-tag200429.html\">giang hồ mạng</a>\" mới bị Công an TP.Hà Nội bắt giữ tên thật là Lê Văn Phú (sinh ngày 13/8/1980, quê tại Yên Bái, hiện đang sinh sống và làm việc ở TP. Hà Nội).</p>\r\n\r\n<p>Vợ Phú Lê là Lã Thúy&nbsp;Kiều (SN 1985, trú tại thôn Phú Đa, xã Bối Cầu, huyện Bình Lục, tỉnh Hà Nam) là tổng giám đốc công ty TNHH thương mại và tổng hợp Kiều Thiên Phát.</p>\r\n\r\n<p>Công ty này có trụ sở tại địa chỉ ngõ 49 Thúy Lĩnh, phường Lĩnh Nam, quận Hoàng Mai, TP. Hà Nội. Từ đây, Phú Lê được giữ chức phó giám đốc công ty Kiều Thiên Phát.</p>\r\n\r\n<p>Phú Lê được biết đến với vẻ bề ngoài khá bặm trợn, đầu trọc, cơ thể phủ kín những hình xăm. Điều gây chú ý hơn là khi xuất hiện trong các clip livestream trên&nbsp;mạng xã hội&nbsp;và YouTube, số trang sức vàng Phú đeo trên người gồm vòng cổ hình đại bàng, nhẫn, lắc tay, đồng hồ, thậm chí dây thắt lưng. Phú Lê được \"giang hồ mạng\" xếp vào dạng có \"số má\" trên mạng xã hội</p>\r\n\r\n<p>.<img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/07/09/giang-ho-mang-phu-le-1-1.jpg\" style=\"height:427px; width:640px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Trước khi bị bắt, Phú Lê thường xuyên livestream trên Facebook nói về đạo lý giang hồ, giảng giải cách làm người.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngoài ra, Phú Lê còn được biết đến là một ca sĩ, diễn viên nghiệp dư, có nhiều MV xoay quanh chủ đề mâu thuẫn giang hồ, tình anh em giang hồ vượt qua khó khăn... với lượt người xem rất lớn. Hiện nay, trên trang Facebook cá nhân Phú Lê có tới hơn 400.000 lượt người theo dõi.</p>\r\n\r\n<p>Dựa vào sự nổi tiếng này,&nbsp;<a href=\"https://vietnamnet.vn/phu-le-tag285508.html\">vợ chồng Phú Lê</a>&nbsp;thường xuyên livestream trên mạng xã hội để bán hàng online và đã thu hút được hàng nghìn người xem, bình luận và đặt mua sản phẩm.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/07/09/giang-ho-mang-phu-le-2.jpg\" style=\"height:1070px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Sản phẩm của công ty TNHH Kiều Thiên Phát hầu hết không được cấp phép, nhưng vợ chồng Phú Lê vẫn livestream “nổ tung trời\" để bán hàng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước đó, tháng 1/2015, công ty TNHH Kiều Thiên Phát bị cơ quan chức năng phạt 50 triệu đồng, thu hồi giấy phép hoạt động vì sản xuất 4 tấn sản phẩm không công bố chất lượng trước khi lưu thông trên thị trường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngày 8/7/2015, ban chỉ đạo Quốc gia chống&nbsp;buôn lậu&nbsp;gian lận thương mại và hàng giả có mặt tại trụ sở kiêm xưởng sản xuất mỹ phẩm của công ty Kiều Thiên Phát tại địa chỉ 21 ngách 1/28 ngõ 49 phố Thuý Lĩnh, phường Lĩnh Nam, quận Hoàng Mai, TP. Hà Nội và phát hiện tại đây có nhiều biểu hiện nghi vấn về sản xuất hóa mỹ phẩm kém chất lượng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/07/09/giang-ho-mang-phu-le-3-1.jpg\" style=\"height:399px; width:640px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Phú Lê và vợ Lã Thúy Kiều trước trụ sở công ty TNHH thương mại và tổng hợp Kiều Thiên Phát</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Qua kiểm tra, cơ quan chức năng ghi nhận có khoảng 100 thùng giấy chứa các loại sản phẩm của doanh nghiệp mới sản xuất (lô hàng tháng 6/2015). Các thùng sản phẩm gồm các loại: Sữa tắm nước hoa cao cấp mang tên Happy White, Law Rel; nước rửa chén siêu nhân, nước giặt cao cấp siêu nhân và nước xả vải Soft pres.</p>\r\n\r\n<p>Lúc này, Lã Thúy Kiều chỉ xuất trình được phiếu công bố sản phẩm mỹ phẩm của 2 nhãn hàng là Happy White, Law Rel. Tại cơ sở sản xuất, cơ quan chức năng thu nhận được nhiều tem nhãn hàng ghi xuất xứ made in Germany, tem chống hàng giả của công ty, tem chống hàng giả của bộ Công an.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/07/09/giang-ho-mang-phu-le-4-1.gif\" style=\"height:405px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Mỹ phẩm nhập ngoại do công ty của vợ chồng Phú Lê pha chế theo công thức được ghi chép sơ sài trong những tờ giấy cũ nát. (Hình ảnh khi lực lượng chức năng kiểm tra cơ sở sản xuất của công ty TNHH Kiều Thiên Phát vào năm 2015)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Làm việc với cơ quan điều tra, kế toán công ty cho biết, công ty tự mua nguyên liệu về và tự pha chế ra các loại sản phẩm. Kiểm tra hóa chất, cơ quan chức năng ghi nhận các hóa chất được đóng trong bao, hộp, thùng không ghi nhãn hiệu, không tem, mác hoặc ghi bằng chữ Trung Quốc và không rõ nguồn gốc xuất xứ.</p>\r\n\r\n<p>Công nghệ pha chế thô sơ nhưng sữa tắm Law Rel đều được gắn mác sản phẩm cao cấp được sản xuất theo công nghệ của Malaysia.</p>\r\n\r\n<p>(Theo Dân Việt)</p>\r\n\r\n<p>&nbsp;</p>', 'GJj6_giang-ho-mang-phu-le-1-140x93.jpg', 0, 0, 12, '2020-08-14', '2020-08-14'),
(98, '\'Tôi mua vàng giá đỉnh\'', 'toi-mua-vang-gia-dinh', '<p>Do chốt lời sớm, chị Hoa cố “mua đuổi” vàng tới giá 62,4 triệu đồng/lượng, cao nhất mọi thời đại. Sau một tuần, người này lỗ 231 triệu đồng vì đầu tư vàng.</p>', '<p>Hồi tháng 2, lo ngại dịch bệnh sẽ ảnh hưởng đến&nbsp;<a href=\"https://vietnamnet.vn/vn/kinh-doanh/thi-truong/\">thị trường</a>&nbsp;bất động sản, chị Phương Hoa (41 tuổi, Cầu Giấy, Hà Nội) thống nhất với chồng bán đất giữ vàng. Lô đất ở Hoài Đức vợ chồng anh chị đầu tư hơn 1 năm, bán được&nbsp;2 tỷ đồng, lãi 17%.</p>\r\n\r\n<p>Chị Hoa dành ra 460 triệu đồng mua 10 lượng vàng, phần còn lại gửi tiết kiệm.</p>\r\n\r\n<p>Cầm vàng lại để vàng rơi<br />\r\n<br />\r\nĐợt chị Hoa mua vào, giá kim loại quý vừa trải qua giai đoạn tăng vọt lên 49 triệu đồng/lượng rồi giảm 5 phiên liên tiếp.</p>\r\n\r\n<p>Giai đoạn nửa cuối tháng 6, khi vàng tiến sát mốc 50 triệu đồng/lượng, ngày nào chị Hoa cũng gọi điện cho chủ tiệm kim hoàn hỏi giá để canh bán. Tuy nhiên, hai vợ chồng chị lại lưỡng lự trong quyết định chốt lời hay giữ tiếp, để rồi bán ra với giá 48,6 triệu đồng/lượng. Chị Hoa nhớ rõ hôm đó là ngày 23/6.</p>\r\n\r\n<p>Sang tháng 7, kim loại quý bắt đầu hành trình tăng giá ngoạn mục. Thông tin vàng trong nước xác lập kỷ lục, vàng thế giới cao nhất 9 năm rồi vượt đỉnh mọi thời đại xuất hiện trên mặt báo mỗi ngày. Chị Hoa chua xót: “Số tôi chắc không giàu được”.</p>\r\n\r\n<p>Thực tế, diễn biến giá vàng trong nước tháng 7 có thể giúp bất cứ nhà đầu tư mua vàng nào thu về lợi nhuận lớn. Mức lãi tối đa nếu mua vàng từ đầu năm đến hết tháng 7 là hơn 32% và lãi 5-6% nếu mua từ đầu tháng 7 (đã trừ chênh lệch giá mua - bán).</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mua-vang-1.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Giá vàng thế giới vượt đỉnh mọi thời đại hôm 27/7. Ảnh:&nbsp;Chí Hùng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong khi giá vàng tăng không có dấu hiệu dừng, lãi suất ngân hàng giảm khiến vợ chồng chị Hoa vô cùng sốt ruột. Ngày 5/8, chị Hoa rút tiền đi mua 20 lượng vàng, giá 58,3 triệu đồng/lượng. Nếu như không vội bán 10 lượng vàng trước đó, chị Hoa đã có thể tiết kiệm 100 triệu đồng cho lần đầu tư này.</p>\r\n\r\n<p>Trưa 6/8, giá vàng miếng SJC tăng vọt lên tới 62,3 triệu đồng/lượng, chốt phiên ở mức 59,6 - 61,4 triệu đồng/lượng (mua vào - bán ra). Tuy đã giảm 900.000 đồng so với mức đỉnh trong ngày, giá kim quý vẫn tăng 2,3 triệu đồng so với chiều hôm trước. Chị Hoa nhẩm tính khoản đầu tư của mình tăng 26 triệu đồng lợi nhuận chỉ sau 1 đêm. Thêm lần nữa, vợ chồng chị quyết định rút tiền gửi ngân hàng để mua thêm 10 lượng vàng, nâng tổng số vàng nắm giữ lên 30 lượng.</p>\r\n\r\n<p>Điều đáng nói, chị Hoa đã mua vàng ở mức giá 62,4 triệu đồng/lượng, cao nhất từ trước đến nay. Trung bình giá mua vào của chị Hoa lúc này đã là 59,7 triệu đồng/lượng.</p>\r\n\r\n<p>Đến ngày 8/8, giá vàng bất ngờ trượt dốc thẳng đứng. Giá bán vàng miếng từ 62,4 triệu đồng/lượng về 60,3 triệu đồng/lượng; giá mua vào cũng giảm 2,25 triệu, chốt phiên ở mức 58,5 triệu/lượng. Đà giảm mạnh khiến nhà đầu tư mua vàng từ 7/8 phải chịu khoản lỗ ròng gần 4 triệu đồng/lượng, tương đương lỗ 6,25% giá trị đầu tư. Với chị Hoa, số lỗ sau một đêm là 35 triệu đồng.</p>\r\n\r\n<p>Những ngày tiếp theo, giá vàng mới thực sự trở thành cơn ác mộng đối với vợ chồng chị Hoa. Giá mua vào tại doanh nghiệp kinh doanh vàng lần lượt giảm từ 58,5 triệu đồng/lượng xuống 56,65 triệu ngày 10/8, xuống tiếp 53,58 triệu ngày 11/8 rồi chốt phiên ngày 12/8 ở mức 52,56 triệu đồng/lượng.</p>\r\n\r\n<p>Chỉ trong vỏn vẹn 1 tuần, tài sản đầu tư của chị Hoa “bốc hơi” 213 triệu đồng, số tiền tương đương gần 1 năm lương của hai vợ chồng chị cộng lại. Tuy nhiên lần này, chị Hoa kiên quyết không bán ra, ít nhất cho đến tháng 11 khi cuộc bầu cử tổng thống Mỹ diễn ra.</p>\r\n\r\n<p>“Nếu giá vàng còn tiếp tục giảm, tôi sẽ cân nhắc rút nốt tiền trong tài khoản ngân hàng để mua thêm, đẩy trung bình giá mua xuống”, chị Hoa nói.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giá vàng sắp tới tăng hay giảm?<br />\r\n<br />\r\nPhiên giao dịch ngày 13/8, giá vàng trên sàn Kitco đi ngang vùng&nbsp;1.930 USD/ounce gần một ngày. Xu hướng đi ngang của thế giới giúp vàng trong nước có phiên giao dịch ổn định khi chênh lệch giá đỉnh và đáy trong phiên dưới 1 triệu đồng/lượng.</p>\r\n\r\n<p>Cụ thể, Công ty TNHH MTV Vàng bạc Đá quý Sài Gòn (SJC) niêm yết giá vàng miếng khu vực TP.HCM ở mức 53,18 - 55,78 triệu đồng/lượng (mua vào - bán ra), tăng 620.000 đồng chiều mua nhưng giảm 600.000 đồng chiều bán so với cuối ngày 12/8.</p>\r\n\r\n<p>Tại vùng giá này, vàng miếng đang thấp hơn gần 5 triệu đồng so với cuối tuần trước, tương đương mức giảm 7,5%. Đà giảm khiến người dân mua vàng lỗ ròng 7,1 triệu/lượng, tương đương 12% sau chưa đầy 1 tuần.</p>\r\n\r\n<p>Thậm chí, nếu tính từ giá đỉnh 62,4 triệu vào cuối ngày 7/8, người mua vàng đã lỗ khoảng 15%.</p>\r\n\r\n<p>Tiến sĩ Nguyễn Trí Hiếu cho rằng hiện tại không thị trường đầu tư nào có được mức an toàn như vàng và đợt điều chỉnh giảm vừa rồi chỉ mang tính ngắn hạn. Ngoài yếu tố đầu cơ đẩy giá vàng tăng nóng thời gian qua, kim quý vẫn có những trợ lực cơ bản mạnh mẽ như dịch bệnh, suy thoái kinh tế, chính sách tiền tệ nới lỏng, căng thẳng địa chính trị.</p>\r\n\r\n<p>Vị chuyên gia cho rằng vàng sẽ sớm tăng trở lại, tuy nhiên, có tăng được trở lại mốc đỉnh hay không rất khó đảm bảo. Từ nay đến cuối năm, vàng sẽ còn nhiều đợt biến động lớn tương tự</p>\r\n\r\n<p>.<img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mua-vang-2.jpg\" style=\"height:382px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Vàng vừa có chuỗi giảm 4 phiên liên tiếp. Ảnh:&nbsp;Chí Hùng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đồng quan điểm, ông Edward Moya, chuyên gia cao cấp tại Công ty giao dịch ngoại hối Oanda (Mỹ), nhận định về lâu dài, các trợ lực giúp giá vàng tăng cao vẫn còn, vì vậy, các nhà đầu tư có thể cân nhắc mua vào thời điểm hiện tại.</p>\r\n\r\n<p>\"Các nhà đầu tư sẽ mua vào khi vàng giảm giá. Nhưng trong ngắn hạn, giá vàng có thể biến động qua ngưỡng&nbsp;2.000 USD/ounce nhiều lần. Vàng còn bị bán tháo mạnh khi chứng khoán Mỹ leo lên mức cao kỷ lục. Dòng tiền đang chảy vào chứng khoán\", ông Moya chia sẻ với&nbsp;Zing.</p>\r\n\r\n<p>Có cái nhìn thận trọng hơn, ông Trần Thanh Hải, Chủ tịch HĐQT Công ty CP Đầu tư và Kinh doanh vàng Việt Nam (VGB), cho rằng yếu tố tăng giá hiện nay rất yếu dù vàng đang hồi phục. Tổng thống&nbsp;Donald Trump&nbsp;đã ký gói trợ cấp thất nghiệp cho người dân Mỹ nên yếu tố tăng giá vàng bằng cung tiền đã hết. Đây cũng là nguyên nhân trực tiếp ảnh hưởng vàng thế giới thời gian qua, từ đó tác động tới giá trong nước.</p>\r\n\r\n<p>“Vàng hiện nay chỉ tăng mạnh lại khi căng thẳng Mỹ - Trung leo thang, dịch bệnh biến động bất ngờ, hoặc căng thẳng địa chính trị lớn… Ngoài ra yếu tố từ nền kinh tế Mỹ chưa có gì mới để vàng tăng lại”, ông Hải nói.</p>\r\n\r\n<p>Trong khi đó, ông Leigh Goehring, đối tác quản lý của Goehring &amp; Rozencwajg Associates, cho rằng từ nay đến cuối năm,&nbsp;<a href=\"https://vietnamnet.vn/gia-vang-tag75311.html\">giá vàng</a>&nbsp;sẽ thấp hơn hiện nay khi nền kinh tế bắt đầu mạnh lên, nhiều người trở lại làm việc hơn và những người mua vàng trước đó vì sợ dịch Covid-19 sẽ trở thành người bán. Tuy nhiên, ông đánh giá đây là một cơ hội mua vàng tốt trước khi giá kim quý tăng trở lại vì vấn đề lạm phát.</p>\r\n\r\n<p>(Theo Zing)</p>', 'GxlW_mua-vang-1-1-360x240.jpg', 0, 0, 13, '2020-08-14', '2020-08-14'),
(99, 'Diễm Quỳnh, Thảo Vân váy ngắn tươi tắn bên MC Anh Tuấn', 'diem-quynh-thao-van-vay-ngan-tuoi-tan-ben-mc-anh-tuan', '<p>Trong bộ ảnh thời trang mới,&nbsp;Quỳnh Kool mặc váy lụa, tạo dáng lơi lả và gợi cảm.</p>', '<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/23/mc-diem-quynh-mc-thao-van-vay-ngan-tuoi-tan-ben-mc-anh-tuan.jpg\" style=\"height:465px; width:650px\" /></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/showbiz-viet-tag50248.html\">Sao Việt&nbsp;</a>14/8: Trên trang cá nhân MC Thảo Vân đăng ảnh với 2 đồng nghiệp MC Diễm Quỳnh và MC Anh Tuấn với hình ảnh khác lạ khi đội tóc giả, diện váy ngắn tươi tắn. Nữ MC úp mở với chia sẻ: \'\'Siêu phẩm sắp trình diễn chăng?\'\'.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/22/thanh-son-tinh-tu-om-quynh-kool.jpg\" style=\"height:386px; width:300px\" />&nbsp;&nbsp;<img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/22/thanh-son-tinh-tu-om-quynh-kool-2.jpg\" style=\"height:385px; width:300px\" /></p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/quynh-kool-tag154771.html\">Quỳnh Kool</a>&nbsp;chụp bộ ảnh mới, tạo dáng lơi lả. Cô trích dẫn câu hay trong sách \"Đắc nhân tâm\": \"Hãy nhớ rằng, người ta co thể đánh giá tính tình của một con người bằng cách làm cho người ấy nổi giận\".</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/22/117540989-2977825638994810-4548854377368003053-o.jpg\" style=\"height:960px; width:637px\" /></p>\r\n\r\n<p>Phương Mỹ Chi tỏ lòng biết ơn đến FC - những người đồng hành với cô 7 năm,&nbsp;có fan&nbsp;đã lập gia đình, thành đạt trong công việc... \"Các anh chị từng nói, em là thanh xuân của các anh chị nhưng thật ra, các anh chị mới là thanh xuân của em\", cô viết.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/22/quynh-kool-loi-la-goi-cam.jpg\" style=\"height:444px; width:300px\" />&nbsp; &nbsp;<img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/22/quynh-kool-loi-la-goi-cam-1.jpg\" style=\"height:519px; width:350px\" /></p>', 'I5TU_diem-quynh-thao-van-vay-ngan-tuoi-tan-ben-mc-anh-tuan-360x240.jpg', 0, 0, 14, '2020-08-14', '2020-08-14'),
(100, 'Cuộc thi hoa hậu sẽ bị thương mại hoá nếu mở toang quy chế', 'cuoc-thi-hoa-hau-se-bi-thuong-mai-hoa-neu-mo-toang-quy-che', '<p>\'\'Các cô đăng quang quảng cáo nhãn hàng lộ liễu, khoe cuộc sống sang chảnh. Nhiều người nói thi hoa hậu phải chăng để đại gia chọn vợ cũng chẳng sai”, nhà biên kịch Chu Thơm nói.</p>', '<p>Lắm cuộc thi hoa hậu quá</p>\r\n\r\n<p>Theo nhà biên kịch Chu Thơm - nguyên Phó trưởng phòng phụ trách Phòng Nghệ thuật, Cục Nghệ thuật biểu diễn (Bộ VHTT&amp;DL), dư luận những năm gần đây liên tục đặt câu hỏi:&nbsp;Ở đâu ra lắm hoa hậu thế?, lẽ thường Cục NTBD nên siết lại các cuộc thi giờ lại nới lỏng, mở toang ra.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/13/cuoc-thi-hoa-hau-se-bi-thuong-mai-hoa-neu-mo-toang-quy-che.jpeg\" style=\"height:467px; width:700px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Hoa hậu Việt Nam 2016 Đỗ Mỹ Linh cùng 2 á hậu.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>“Một năm, hai năm không có cuộc thi hoa hậu thì hình ảnh người phụ nữ Việt Nam có bị lu mờ, có bị mai một đi trên trường quốc tế hay không? Lắm cuộc thi quá, cuộc thi nào cũng lùm xùm, cũng có tì vết. Lúc thì<a href=\"https://vietnamnet.vn/hoa-hau-viet-nam-2020-tag264370.html\">&nbsp;hoa hậu</a>&nbsp;chưa học hết lớp 12, lúc thì hoa hậu dối trá lấy chồng rồi nói chưa, có cô hoa hậu đăng quang rồi sống nhố nhăng. Tôi hoàn toàn không muốn tổ chức thi hoa hậu, người đẹp dày đặc, càng không muốn đưa về các tỉnh. Bởi để tham gia cuộc thi, có người phải bỏ bê rất nhiều thứ để dồn vào đó, quanh năm ngày tháng chỉ đi thi để sau một đêm được đổi đời”, nhà biên kịch Chu Thơm chia sẻ.</p>\r\n\r\n<p>Thêm vào đó, nhà biên kịch Chu Thơm cho rằng thực tế, các hoa hậu sau khi đăng quang ít làm việc có ích vì cộng đồng, toàn khoe túi, khoe xe, khoe lấy đại gia. “Tôi thấy cuộc thi hoa hậu, nhà tổ chức lợi ghê gớm, họ thu được nhiều tiền từ tài trợ. Cứ hé răng ra là có nhà tài trợ trong khi các cuộc thi trí tuệ con người, trí tuệ Việt Nam như&nbsp;Đường lên đỉnh Olympia&nbsp;hay như thế thì ít người tài trợ lắm. Các cô hoa hậu đăng quang là quảng cáo nhãn hàng lộ liễu, khoe cuộc sống sang chảnh dù trước khi đăng quang chẳng khá giả gì. Nhiều người nói cuộc thi hoa hậu phải chăng để đại gia chọn vợ cũng chẳng sai”, nhà biên kịch Chu Thơm thẳng thắn.</p>\r\n\r\n<p>Nhà biên kịch Chu Thơm cũng cho rằng làm gì cũng phải có cấp bậc cụ thể. “Từ học sinh giỏi của trường mới được cử đi thi học sinh giỏi cấp huyện, từ cấp huyện lên cấp tỉnh rồi tới thành phố, quốc gia. Hoa hậu đi thi quốc tế cũng vậy, tự nhiên chẳng có thành tích gì trong nước mà cũng được đi thi miễn có giấy mời”, ông Chu Thơm nói.</p>\r\n\r\n<p>Liên quan đến dự thảo Nghị định mới về các cuộc thi người đẹp với nhiều tiêu chí được nới lỏng, ông Xuân Sơn - TBT báo Tiền Phong - đơn vị nhiều năm có kinh nghiệm tổ chức Hoa hậu Việt Nam chia sẻ: \"Trong chương 3 dự thảo Nghị định, có một số điểm làm chúng tôi lo ngại.</p>\r\n\r\n<p>Thứ nhất, hai phương án 1 và 2 của điều 23, chương 3 đều quy định các cuộc thi người đẹp, người mẫu toàn quốc sẽ do các tỉnh, thành phố nơi diễn ra vòng chung kết cấp phép. Điều này có thể dẫn đến hai hệ luỵ.</p>\r\n\r\n<p>Thứ nhất, chúng ta không còn cuộc thi hoa hậu nào có thương hiệu quốc gia nữa. Thực tế trong quá trình phát triển loại hình hoạt động này, có một số cuộc thi đã tạo được uy tín vững chắc, mang tầm quốc gia và có tính đại diện lớn, trong đó có cuộc thi Hoa hậu Việt Nam. Một trong các yếu tố làm nên tầm cỡ của nó chính là được cơ quan quản lý nhà nước có thẩm quyền cao nhất về lĩnh vực này cấp phép.</p>\r\n\r\n<p>Thứ hai, nhiều khả năng lại xảy ra loạn thi. Với dự thảo mỗi tỉnh thành mỗi năm được cấp phép 1 cuộc (phương án 1), thậm chí đến 4 cuộc (phương án 2), về lý thuyết mỗi năm nước ta có thể cấp phép đến hơn 60, thậm chí hơn 250 cuộc thi. Cho dù trên thực tế khó có thể xảy ra trường hợp có nhiều cuộc thi đến vậy nhưng khả năng có nhiều cuộc thi xuất hiện và loạn thi là hoàn toàn có thể xảy ra. Bấy lâu nay, các cuộc thi toàn quốc do Bộ VHTT&amp;DL cấp phép chặt chẽ mà dư luận vẫn liên tục phê phán là loạn thì với quy định như trên, gần như chắc chắn tình hình sẽ xấu hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/cuoc-thi-hoa-hau-se-bi-thuong-mai-hoa-neu-mo-toang-quy-che.jpg\" style=\"height:601px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Ông Lê Xuân Sơn - TBT báo Tiền Phong.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong hội nghị lấy ý kiến khi Bộ VH,TT&amp;DL chuẩn bị dự thảo tổ chức tại TP.HCM tôi có nhấn mạnh rằng ở nước ta, danh hiệu hoa hậu và các danh hiệu cao khác trong cuộc thi hoa hậu tạo ra cho người đoạt rất nhiều cơ hội. Tức là các cuộc thi hoa hậu, người đẹp ở nước ta rất hất dẫn, nhất là đối với những đơn vị, cá nhân tiếp cận việc tổ chức cuộc thi thuần tuý theo hướng kinh doanh. Chúng ta vẫn nghe dư luận về những cuộc thi hoa hậu do người Việt Nam sang một nước khác tổ chức với đôi ba chục thí sinh (thiếu thì thuê thêm), thuê một phòng tổ chức sự kiện ở một khách sạn, thi một vài hoặc thậm chí một buổi là xong. Về nước vẫn công bố là hoa hậu, á hậu như ai. Bây giờ, với quy định mới này, khả năng những người như vậy không cần ra nước ngoài tổ chức nữa.</p>\r\n\r\n<p>Thứ ba, quy định này nhiều khả năng gây phiền hà hơn nhiều cho những cuộc thi lớn được tổ chức nghiêm túc. Chúng tôi sợ rằng việc cấp phép của một tỉnh, thành phố sẽ không có hiệu lực đối với các địa phương khác. Những cuộc thi lớn, ngoài việc các vòng chung khảo và vòng chung kết được tổ chức tại 3 tỉnh thành khác nhau các cuộc thi phụ, các hoạt động xã hội, hoạt động đồng hành của nó còn diễn ra ở hàng chục địa phương khác nữa. Như vậy, thay vì xin phép một lần như trước đây, các cuộc thi lớn sẽ phải xin hàng chục giấy phép? Còn đối với phương án 3 của dự thảo điều 23 thì không rõ việc cấp phép sẽ thế nào.</p>\r\n\r\n<p>Về điều kiện thí sinh đi thi quốc tế, dự thảo này bỏ quy định từng có trong các nghị định 79 (2012) và 15 (2016) là thí sinh đi thi các cuộc thi quốc tế phải đạt giải chính tại một cuộc thi tổ chức trong nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dự thảo này có vẻ công bằng và mở ra nhiều cơ hội cho nhiều người hơn nhưng lại tiềm ẩn rủi ro lớn là những người không xứng đáng có thể làm mất uy tín của phụ nữ Việt Nam, con người Việt Nam ở bình diện quốc tế. Và với việc được gắn mác, trở về, họ có điều kiện làm nhiều thứ không ổn khác nữa. Sẽ hợp lý hơn nếu “mở” quy định “đoạt giải chính” tại các cuộc thi trong nước đó ra chút nữa. Hoặc có một cơ chế tuyển chọn, thẩm định khác\'\'.</p>\r\n\r\n<p>Các cuộc thi nhan sắc thiên về yếu tố kinh doanh nhiều hơn là tìm kiếm</p>\r\n\r\n<p>Là người nhận vương miện Hoa hậu Việt Nam năm 2010, từng là thành viên giám khảo của cuộc thi này năm 2014 và tham gia \"cầm cân nảy mực\" trong nhiều cuộc thi sắc đẹp khác, Hoa hậu&nbsp;<a href=\"https://vietnamnet.vn/ngoc-han-tag39942.html\">Ngọc Hân&nbsp;</a>chia sẻ&nbsp;hiện&nbsp;có quá nhiều cuộc thi nhan sắc. Nhiều người còn nhận xét là loạn thi hoa hậu.</p>\r\n\r\n<p>“Tôi là người trong nghề, trong giới nhưng đôi khi nghe tới tên một hoa hậu nào đó mà nghĩ mãi vẫn không nhớ ra bạn ấy là ai, cuộc thi bạn ấy tham gia là gì. Công chúng có khi còn hoang mang hơn. Nếu nới lỏng việc thi người đẹp như trong dự thảo mới, phân cấp cho các địa phương tổ chức thi người đẹp toàn quốc, tôi e là danh hiệu Hoa hậu sẽ trở nên tràn lan, không còn nhiều ý nghĩa như hiện nay. Nếu nới lỏng quy định, các cuộc thi nhan sắc sẽ thiên về yếu tố kinh doanh nhiều hơn là tìm kiếm, tôn vinh vẻ đẹp của người phụ nữ Việt Nam”, Hoa hậu Ngọc Hân chia sẻ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/12/15/mo-toang-canh-cua-cho-cac-cuoc-thi-nhan-sac-1.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Hoa hậu Ngọc Hân đăng quang năm 2010.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Về việc dự thảo Nghị định mới quy định người đẹp không cần đạt danh hiệu chính thức tại các cuộc thi trong nước vẫn được đi thi quốc tế, hoa hậu Ngọc Hân đánh giá đây là quy định rất táo bạo của người làm quản lý. “Quy định này có thể mở ra cơ hội cho rất nhiều người đẹp không phù hợp với tiêu chí các cuộc thi sắc đẹp trong nước nhưng lại có vẻ đẹp tiệm cận tiêu chí của các cuộc thi nhan sắc thế giới. Tuy nhiên, tôi nghĩ ít có đơn vị hay người đẹp nào dám mạnh dạn đem chuông đi đánh xứ người khi chưa có trải nghiệm trong các cuộc thi nhan sắc trong nước. Bởi lẽ, sân chơi quốc tế rất khắc nghiệt, phải là người có bản lĩnh, có kinh nghiệm mới dám chinh phục. Tôi đánh giá quy định trên của dự thảo là rất táo bạo”.&nbsp;</p>\r\n\r\n<p>Hoa hậu Ngọc Hân nêu ý kiến, giải pháp an toàn vẫn là chọn người đẹp có thể không nằm trong top 3 các cuộc thi nhan sắc trong nước, chỉ đoạt giải phụ nhưng có vẻ đẹp phù hợp để tham dự các cuộc thi quốc tế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'kWly_hai-nam-viet-nam-khong-co-hoa-hau-vi-the-cua-phu-nu-co-mai-mot-1-360x240.jpg', 0, 0, 15, '2020-08-14', '2020-08-14'),
(101, 'Tác giả \'Nhật ký của mẹ\': Trải qua biến cố tôi mạnh mẽ hơn nhiều', 'tac-gia-nhat-ky-cua-me-trai-qua-bien-co-toi-manh-me-hon-nhieu', '<p>\"Một&nbsp;năm trước, biến cố xảy ra khiến cuộc sống của tôi không còn như cũ. Tôi trải qua giai đoạn rất buồn, gần như trầm cảm, mất 1 năm để vượt qua\", Nguyễn Văn Chung chia sẻ.</p>', '<p>Nguyễn Văn Chung chơi ca khúc đầu tiên trong 6 bước trị liệu của album \"Heal me\". Clip:&nbsp;Hương Giang</p>\r\n\r\n<p>Trầm cảm vì đổ vỡ hôn nhân</p>\r\n\r\n<p>- Ra album nhạc trị liệu, anh cũng chữa trị nỗi đau của mình bằng âm nhạc?</p>\r\n\r\n<p>Âm nhạc ngoài công năng giải trí, hưởng thụ, biểu diễn… còn có thể trị liệu tâm hồn, đơn cử là nhạc thiền. Tuy nhiên, tôi không làm nhạc thiền mà nhạc trị liệu có tính đại chúng và dễ nghe hơn.</p>\r\n\r\n<p>Chính xác album&nbsp;Heal me&nbsp;của tôi ra đời từ biến cố 1 năm trước. Sự kiện ấy khiến cuộc sống của tôi không còn như trước nữa. Khi ấy, tôi trải qua giai đoạn rất buồn, gần như trầm cảm. Tôi mất đúng 1 năm để vượt qua hoàn toàn nhưng không muốn chia sẻ quá nhiều khi mọi thứ đã là quá khứ và tôi hiện cũng ổn rồi.</p>\r\n\r\n<p>10 năm trước, khi buồn, tôi thường ở một mình bên cây đàn giữa đêm. Đó là hoàn cảnh bài&nbsp;Tiếng dương cầm trong đêm&nbsp;ra đời. Tôi xem cây piano như người bạn của mình nên luôn tìm đến nó khi buồn. Với các bài nhạc cho album&nbsp;Heal me, tôi chỉ sáng tác giai điệu mà không viết lời. Nếu có lời, bạn chỉ nhìn chúng là nỗi buồn của tôi chứ không phải của bạn.</p>\r\n\r\n<p>Album này chia sẻ đến mọi người cách tôi vượt qua biến cố, nỗi buồn và sự tiêu cực. Tuy nhiên, nhạc trị liệu không thay thế bác sĩ tâm lý. Tôi mong bài nhạc của mình sẽ là người bạn bên cạnh khán giả khi họ gặp khó khăn như cây đàn luôn ở bên tôi vậy.</p>\r\n\r\n<p>- Xảy ra cú sốc ấy, anh vẫn không gián đoạn công việc, có thể hiểu là mọi thứ không quá nghiêm trọng?</p>\r\n\r\n<p>Khi buồn, tôi lao vào làm việc nhiều hơn. Tôi không bao giờ để cảm xúc tiêu cực chi phối con người mình, nhất là tôi còn uy tín với khách hàng chứ! Tôi làm việc nhiều để không còn thời gian suy nghĩ linh tinh. Dù khi xong việc, tôi vẫn rất cô đơn, bên cạnh chỉ có cây đàn thôi. Một năm trước, tôi quyết định dừng lại để bước tiếp, để tốt hơn chính mình của ngày xưa. Chỉ như vậy, tôi mới thấy quyết định của mình có ý nghĩa, cũng như để mọi người và cả người còn lại trong cuộc thấy rằng tôi quyết định đúng.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/ns-nguyen-van-chung.jpg\" title=\"Nguyễn Văn Chung bên cây Piano - người bạn luôn có mặt khi anh buồn.\"><img alt=\"Tác giả \'Nhật ký của mẹ\': Trải qua biến cố tôi mạnh mẽ hơn nhiều\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/ns-nguyen-van-chung.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Văn Chung bên cây Piano - người bạn luôn có mặt khi anh buồn.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- Anh có hối tiếc và muốn thay đổi điều gì trong việc dừng lại cuộc hôn nhân này?</p>\r\n\r\n<p>Tôi từng suy nghĩ rất nhiều. Mọi người thường nói tôi là người điềm đạm, bình tĩnh. Khi tôi quyết định như vậy, nghĩa là mọi thứ không thể cứu vãn. Thật ra, bây giờ tôi có ước cho thời gian quay lại thì không thực tế và cũng không cần thiết nữa! Mỗi chuyện xảy ra với tôi ắt có lý do, tôi chẳng muốn thay đổi điều gì trong quá khứ cả. Quá khứ của tôi tạo nên&nbsp;<a href=\"https://vietnamnet.vn/nguyen-van-chung-tag41185.html\">Nguyễn Văn Chung</a>&nbsp;hôm nay. Tôi thấy mình bây giờ rất ổn.</p>\r\n\r\n<p>Có điều này, tôi muốn nhấn mạnh rằng mình không tiêu cực. Tôi chưa từng có suy nghĩ sẽ tự hành hạ hay làm tổn thương bản thân. Tôi luôn hướng mọi thứ tốt đẹp hơn. Một năm qua, tôi có nhiều thời gian hơn cho bản thân. Tôi rèn luyện cơ thể như tập gym, thể dục, đá banh… và luôn đầy năng lượng.</p>\r\n\r\n<p>Với tôi, chia tay không phải chấm hết mà mở ra cho mỗi người một lối đi tìm hạnh phúc. Sau chia tay, cô ấy xứng đáng có được hạnh phúc riêng và tôi cũng vậy. Chúng tôi không mang lại hạnh phúc cho nhau nữa mới phải chia tay. Tôi không nhìn chia tay toàn là đau khổ.</p>\r\n\r\n<p>Quan trọng hơn, hai người cần văn minh hậu chia tay khi giữa chúng tôi vẫn còn điểm chung là con cái. Chúng tôi có trách nhiệm lo cho con tốt nhất, từ tinh thần đến vật chất. Các con cần ba sẽ có ba, cần mẹ sẽ có mẹ. Các con không bị thiếu thốn cái gì trong hành trình phát triển thành người ngay cả khi bố mẹ không ở bên nhau nữa. Với chúng tôi, cái tình có thể hết nhưng cái nghĩa vẫn còn. Dù gì, chúng tôi cũng là người thân của nhau!</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/nvc.jpg\" title=\"Nguyễn Văn Chung trị liệu tâm hồn bằng âm nhạc.\"><img alt=\"Tác giả \'Nhật ký của mẹ\': Trải qua biến cố tôi mạnh mẽ hơn nhiều\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/nvc.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Văn Chung trị liệu tâm hồn bằng âm nhạc.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tôi chưa có tình yêu mới</p>\r\n\r\n<p>- Thay đổi không khí một chút, phải nói rằng tôi đi đến đâu cũng có thể nghe bài “Nhật ký của mẹ” từ… bệnh viện phụ sản đến quán café. Anh nhiều hit bậc nhất, phải chăng tiền tác quyền đã tràn két?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiền tác quyền của tôi không cao nhất, chỉ trong top 5 – 10 thôi. Dù vậy, nó đảm bảo cho tôi và cả con tôi 1 cuộc sống tốt đẹp, đầy đủ. Các con tôi có thể thu tiền bản quyền đến 50 năm sau khi tôi mất mà.</p>\r\n\r\n<p>Dĩ nhiên, tiền tràn két thì không! Tôi vẫn đang giải quyết những ảnh hưởng từ sự cố 1 năm trước. Đổ vỡ không chỉ là cú sốc tinh thần mà còn ảnh hưởng rất nghiêm trọng đến phương diện tài chính.</p>\r\n\r\n<p>Tôi thấy may mắn khi chúng tôi thỏa thuận được chuyện con cái. Với tôi, mất hết tiền cũng được, miễn là ba và mẹ vẫn lo cho con đầy đủ dù con ở với ai đi nữa. Chúng tôi không bao giờ được phép làm gì ảnh hưởng xấu đến tinh thần của con, như ba nói xấu mẹ với con hoặc ngược lại. &nbsp;</p>\r\n\r\n<p>- Anh nói thế nào để các con hiểu chuyện xảy ra giữa ba và mẹ?</p>\r\n\r\n<p>Tôi và mẹ bé chưa nói cho bé biết. Tôi chỉ nói:&nbsp;Con ở bên mẹ, chăm sóc mẹ nhé, còn ba chăm sóc bà. Chúng ta là đàn ông, phải biết chăm sóc mẹ của mình. Tôi không chuẩn bị tâm lý hay tư tưởng để nói cho con. Rồi sẽ có ngày bé đủ lớn và tự biết chuyện gì xảy ra với ba mẹ. Sau chia tay, tôi đau đáu nhất chuyện con sẽ lớn lên theo từng giai đoạn thế nào. Ba mối quan tâm lớn nhất của tôi hiện là con tôi, mẹ tôi và công việc.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/nguyen-van-chung-suyt-tram-cam-sau-hon-nhan-do-vo.jpg\" title=\"Nguyễn Văn Chung được cô bạn thân Ốc Thanh Vân động viên.\"><img alt=\"Tác giả \'Nhật ký của mẹ\': Trải qua biến cố tôi mạnh mẽ hơn nhiều\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/nguyen-van-chung-suyt-tram-cam-sau-hon-nhan-do-vo.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Văn Chung được cô bạn thân Ốc Thanh Vân động viên.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- Có ý kiến nói nhạc sĩ phải buồn viết nhạc mới hay, có đúng không?</p>\r\n\r\n<p>Tôi muốn chỉnh lại:&nbsp;Nhạc sĩ phải trải nghiệm viết nhạc mới hay, thì đúng hơn. Người nhạc sĩ phải từng buồn, từng vui, trải qua hạnh phúc lẫn những hối tiếc… Nếu không có con, tôi sẽ không thể viết được bài nhạc thiếu nhi nào. Nếu không từng trải qua biến cố, sẽ không có album&nbsp;Heal me.</p>\r\n\r\n<p>- Việc anh lập kỷ lục với 300 ca khúc thiếu nhi có phải là “chỉ tiêu” anh mong muốn đạt được từ đầu dự án?</p>\r\n\r\n<p>Khi viết xong một album, tôi mới thấy thiếu rất nhiều đề tài. Càng viết, tôi càng thấy thiếu những điều bình thường nhất xung quanh con và không bao giờ bị cạn ý tưởng. 300 bài ấy chia làm 5 nội dung: gia đình; mái trường; thế giới tuổi thơ; trò chơi và những bài học nhỏ và những bài ngày lễ, Tết.&nbsp;</p>\r\n\r\n<p>Những năm qua, tôi có quá nhiều nỗi lo nên không thể dành hết thời gian, tâm trí cho công việc. Những gánh nặng và trách nhiệm ấy tự tôi gánh lấy, không muốn chia sẻ với ai. Tôi đã rất chật vật để cân bằng mọi thứ. Hiện tại, tôi vẫn chưa có tình yêu mới nên 24 tiếng mỗi ngày hoàn toàn dành cho công việc và cho chính mình.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/ns-nguyen-van-chung-va-con-gai.jpg\" title=\"Nguyễn Văn Chung bên con gái.\"><img alt=\"Tác giả \'Nhật ký của mẹ\': Trải qua biến cố tôi mạnh mẽ hơn nhiều\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/ns-nguyen-van-chung-va-con-gai.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Văn Chung bên&nbsp;con gái.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- Trở lại với nhạc tình yêu, anh có còn tự tin sẽ thành hit-maker?</p>\r\n\r\n<p>Tôi sẽ cố gắng vì tôi đã từng làm được rồi. Tôi không ngại mình có tạo hit hay không vì sau tất cả, tôi đang làm vì đam mê. Tìm được 1 ca sĩ hát hay nhạc của mình đã là niềm vui lớn nhất. Như vậy, tôi có quay trở lại vị trí cũ hay không thì cũng không còn gì hối tiếc!</p>\r\n\r\n<p>Trở lại thị trường hiện tại, có lẽ tôi đã hơi chậm. Tôi phải bù đắp kiến thức về sản xuất, phát hành và truyền thông. Tôi chẳng ngại học hỏi từ các bạn trẻ, đàn em của mình. Mặt khác, tôi tự tin có kinh nghiệm, khả năng thích nghi nhanh và cảm xúc. Xem 1 bộ phim, tôi có thể khóc được thì biết trái tim mình còn nóng. Tôi chỉ sợ trái tim chai sạn, không còn cảm xúc viết nhạc. Tôi tin khi làm bằng tất cả nỗ lực, ngay cả khi không vinh quang thì cũng phải ồn ào!&nbsp;(cười)</p>', 'bhIF_nguyen-van-chung-suyt-tram-cam-sau-hon-nhan-do-vo-1-360x240.jpg', 0, 0, 16, '2020-08-15', '2020-08-15'),
(102, '\'Đi qua mùa hạ\' bất ngờ sốt trở lại vì diễn viên Nguyễn Trọng Hưng', 'di-qua-mua-ha-bat-ngo-sot-tro-lai-vi-dien-vien-nguyen-trong-hung', '<p>Dù đã lên sóng được 3 năm nhưng&nbsp;\'Đi qua mùa hạ\' lọt top tìm kiếm nhiều, nhiều clip đạt triệu view trong vài giờ.</p>', '<p>Clip trích đoạn&nbsp;tập 23 phim \'Đi qua mùa hạ\' đạt hơn 4 triệu view</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/di-qua-mua-ha-tag154772.html\">Đi qua mùa hạ</a>là bộ phim về đề tài sinh viên công chiếu trong khung giờ Rubic8 trên VTV3 từ tháng 7/2017. Phim có sự góp mặt của các diễn viên Quỳnh Kool, Đình Tú, Bình An và&nbsp;<a href=\"https://vietnamnet.vn/nguyen-trong-hung-tag285942.html\">Nguyễn Trọng Hưng</a>. Thời điểm lên sóng&nbsp;Đi qua mùa hạ&nbsp;không mấy được chú ý.</p>\r\n\r\n<p>Tuy nhiên vì scandal đời tư của diễn viên&nbsp;Nguyễn Trọng Hưng mà bộ phim được dân mạng tìm kiếm trở lại với lượt xem chóng mặt. Đây là điều hiếm gặp với một bộ phim truyền hình đã phát sóng từ lâu.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/di-qua-mua-ha-bat-ngo-sot-tro-lai-vi-dien-vien-trong-hung-1.jpg\" title=\"Vai Trung của Trọng Hưng chỉ là vai phụ và khá mờ nhạt trong phim \"><img alt=\"\'Đi qua mùa hạ\' bất ngờ sốt trở lại vì diễn viên Nguyễn Trọng Hưng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/di-qua-mua-ha-bat-ngo-sot-tro-lai-vi-dien-vien-trong-hung-1.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vai Trung của Trọng Hưng chỉ là vai phụ và khá mờ nhạt trong phim \'Đi qua mùa hạ\'.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đi qua mùa hạ&nbsp;có tới 20 nghìn lượt tìm kiếm, lọt top Google Trends Việt Nam ngày 13/8. Trích đoạn của các tập phim có sự tham gia của Nguyễn Trọng Hưng đều có lượt xem rất cao trên&nbsp;VTV Giải trí. Riêng trích đoạn tập 23 sau 1 ngày đăng tải trên fanpage này đã đạt 4,2 triệu lượt xem, 81 nghìn lượt thích và gần 9000 bình luận.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Clip trích đoạn tập 22 có diễn viên Trọng Hưng đến nay cũng đạt 5,6 triệu lượt xem sau 2 ngày đăng tải với 105 nghìn lượt thích và 2500 bình luận trên VTV Giải trí. Phần đông nhận xét diễn xuất của Trọng Hưng trong vai Hưng không mấy ấn tượng và xem chủ yếu vì tò mò, thậm chí khán giả Lan Anh bình luận: \"Mong từ nay Hưng đừng xuất hiện trên bất kể bộ phim nào nữa\".&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trích đoạn phim \'Đi qua mùa hạ\' tập 23 đạt hơn 5 triệu view</p>', '64vv_di-qua-mua-ha-bat-ngo-sot-tro-lai-vi-dien-vien-trong-hung.gif', 0, 0, 17, '2020-08-15', '2020-08-15'),
(103, 'MC Thu Hương VTV có thể phải phẫu thuật thêm sau 4 ca mổ', 'mc-thu-huong-vtv-co-the-phai-phau-thuat-them-sau-4-ca-mo', '<p>Lần đầu tiên MC Thu Hương chia sẻ hình ảnh trước và sau đợt phẫu thuật mắt cũng như tình trạng sức khỏe của mình.</p>', '<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-1.jpg\" title=\"MC Thu Hương là gương mặt quen thuộc của nhiều chương trình trên VTV. \"><img alt=\"MC Thu Hương VTV có thể phải phẫu thuật thêm sau 4 ca mổ\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-1.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>MC Thu Hương là gương mặt quen thuộc của nhiều chương trình trên VTV.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><a href=\"https://vietnamnet.vn/mc-thu-huong-tag141740.html\">MC Thu Hương</a>&nbsp;vừa có bài chia sẻ khá dài trên trang cá nhân sau 1 tuần kết thúc ca mổ cuối cùng. Cô phải nhờ người giúp đỡ để có thể đăng tải bài viết bởi thời gian qua&nbsp;Thu Hương không thể sử dụng điện thoại hay đọc tin nhắn bởi mắt vẫn còn mờ nhòe sau phẫu thuật.&nbsp;</p>\r\n\r\n<p>Nữ MC của Cafe sáng với VTV3 cho biết đã lên bàn mổ 4 lần. \"Vẫn nhớ cảm giác khi ngồi chờ để lên bàn mổ trong lần đầu tiên, 2 tay nắm chặt, run, hồi hộp, \'cuối cùng mình cũng phẫu thuật rồi\', mắt mình bắt đầu ướt, nhưng mình nghĩ điều đó không tốt cho ca phẫu thuật nên đã cố gắng lấy lại bình tĩnh. Vẫn nhớ ca phẫu thuật thứ 3, sau khi về giường bệnh, mình đau đến mức phải bật khóc. Mắt mình phải khâu, nó đau đớn, vướng víu và khó chịu vô cùng. Mình lên bàn mổ nhiều đến nỗi giờ quen và thành thục các khâu chuẩn bị, đến nỗi các y bác sĩ cũng nhớ mặt. Và vẫn nhớ cảm giác tay nắm chặt vì đau, đến nỗi vết móng tay in hằn trong lòng bàn tay nữa\", cô chia sẻ về những gì&nbsp;mới&nbsp;trải qua.&nbsp;</p>\r\n\r\n<p>Thu Hương cho biết sau đợt&nbsp;phẫu thuật, mắt cô cuối cùng đã được cảm nhận màu sắc trở lại, hình ảnh đậm nét hơn trước. Nhưng do mới phẫu thuật xong nên mắt&nbsp;của nữ MC&nbsp;còn nhìn mờ nhoè. \"Thời gian đầu này mình cảm thấy như không còn là đôi mắt của mình nên thấy khá stress và lo lắng, có những lúc còn cảm thấy bực bội, bất lực với đôi mắt của mình. Bác sĩ nói mắt mình cần thời gian thích nghi. Có lẽ sẽ lâu?... Nhưng mình cũng hơi lo không biết liệu rồi mình có nhìn rõ được hoàn toàn thật không\",&nbsp;cô viết.</p>\r\n\r\n<p><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-3.jpg\"><img alt=\"MC Thu Hương VTV có thể phải phẫu thuật thêm sau 4 ca mổ\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-3.jpg\" title=\"MC Thu Hương VTV\" /></a><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-2.jpg\"><img alt=\"MC Thu Hương VTV có thể phải phẫu thuật thêm sau 4 ca mổ\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/mc-thu-huong-vtv-2.jpg\" title=\"MC Thu Hương VTV\" /></a></p>\r\n\r\n<p>Hình ảnh trước và sau khi phẫu thuật mắt của&nbsp;MC Thu Hương</p>\r\n\r\n<p>Tuy nhiên Thu Hương vẫn không giấu được cảm giác hạnh phúc khi được nhìn thấy cảnh vật, ánh nắng đầy màu sắc vào ngày về nhà. \"Ít nhất bây giờ không còn ai phải dắt mình đi nữa. Hiện mình bắt đầu cố gắng luyện đọc cho mắt với hy vọng mắt sẽ sớm được nhìn rõ, giờ mình nhìn được chút ở một cự ly nhất định\", cô chia sẻ.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nữ MC VTV hy vọng mọi thứ sẽ tốt đẹp vì&nbsp;cô&nbsp;còn có thể phải phẫu thuật thêm nữa. Cô cho hay nhiều người nghĩ mình bị đục thuỷ tinh thể bình thường như người già nhưng mắt&nbsp;Thu Hương không&nbsp;phải vậy và trường hợp của cô là một bài toán khó. Tuy vậy nữ MC 34 tuổi không chia sẻ kỹ thêm về tình trạng bệnh của mình. Thu Hương cho biết hiện tại có mẹ&nbsp;cô chăm sóc. Thời điểm phẫu thuật&nbsp;cô&nbsp;giấu hoàn toàn và không để mẹ đến viện, thay vào đó là nhờ bạn bè&nbsp;hỗ trợ để bà không phải lo lắng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/mc-thu-huong-tag141740.html\">Thu Hương</a>&nbsp;làm MC chương trình \'Người phụ nữ hạnh phúc\' trên VTV3</p>', 'erhi_mc-thu-huong-vtv-360x240.jpg', 0, 0, 18, '2020-08-15', '2020-08-15');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(104, 'Ông Trump đã lầm tưởng về Trung Quốc?', 'ong-trump-da-lam-tuong-ve-trung-quoc-', '<p>Trung Quốc ngày nay đã lớn mạnh hơn nhiều so với 4 năm trước.</p>', '<p>Nước Mỹ trong giai đoạn này của nhiệm kỳ Tổng thống Donald Trump đang đối mặt với hàng loạt vấn đề, từ biểu tình ôn hòa đến đại dịch Covid-19.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/ong-trump-da-lam-tuong-ve-trung-quoc.jpg\" title=\"Tổng thống Mỹ Donald Trump và Chủ tịch Trung Quốc Tập Cận Bình. Ảnh: Reuters\"><img alt=\"Ông Trump đã lầm tưởng về Trung Quốc?\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/09/ong-trump-da-lam-tuong-ve-trung-quoc.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng thống Mỹ Donald Trump và Chủ tịch Trung Quốc Tập Cận Bình. Ảnh: Reuters</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Với nhiều người ủng hộ Tổng thống Trump, một chủ đề mà họ thường nhắc tới để bênh vực ông, đó là \"những quả bóng sút về phía Trung Quốc\". Nhưng thực tế, ông Trump không làm được như vậy, theo cây viết bình luận Mona Charen trên trang tin The Bulwark ngày 12/8. Bà là thành viên cấp cao của nhóm cố vấn Trung tâm Đạo đức và Chính sách công, trụ sở ở Washington D.C.</p>\r\n\r\n<p>Mona Charen chỉ ra rằng, có rất nhiều lý do để lo lắng về&nbsp;<a href=\"https://vietnamnet.vn/trung-quoc-tag60811.html\">Trung Quốc</a>. Với dân số 1,4 tỷ người, một quân đội khổng lồ và nền kinh tế lớn thứ 2 thế giới, Trung Quốc đang bắt đầu chuyển đổi sức mạnh của mình ở khắp nơi. Trung Quốc đang nhanh chóng trở nên giàu có và duy trì một quan điểm cứng rắn. Nước này đang chạy đua về trí tuệ nhân tạo cùng nhiều công nghệ khác, đồng thời xuất khẩu kỹ thuật theo dõi của mình tới nhiều nước.</p>\r\n\r\n<p>Vì vậy, Tổng thống Trump đã đúng khi lo lắng về Trung Quốc. Ông Trump chỉ trích các tổng thống Mỹ trước kia đã đàm phán các thỏa thuận thương mại \"vô cùng yếu kém\", và ông cam kết chính quyền của mình sẽ đảo ngược thâm hụt ngân sách, đưa việc làm khối sản xuất về nước, chặn đứng các hành vi thương mại bất công bằng như bán phá giá, thao túng tiền tệ và đánh cắp tài sản trí tuệ.</p>\r\n\r\n<p>Tất cả tưởng như dễ dàng. Ông nói\" Chúng ta có những tấm thẻ, chúng ta có rất nhiều sức mạnh với Trung Quốc\".</p>\r\n\r\n<p>Nhưng nữ chuyên gia Charen cho rằng, ông Trump khó đạt được mục tiêu nào trong số trên.</p>\r\n\r\n<p>Nếu suy xét những gì ông Trump hy vọng đạt được với Trung Quốc, thì ngay hành động đầu tiên của ông, hủy Hiệp định Đối tác xuyên Thái Bình Dương (TPP), là một sai lầm lớn.</p>\r\n\r\n<p>Nếu ông Trump thực sự muốn đẩy Trung Quốc vào bất lợi, ông sẽ phải dựa vào TPP bằng cách ký thêm thỏa thuận thương mại tự do với các nước trên khắp hành tinh. Nếu Mỹ hợp tác với Liên minh châu Âu (EU), Ấn Độ và Brazil cùng các nước TPP, tất cả có thể mang lại một sức mạnh tổng hợp trên 75% GDP thế giới.</p>\r\n\r\n<p>Nhưng Tổng thống Mỹ thứ 45 cho rằng các cuộc chiến thương mại \"là tốt và dễ chiến thắng\". Và ông tuyên bố những cuộc chiến đó... với tất cả. \"Thuế! Hãy mang thuế về cho tôi\", ông yêu cầu các trợ tá. Và họ đã hành động. Không chỉ với Trung Quốc mà cả với những người bạn của Mỹ. Thực tế, ông Trump đã áp thêm thuế lên Canada, Mexico và EU nhiều hơn so với Trung Quốc.</p>\r\n\r\n<p>Thực tế cho thấy, áp thuế đơn phương không bao giờ là tốt hay dễ dàng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tính đến 2018, Mỹ không còn những tấm thẻ bài mà ông Trump nghĩ nước này vẫn đang nắm giữ. Người Mỹ phải trả giá cao hơn cho mọi hàng hóa, từ xe đạp tới vali hay tivi, mũ nón và đầu vào công nghiệp.</p>\r\n\r\n<p>Và Trung Quốc cũng đáp trả bằng cách áp các mức thuế của họ, đặc biệt nhắm vào nông dân Mỹ.&nbsp;</p>\r\n\r\n<p>Tổng thống&nbsp;<a href=\"https://vietnamnet.vn/donald-trump-tag20457.html\">Trump</a>&nbsp;cam kết,&nbsp;bằng cách trừng phạt Trung Quốc, ông sẽ hồi sinh vùng Trung Tây, nhưng trong khi Thung lũng Silicon chứng kiến sự gia tăng việc làm trong khối sản xuất thì Pennsylvania, Michigan, Ohio, và Wisconsin mất đi nhiều công việc ở nhà máy.</p>\r\n\r\n<p>Còn \"thỏa thuận thương mại vĩ đại\" mà ông Trump đàm phán với Trung Quốc thì sao?</p>\r\n\r\n<p>Thỏa thuận bắt buộc Trung Quốc mua 200 tỷ USD hàng hóa và dịch vụ trong khoảng thời gian 2 năm trên tổng lượng hàng nhập khẩu năm 2017. Nghe thì rất tuyệt, nhưng Trung Quốc vốn thường ít khi thực hiện được đúng các cam kết tương tự.&nbsp;</p>\r\n\r\n<p>Ngay tại buổi lễ của Nhà Trắng thông báo thỏa thuận, Phó Thủ tướng Trung Quốc cũng nói thẳng rằng Bắc Kinh sẽ đưa ra các quyết định mua hàng tương lai \"dựa vào điều kiện thị trường\".</p>\r\n\r\n<p>Sự vươn dậy của Trung Quốc đặt ra một thách thức lớn đối với vai trò lãnh đạo của Mỹ trên toàn thế giới.</p>\r\n\r\n<p>Theo bà Charen,&nbsp;<a href=\"https://vietnamnet.vn/my-tag38291.html\">Mỹ</a>&nbsp;có thể và nên đáp lại bằng cách phát huy thế mạnh của mình: cải thiện các liên minh hiện có, dẫn dắt các tổ chức hàng đầu thế giới như WTO và WHO thay vì rút lui, ủng hộ các quyền tự do dân sự, chào đón những người nhập cư và sinh viên tài năng, những người có thể giúp Mỹ duy trì lợi thế công nghệ, và có được ngôi nhà tài chính của riêng mình để không cần phải vay mượn từ Trung Quốc nữa.</p>', 'H2aX_ong-trump-da-lam-tuong-ve-trung-quoc-360x240.jpg', 0, 0, 21, '2020-08-15', '2020-08-15'),
(105, 'Những điều ít người biết về gia đình Putin', 'nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin', '<p>Khi thông báo Bộ Y tế Nga phê chuẩn vắc-xin Covid-19 đầu tiên trên thế giới hôm 11/8, Tổng thống Vladimir Putin tiết lộ con gái ông đã được tiêm thử. Điều này khiến không ít người tò mò về gia đình ông.</p>', '<p>Là một chính trị gia và cựu sĩ quan KGB, Tổng thống&nbsp;<a href=\"https://vietnamnet.vn/vladimir-putin-tag181900.html\">Putin</a>&nbsp;rất hiếm khi tiết lộ thông tin về gia đình mình. Nổi tiếng toàn thế giới với các quyết sách quân sự và chính trị cứng rắn, ông&nbsp;lại kín tiếng về cuộc sống riêng tư và luôn giữ gia đình tránh xa sự chú ý của truyền thông.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-4.jpg\" title=\"Tổng thống Vladimir và vợ cũ Lyudmila Shkrebneva. Ảnh: Kremlin\"><img alt=\"Những điều ít người biết về gia đình Putin\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-4.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng thống Vladimir và vợ cũ Lyudmila Shkrebneva. Ảnh: Kremlin</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo tờ Thời báo Ấn Độ, ông Putin cưới bà Lyudmila Shkrebneva, từng là một tiếp viên hàng không, vào năm 1983. Họ có hai con gái. Tuy nhiên, cuộc hôn nhân kéo dài 30 năm đã chấm dứt vào năm 2013.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-3.jpg\" title=\"Tổng thống Vladimir Putin, cùng vợ Lyudmila và con gái Maria tới một điểm bầu cử ở Moscow hồi tháng 12/2007. Ảnh: AP\"><img alt=\"Những điều ít người biết về gia đình Putin\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-3.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng thống Vladimir Putin, cùng vợ Lyudmila và con gái Maria tới một điểm bầu cử ở Moscow hồi tháng 12/2007. Ảnh: AP</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Con gái lớn của ông Putin là Maria Vorontsova, có biệt danh là Masha, năm nay 35 tuổi. Cô là một nhà nghiên cứu y khoa, và hiện đang sống cùng chồng người Hà Lan Jorrit Faassen.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-1.jpg\" title=\"Maria Vorontsova. Ảnh: Times of India\"><img alt=\"Những điều ít người biết về gia đình Putin\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-1.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Maria Vorontsova. Ảnh: Times of India</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo Business Insider, vợ chồng Maria có một đứa con. Tổng thống Putin cũng từng nói với nhà làm phim Oliver Stone năm 2017 rằng, mình đã lên chức ông ngoại. Khi Stone hỏi Putin có hay chơi cùng cháu không, nhà lãnh đạo Nga trả lời: \"Hiếm khi\".</p>\r\n\r\n<p>Con gái thứ hai của ông Putin là&nbsp;Katerina Tikhonova, có biệt danh là Katya, theo đuổi nghề khiêu vũ chuyên nghiệp. Năm 2013, cô cưới tỷ phú Nga Kirill Shamalov nhưng cuộc hôn nhân của họ không kéo dài lâu. Cặp đôi li dị vào năm 2018.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-2.jpg\" title=\"Katerina Tikhonova Ảnh: Times of India\"><img alt=\"Những điều ít người biết về gia đình Putin\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-2.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Katerina Tikhonova Ảnh: Times of India</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Hồi tháng 11/2014, trong cuộc phỏng vấn của hãng tin TASS, khi được hỏi hai con gái của ông có đang sống ở Nga không, ông Putin trả lời: \"Cả hai đều ở Moscow. Chúng tôi thỉnh thoảng gặp nhau ở nhà\".</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin.jpg\" title=\"Ảnh: Russian Archives\"><img alt=\"Những điều ít người biết về gia đình Putin\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/15/nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ảnh: Russian Archives</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tổng thống&nbsp;Putin được mô tả là một người rất yêu con. Trong một lần trả lời phỏng vấn, vợ cũ của ông nói: \"Không phải người cha nào cũng yêu con nhiều như ông ấy. Ông ấy luôn chiều chuộng chúng, trong khi tôi lại là người rất kỷ luật\".</p>', 'zzZx_nhung-dieu-it-nguoi-biet-ve-gia-dinh-putin-360x240.jpg', 0, 0, 22, '2020-08-15', '2020-08-15'),
(106, 'Tìm hiểu căn cốt của tiến trình hòa bình Trung Đông', 'tim-hieu-can-cot-cua-tien-trinh-hoa-binh-trung-dong', '<p>Sau hàng thập kỷ đấu tranh giành lại những quyền cơ bản của mình, Palestine đã phải chịu biết bao tổn thất nặng nề do chính sách của Israel.</p>', '<p>3.500 năm trước CN, Palestine là đất của người Canaen thuộc dòng Semite. Năm 2.000 trước CN, người Hebreux, tổ tiên người Do Thái, đến khai phá vùng này. Đến năm 1.200, người Philistines từ đảo Crete đến cư trú và đổi tên thành Palestine. Trước khi bị chia cắt và bị Israel xâm chiếm, vùng đất này có diện tích 27.000km2, phần lớn dân số là người Ảrập, đạo Hồi được coi là quốc giáo.</p>\r\n\r\n<p>Cuối những năm 1910, Đế quốc Anh vốn đang rất hùng mạnh đã giành được quyền uỷ trị vùng đất Jerusalem và Palestine từ tay Thổ Nhĩ Kì. Tháng 1/1919, Anh ra tuyên bố Banfor về việc thiết lập ở Palestine một tổ quốc cho dân tộc Do Thái, đồng thời công nhận Palestine là một thực thể tách rời khỏi Vương quốc Ảrập và người Do Thái có chủ quyền tại đây.</p>\r\n\r\n<p>Ngày 29/11/1947, Liên Hợp quốc (LHQ) thông qua nghị quyết 181 về việc bãi bỏ quyền uỷ trị của Anh và chia vùng đất này thành 2 nhà nước: Nhà nước Do Thái chiếm 58,47% diện tích vùng Palestine (14.100km2) với 498.000 người Do Thái, 325.000 người Ảrập; Nhà nước Ảrập chiếm 43,53% diện tích với 807.000 người Ảrập và 10.000 người Do Thái. Riêng thành phố Jerusalem với 100.000 người Do Thái và 105.000 người Ảrập nằm trong quốc gia Ảrập sẽ được quốc tế hoá.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/07/06/17/van-de-palestine-cot-loi-cua-tien-trinh-hoa-binh-trung-dong.jpeg\" title=\"Chủ tịch PLO Yasser Arafat (phải), Thủ tướng Israel Yitzhak Rabin (trái) và Tổng thống Mỹ Bill Clinton. Ảnh: Reuters\"><img alt=\"Tìm hiểu căn cốt của tiến trình hòa bình Trung Đông\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/06/17/van-de-palestine-cot-loi-cua-tien-trinh-hoa-binh-trung-dong.jpeg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Chủ tịch PLO Yasser Arafat (phải), Thủ tướng Israel Yitzhak Rabin (trái) và Tổng thống Mỹ Bill Clinton. Ảnh: Reuters</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tuy nhiên, ngày 14/5/1948, Hội đồng Dân tộc Do Thái tuyên bố thành lập nhà nước Israel với thủ đô là Jerusalem, bất chấp tinh thần nghị quyết 181. Biểu thị tình đoàn kết với những người anh em Palestine, các nước Ảrập đã phản đối hành động này của Israel. Chính vì vậy, sự nghiệp đấu tranh giành những quyền cơ bản của dân tộc Palestine đã trở thành cuộc xung đột Ảrập – Israel. Vấn đề Palestine đã trở thành cốt lõi của tiến trình hòa bình Trung Đông.</p>\r\n\r\n<p>Đây là cuộc tranh chấp khu vực kéo dài nhất và nhiều mâu thuẫn nhất trong lịch sử thế giới đương đại, với những vụ tiến công đơn lẻ diễn ra hầu như hàng ngày giữa các bên liên quan cùng 6 cuộc chiến tranh, xung đột quy mô lớn đã diễn ra.</p>\r\n\r\n<p>Tháng 5/1964, Hội đồng Dân tộc Palestine (PNC) họp lần thứ nhất ở Đông Jerusalem tuyên bố thành lập Tổ chức Giải phóng Palestine (PLO) do Al Fatah, tổ chức cách mạng đầu tiên của nhân dân Palestine được thành lập năm 1958, làm nòng cốt. Từ đó, PLO trở thành đại diện hợp pháp duy nhất của nhân dân Palestine.</p>\r\n\r\n<p>Năm 1975, tại Khóa họp 30 của Đại Hội đồng LHQ, PLO đã được mời tham gia với tư cách quan sát viên. Ngày 15/11/1988, nhà nước Palestine tuyên bố thành lập, nhưng chưa được LHQ công nhận. Năm 1994, trên cơ sở hiệp định Oslo ký năm 1993 giữa Chủ tịch PLO Yasser Arafat và Thủ tướng Israel Yitzhak Rabin, Chính quyền Dân tộc Palestine (PA) được thành lập để điều hành một số khu vực thuộc Dải Gaza và khu Bờ Tây. Tuy nhiên, chế độ tự quản cho người Palestine tại những vùng đất nói trên chưa bao giờ được thực hiện đầy đủ.</p>\r\n\r\n<p>Cùng với các cuộc vận động ngoại giao, người Palestine đã tiến hành hai cuộc nổi dậy (Intifada) vào năm 1987 và 2000, nhưng không chấm dứt được sự chiếm đóng của Israel và triển vọng thành lập một Nhà nước Palestine ngày càng xa vời. Tháng 9/2010, dưới sự trung gian của Mỹ, các cuộc đàm phán trực tiếp giữa Israel và Palestine được nối lại. Tuy nhiên, sau gần một năm, các cuộc đàm phán đã không thu được kết quả mong đợi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vấn đề ở chỗ, mặc dù đã rút khỏi Dải Gaza (hiện do Phong trào Hồi giáo Hamas kiểm soát), song Israel tiếp tục mở rộng các khu định cư Do Thái ở khu Bờ Tây, và đây là nguyên nhân chính dẫn đến sự đổ vỡ của các cuộc đàm phán trực tiếp giữa Palestine và Israel.</p>\r\n\r\n<p>Thất vọng trước hiện trạng bế tắc này, ngày 23/9/2011, Tổng thống Palestine Mahmoud Abbas đã chính thức trình lên Tổng thư kí LHQ đơn xin gia nhập tổ chức này với quy chế quốc gia thành viên đầy đủ và đề nghị công nhận Palestine là một nhà nước độc lập với đường biên giới trước cuộc chiến tranh năm 1967. Trong khi đợi chờ, ngày 31/10/2011, Tổ chức Văn hóa, Giáo dục và Khoa học của LHQ (UNESCO) đã chính thức phê chuẩn Palestine trở thành thành viên đầy đủ thứ 195.</p>\r\n\r\n<p>Đây là một thắng lợi ngoại giao quan trọng, là niềm khích lệ lớn đối với người Palestine trong nỗ lực đạt mục tiêu thành lập một nhà nước Palestine độc lập. Điều đó có nghĩa là đa số thế giới ủng hộ quyền của người Palestine trở thành một nhà nước độc lập và là thành viên của cộng đồng thế giới.</p>\r\n\r\n<p>Việc chính quyền của Tổng thống Mỹ Donald Trump gần đây chính thức công nhận Jerusalem là thủ đô của Nhà nước Do Thái, cũng như ủng hộ Israel mở rộng các vùng định cư ở Bờ Tây là “đổ thêm dầu vào lửa” và càng làm phức tạp thêm tình hình giải quyết vấn đề Palestine, vốn tồn tại đã hơn 70 năm.</p>\r\n\r\n<p>Sau hàng thập kỷ đấu tranh giành lại những quyền cơ bản của mình, Palestine đã phải chịu biết bao tổn thất nặng nề do chính sách mở rộng các khu định cư của Israel trên các vùng đất của Palestine bị chiếm đóng. Yêu cầu công nhận Palestine là một nhà nước độc lập với Đông Jerusalem là thủ đô, với đường biên giới trước cuộc chiến tranh năm 1967, là nguyện vọng chính đáng của nhân dân Palestine.&nbsp;</p>', 'DJPX_van-de-palestine-cot-loi-cua-tien-trinh-hoa-binh-trung-dong-360x240.jpeg', 0, 0, 23, '2020-08-15', '2020-08-15'),
(107, 'Bất chấp lũ chảy xiết, chó mẹ quên mình nhảy xuống nước cứu con', 'bat-chap-lu-chay-xiet-cho-me-quen-minh-nhay-xuong-nuoc-cuu-con', '<p>Chó mẹ không quản hiểm nguy nhảy xuống nước lũ để cứu đứa con bé bỏng của nó.</p>', '<p>Theo Tân Hoa Xã, một người đàn ông sống tại vùng Mị Sơn thuộc tỉnh Tứ Xuyên, Trung Quốc hôm 12/8 đã cố cứu chó mẹ cùng các đứa con của nó từ mô đất bị nước lũ bao vây tới nơi an toàn.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/bat-chap-lu-chay-cho-me-quen-minh-nhay-xuong-nuoc-cuu-con.jpg\" title=\"Chó mẹ đang cố gắng cứu con nó khỏi dòng lũ. Ảnh: Haokan\"><img alt=\"Bất chấp lũ chảy xiết, chó mẹ quên mình nhảy xuống nước cứu con\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/bat-chap-lu-chay-cho-me-quen-minh-nhay-xuong-nuoc-cuu-con.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Chó mẹ đang cố gắng cứu con nó khỏi dòng lũ. Ảnh: Haokan</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tuy nhiên, khi người đàn ông trên đưa được đàn chó tới chỗ an toàn, thì chó mẹ phát hiện một đứa con của nó bị dòng nước lũ cuốn đi. Lập tức chó mẹ nhảy xuống nước cứu con, rồi cố gắng bơi trở lại vào bờ.</p>\r\n\r\n<p>Sau khi video ghi lại sự việc trên được đăng tải trên mạng xã hội Weibo, nhiều cư dân mạng đã hết lời ca ngợi hành động xả thân cứu con của chó mẹ. “Tình mẫu tử luôn rất vĩ đại, bất kể đó là của người hay động vật”, một người dùng Weibo viết.</p>', 'AkQU_bat-chap-lu-chay-cho-me-quen-minh-nhay-xuong-nuoc-cuu-con-360x240.jpg', 0, 0, 24, '2020-08-15', '2020-08-15'),
(108, 'Đưa 340 công dân từ Mỹ về nước', 'dua-340-cong-dan-tu-my-ve-nuoc', '<p>Các cơ quan chức năng Việt Nam và Hãng hàng không Quốc gia Việt Nam trong hai ngày 10-11/8 đã phối hợp với bên sở tại đưa hơn 340 công dân Việt Nam từ Mỹ về nước an toàn.</p>', '<p>Hành khách trên chuyến bay đến từ nhiều bang ở Mỹ, bao gồm trẻ em dưới 18 tuổi, người cao tuổi, sinh viên đã hoàn thành khoá học gặp khó khăn về nơi ở, không thể gia hạn thị thực ở sở tại.</p>\r\n\r\n<p>Ngoài ra, thành phần đoàn còn một số doanh nhân, trí thức theo yêu cầu công tác trong nước, công dân xuất cảnh ngắn hạn gặp khó khăn về nơi ở, tài chính, và các trường hợp đặc biệt khó khăn khác.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/12/12/340-cong-dan-tu-hoa-ky-ve-nuoc.jpg\" title=\"Nhiều người phải di chuyển từ những nơi ở xa sân bay\"><img alt=\"Đưa 340 công dân từ Mỹ về nước\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/12/12/340-cong-dan-tu-hoa-ky-ve-nuoc.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nhiều người phải di chuyển từ những nơi ở xa sân bay</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đại sứ quán Việt Nam tại&nbsp;Mỹ&nbsp;đã tích cực hỗ trợ công dân, trong đó có nhiều người phải di chuyển từ những nơi ở xa sân bay; đồng thời, cử cán bộ trực tiếp đến sân bay phối hợp với hãng hàng không để hỗ trợ công dân hoàn thành các thủ tục cần thiết trước khi lên máy bay.</p>\r\n\r\n<p>Trong suốt chuyến bay, hành khách và toàn bộ tổ bay chấp hành nghiêm các quy định về phòng, chống dịch bệnh, vệ sinh dịch tễ. Tất cả hành khách cũng đã được cách ly và kiểm tra y tế theo quy định khi máy bay hạ cánh xuống sân bay Vân Đồn (Quảng Ninh).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời gian tới, việc đưa công dân có hoàn cảnh đặc biệt khó khăn về nước sẽ được sắp xếp theo nguyện vọng của công dân, phù hợp với tình hình dịch bệnh và năng lực cách ly trong nước.</p>', 'DdnX_340-cong-dan-tu-hoa-ky-ve-nuoc-360x240.jpg', 0, 0, 25, '2020-08-15', '2020-08-15'),
(109, 'Hiệu trưởng bị cảnh cáo vì nhờ giáo viên thi hộ', 'hieu-truong-bi-canh-cao-vi-nho-giao-vien-thi-ho', '<p>Một hiệu trưởng ở Phú Yên bị kỷ luật cảnh cáo do nhờ người khác thi hộ để hoàn thành chương trình đại học của Đại học Huế.</p>', '<p>UBND Huyện Tây Hòa (Phú Yên) vừa ra quyết định kỷ luật bằng hình thức cảnh cáo đối với ông Huỳnh Cát Tạo, Hiệu trưởng Trường THCS Nguyễn Anh Hào vì vi phạm đạo đức nghề giáo.</p>\r\n\r\n<p>Đồng thời, huyện này cũng có công văn đề nghị ĐH Sư phạm Huế xem xét việc cấp bằng cử nhân cho ông Huỳnh Cát Tạo.&nbsp;</p>\r\n\r\n<p>Theo kết quả xác minh của Phòng GD-ĐT Huyện Tây Hòa, từ năm 2012 - 2014, khi còn là Hiệu phó trường THCS Lê Hoàn, ông Tạo đăng ký học lớp cử nhân Toán hệ đào tạo từ xa của ĐH Sư phạm Huế. Địa điểm học là tại Trung tâm giáo dục thường xuyên Phú Yên.</p>\r\n\r\n<p>Tuy nhiên, trong thời gian theo học, ông Tạo đã nhờ&nbsp;người làm hộ&nbsp;bài kiểm tra môn Phân tích, Logic và Xác suất thống kê.</p>\r\n\r\n<p><img alt=\"Hiệu trưởng bị cảnh cáo vì nhờ giáo viên thi hộ\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/hieu-truong-bi-canh-cao-vi-nho-giao-vien-thi-ho-lay-bang-dai-hoc.jpg\" /></p>\r\n\r\n<p>Trường Trung học Cơ sở Nguyễn Anh Hào (Phú Yên)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong tường trình, ông Huỳnh Cát Tạo cho biết, trong khoảng thời gian đi học, ông tham gia học tập đầy đủ, tự học, tự làm bài kiểm tra, không nhờ bất kỳ ai giúp đỡ hoặc làm bài kiểm tra hộ. Đây chỉ là trường hợp bất khả kháng.</p>\r\n\r\n<p>Nguyên do là hè năm 2013, ông Tạo gặp tai nạn và bị gãy xương đòn phải. Khi vừa xuất viện thì lớp có bài kiểm tra nên đã nhờ một đồng nghiệp làm giúp.</p>\r\n\r\n<p>Sau khi lấy bằng đại học, ông Tạo được bổ nhiệm làm Hiệu trưởng trường THCS Lê Hoàn, rồi chuyển sang làm Hiệu trưởng trường THCS Nguyễn Anh Hào.</p>', 'z6Rm_hieu-truong-bi-canh-cao-vi-nho-giao-vien-thi-ho-lay-bang-dai-hoc-360x240.jpg', 0, 0, 28, '2020-08-15', '2020-08-15'),
(110, 'Chàng trai xứ Nghệ bỏ ngang đại học rẽ sang học nghề', 'chang-trai-xu-nghe-bo-ngang-dai-hoc-re-sang-hoc-nghe', '<p>Đại sứ Kỹ năng nghề Nguyễn Văn Thiết cho rằng, mọi sự lựa chọn có thể không hoàn hảo nhưng mỗi người sẽ biết bản thân mình thích gì và làm được gì.&nbsp;Chỉ khi học thứ mình thích, mình mới đam mê và dành thời gian cho nó.</p>', '<p>Nhiều người thường quan niệm chỉ có đại học mời là cánh cửa mở ra tương lai tươi sáng. Thế nhưng chàng trai Nguyễn Văn Thiết (SN 1995 - Nghệ An)&nbsp;dường như đã có một lựa chọn khá \"ngược\" khi quyết định từ bỏ chương trình đại học để học nghề.</p>\r\n\r\n<p>Quyết định này của anh từng vấp phải sự hoài nghi của không ít người. Tuy nhiên, anh đã khiến mọi người phải ngỡ ngàng khi giành huy chương vàng kỳ thi Tay&nbsp;nghề ASEAN rồi được nhận bằng khen của Thủ tướng Chính phủ.</p>\r\n\r\n<p>Sau khi ra trường, Thiết được tập đoàn lớn mời về công tác. Mới đây anh được Tổng cục Giáo dục nghề nghiệp lựa chọn trở thành 1 trong 10 Đại sứ Kỹ năng nghề. Từ trải nghiệm thực tế của mình, anh tự tin đưa ra lời khuyên: “Tôi nghĩ, các bạn trẻ đừng ngại khi chọn cho mình con đường học nghề”.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/08/21/dddd.jpg\" title=\"Nguyễn Văn Thiết (đứng giữa) trong lễ vinh danh Gương mặt trẻ Thủ đô tiêu biểu năm 2016\"><img alt=\"Chàng trai xứ Nghệ bỏ ngang đại học rẽ sang học nghề\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/08/21/dddd.jpg\" style=\"height:490px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Nguyễn Văn Thiết (đứng giữa) trong lễ vinh danh Gương mặt trẻ Thủ đô tiêu biểu năm 2016</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>“Tôi từng nghĩ học nghề là kém sang”</p>\r\n\r\n<p>Quê Thiết ở xã nghèo Nghi Công Nam (Nghi Lộc, Nghệ An). Chàng trai 9X lớn lên với nhiều hoài bão. Năm 2013, Thiết tốt nghiệp THPT và đỗ nguyện vọng 2 một trường đại học ở Vinh. Sau khi theo học một thời gian, cảm thấy ngành học không đúng mong ước của mình, anh đã&nbsp; rút hồ sơ, xin đi làm.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/09/14/cac-ban-tre-dung-ngai-khi-chon-cho-minh-con-duong-hoc-nghe.jpg\" title=\"Ý chí kiên cường của chàng trai 9X được rèn luyện qua gian khổ\"><img alt=\"Chàng trai xứ Nghệ bỏ ngang đại học rẽ sang học nghề\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/09/14/cac-ban-tre-dung-ngai-khi-chon-cho-minh-con-duong-hoc-nghe.jpg\" style=\"height:541px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ý chí kiên cường của chàng trai 9X được rèn luyện qua gian khổ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>“Mọi người cho rằng quyết định của tôi là hồ đồ nhưng tôi thấy, hàng năm, tỷ lệ sinh viên tốt nghiệp đại học không kiếm được việc làm khá cao, vì họ học chuyên ngành không đúng với năng lực, sở thích của mình. Chỉ khi học thứ mình thích, mình mới đam mê và dành thời gian cho nó”, Thiết tâm sự.</p>\r\n\r\n<p>Anh cho rằng, mọi sự lựa chọn có thể không hoàn hảo nhưng mỗi người sẽ biết bản thân mình thích gì và làm được gì.</p>\r\n\r\n<p>Bên cạnh đó, lý do Thiết rời giảng đường còn vì hoàn cảnh gia đình. Anh kể, bố mẹ sinh được 3 người con, chị gái anh phải nghỉ học từ năm lớp 10, vào miền Nam kiếm tiền phụ giúp bố mẹ, anh trai cũng học nghề ngoài Hà Nội.</p>\r\n\r\n<p>Sau khi nghỉ học, Thiết bắt đầu đi làm thuê, dự định kiếm tiền, ôn thi thêm 1 năm nữa. “Tôi muốn thi bằng được vào ngôi trường đại học mình mơ ước”.</p>\r\n\r\n<p>Nỗi lo cơm áo và tháng ngày trầy trật với nắng gió mưu sinh, chàng trai xứ Nghệ thấm thía hơn bao giờ hết sự vất vả của công việc chân tay. Thời gian anh làm bốc vác gạo, mỗi tháng kiếm được 3 triệu đồng, mồ hôi chưa ráo, đã hết tiền. “Tôi nhận ra, muốn giàu phải bán chất xám, chẳng ai bán sức khỏe, sức lực”, Thiết nhớ lại.</p>\r\n\r\n<p>Cũng vào lúc đó, anh trai Thiết, Nguyễn Văn Long báo tin đã giành giải Nhất thi nghề quốc gia và được chọn vào đội tuyển đại diện Việt Nam tham gia kỳ thi Tay nghề ASEAN lần thứ 10 (2014). Tại cuộc thi này, Long xuất sắc giành huy chương vàng, vượt qua nhiều đối thủ đến từ các quốc gia trong khu vực.</p>\r\n\r\n<p>Khi ấy Long đang học khóa 2, học nghề lắp đặt và điều khiển điện tại trường Cao đẳng nghề Công nghệ cao Hà Nội. Chứng kiến những gì anh trai đạt được, Thiết nộp hồ sơ xin vào trường anh trai học.</p>\r\n\r\n<p>“Người ta hỏi tôi, tại sao đang học đại học lại bỏ đi học nghề? Như vậy có buồn không? Thực sự, tôi cũng buồn. Vì ngày đi học cũng thuộc diện học giỏi, có năng lực. Bạn bè học đại học về làng ai cũng nức nở khen\", Thiết tâm sự.</p>\r\n\r\n<p>Anh cũng chia sẻ, thời gian đầu anh còn suy nghĩ học nghề là kém sang, ai hỏi cũng xấu hổ không nói. Thế nhưng, nhìn vào thành tựu anh trai đạt được, Thiết nuôi ước mơ giành tấm huy chương Vàng Tay nghề ASEAN giống anh trai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chuỗi ngày học xa nhà, Thiết ở trong kí túc xá. Anh nhớ lại, bữa chính là mì gói, thi thoảng mới có miếng thịt, quả trứng. Anh đặt mục tiêu tiết kiệm, mỗi tháng chỉ chi tiêu 700 nghìn đồng cho ăn uống. Mùa đông năm 2015, thời tiết khắc nghiệt, Thiết nằm trên chiếc giường tầng với manh chiếu cũ kỹ, anh mặc nhiều lớp quần áo chống rét để ngủ. Vì nếu bỏ tiền ra mua chăn, sẽ lạm vào tiền ăn.</p>\r\n\r\n<p>Ý chí kiên cường được tôi rèn qua những nhọc nhằn đã khiến Thiết ngày càng bản lĩnh, mạnh mẽ. Anh từng bước chinh phục đỉnh cao khi giành được chiến thắng tại các cuộc thi nghề trong và ngoài nước.</p>\r\n\r\n<p>Hiện Thiết có công việc với mức lương cao, cuộc sống dư dả. Đầu năm 2017, Thiết và anh trai đã dành dụm được khoản tiền xây dựng cho bố mẹ căn nhà khang trang ở quê.&nbsp;&nbsp;</p>\r\n\r\n<p>Vòng nguyệt quế cho người chiến thắng</p>\r\n\r\n<p>Mặc dù muộn gần 1 học kỳ, nhưng Thiết học đuổi kịp các bạn rất nhanh. Chàng sinh viên trẻ \"đánh liều\" đăng ký cuộc thi Tay nghề Quốc gia. Những buổi ôn luyện bên máy móc, mạch điện và con số khiến Thiết hoa mắt. Nhưng anh không chịu lùi bước, hỏng thì làm lại.</p>\r\n\r\n<p>“Càng dành thời gian cho việc nghiên cứu, tôi càng thấy sức hấp dẫn của ngành nghề mình theo đuổi. Các ý tưởng nối tiếp nhau xuất hiện. Nhiều lần ở trong phòng thực hành cả ngày đến quên ăn, quên nghỉ”, chàng trai 9X chia sẻ.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/09/14/cac-ban-tre-dung-ngai-khi-chon-cho-minh-con-duong-hoc-nghe-1.jpg\" title=\"Nguyễn Văn Thiết đã giành được huy chương vàng kỳ thi Tay nghề ASEAN\"><img alt=\"Chàng trai xứ Nghệ bỏ ngang đại học rẽ sang học nghề\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/09/14/cac-ban-tre-dung-ngai-khi-chon-cho-minh-con-duong-hoc-nghe-1.jpg\" style=\"height:578px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Văn Thiết đã giành được huy chương vàng kỳ thi Tay nghề ASEAN</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ban đầu Thiết lắp ráp một thiết bị hết 4 tiếng, sau rút ngắn còn 2 tiếng, rồi 45 phút và đến lúc bước vào kỳ thi Quốc gia, Thiết chỉ lắp trong vòng 25 phút. Giành giải nhất Quốc gia, Thiết lên đường sang Malaysia thi tài ở nhóm nghề Tự động hóa công nghiệp tại kỳ thi Tay nghề ASEAN lần thứ 11 (năm 2016).</p>\r\n\r\n<p>Ngày thi đầu tiên, Thiết và đồng đội phải đứng liên tục nhiều tiếng đồng hồ, mồ hôi túa ra mờ hết chiếc kính bảo hộ. Kết quả, đoàn Việt Nam hoàn thành đầu tiên trong 2 tiếng 45 phút, trong khi thời gian BTC quy định là 5 tiếng.</p>\r\n\r\n<p>Ngày thứ 2, hai chiếc cầu chì bị thiếu ruột nên đoàn Việt Nam về thứ hai sau 4 tiếng 53 phút, cách đoàn chủ nhà Malaysia 1 phút.</p>\r\n\r\n<p>Ngày thứ 3, với đề bài bằng tiếng Anh, sau 7 tiếng lập trình, đoàn Việt Nam vượt lên dẫn đầu. Cuộc thi năm đó, Thiết và đồng đội đã xuất sắc giành&nbsp; huy chương vàng quý giá.</p>\r\n\r\n<p>Con đường học nghề đã giúp Thiết gặt hái thành công, vinh dự trở thành một trong 10 gương mặt trẻ Thủ đô tiêu biểu năm 2016.</p>\r\n\r\n<p>Suốt thời gian học nghề, Thiết được nhận học bổng của trường. Bố mẹ ở quê không phải chu cấp. Ngay từ năm cuối tại trường cao đẳng, đã có nhà tuyển dụng đến đặt vấn đề mời Thiết về làm việc sau khi ra trường. Anh quyết định vừa làm vừa học để nâng cao thêm kiến thức, tay nghề đáp ứng nhu cầu của doanh nghiệp.</p>\r\n\r\n<p>Trong vai trò một Đại sứ Kỹ năng nghề, anh chia sẻ: “Các đại sứ nghề cần giúp các bậc phụ huynh và bạn trẻ hiểu đúng về giáo dục nghề nghiệp. Khi bạn trẻ đã có nghề tốt, thì sẽ có việc tốt và tương lai tốt. Nghề nào cũng vậy, kiến thức có mà không trau dồi, luyện tập sẽ không thể phát huy. Bởi vậy, mỗi người phải học hỏi, bồi dưỡng kiến thức không ngừng nghỉ\".</p>', 'TSfR_dddd-360x240.jpg', 0, 0, 29, '2020-08-15', '2020-08-15'),
(111, 'Sinh viên quốc tế năm nhất không được đến Mỹ nếu học trực tuyến hoàn toàn', 'sinh-vien-quoc-te-nam-nhat-khong-duoc-den-my-neu-hoc-truc-tuyen-hoan-toan', '<p>Sinh viên quốc tế năm nhất – bao gồm bậc đại học lẫn cao học và tương tự - sẽ không được phép đến Hoa Kỳ vào mùa thu này nếu trường học của họ chỉ cung cấp các lớp học trực tuyến.</p>', '<p>Đây là quy định trong hướng dẫn mà chính quyền của Tổng thống Donald Trump ban hành hôm nay.</p>\r\n\r\n<p>Tuy nhiên, theo hướng dẫn này, sinh viên quốc tế đã hoàn thành kì học mùa xuân và đã có thị thực có thể ở lại Mỹ hoặc quay trở lại Mỹ sau kỳ nghỉ hè, ngay cả khi trường đại học của họ chỉ cung cấp các lớp học trực tuyến.</p>\r\n\r\n<p>Các quy tắc áp dụng cho \"sinh viên xin visa ở trạng thái mới hoặc lần đầu xin thị thực sau ngày 9 tháng 3\" theo Cơ quan Thực thi Di trú và Hải quan. Những sinh viên này \"sẽ không được phép nhập cảnh vào Mỹ để theo học một chương trình trực tuyến 100%\".</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/07/25/12/sinh-vien-quoc-te-nam-nhat-khong-duoc-den-my-neu-hoc-truc-tuyen-hoan-toan.jpg\" title=\"Sinh viên quốc tế năm nhất không được đến Mỹ nếu học trực tuyến hoàn toàn\"><img alt=\"Sinh viên quốc tế năm nhất không được đến Mỹ nếu học trực tuyến hoàn toàn\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/25/12/sinh-vien-quoc-te-nam-nhat-khong-duoc-den-my-neu-hoc-truc-tuyen-hoan-toan.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sinh viên quốc tế năm nhất không được đến Mỹ nếu học trực tuyến hoàn toàn</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngoài ra, các viên chức nhà trường được chỉ định không cấp Mẫu đơn I-20 cho sinh viên năm nhất trong quá trình xin cấp thị thực mà dự định tham gia các lớp học hoàn toàn trực tuyến tại các cơ sở giáo dục ở Mỹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chính quyền Trump đã từ bỏ các quy định về visa sinh viên đưa ra vào đầu tháng 7 sau hàng loạt vụ kiện và phản đối dữ dội bởi hơn 200 trường đại học và 17 tiểu bang.</p>\r\n\r\n<p>Điều này cho phép các sinh viên khoá trên có thể tiếp tục ở lại Mỹ hay quay lại Mỹ kể cả khi các lớp học của họ hoàn toàn online.</p>\r\n\r\n<p>Tuy nhiên, sinh viên quốc tế năm nhất nếu muốn học tại Mỹ sẽ cần phải đăng ký vào một cơ sở giáo dục cung cấp ít nhất một số lớp học trực tiếp. Các sinh viên, cả năm nhất và năm trên, có thể ở lại Mỹ nếu trường đại học của họ chuyển sang chỉ giảng dạy trực tuyến vào giữa học kỳ.</p>', 'v8Rf_truong-dai-hoc-my-doi-phuong-an-de-bao-ve-sinh-vien-quoc-te-360x240.jpg', 0, 0, 30, '2020-08-15', '2020-08-15'),
(112, 'Bước ngoặt bất ngờ của nữ sinh thủ khoa trường Kinh tế', 'buoc-ngoat-bat-ngo-cua-nu-sinh-thu-khoa-truong-kinh-te', '<p>Đạt điểm trung bình học tập 3,92/4 (tức quy đổi 9,13/10), Trần Diệu Hương trở thành thủ khoa đầu ra của ngành Tài chính doanh nghiệp và cả Trường ĐH Kinh tế quốc dân năm 2020 trong đợt tốt nghiệp sớm.</p>', '<p>Trong bảng điểm của cô bạn ở&nbsp;Thanh Trì (Hà Nội) có tới 40/45 học phần đạt điểm A.</p>\r\n\r\n<p>\"Đến giờ em vẫn cảm thấy rất vui, có chút lâng lâng khó tả. Em vui vì 4 năm học đại học đã để lại được chút dấu ấn”, Hương chia sẻ.</p>\r\n\r\n<p>Ban đầu, mục tiêu thiết thực mà&nbsp;cô gái&nbsp;sinh năm 1998 đặt ra chỉ là đạt học bổng từng kỳ để đỡ được phần nào học phí. Trong suốt 7 kỳ học, Hương đều giành được học bổng của trường và một số học bổng của doanh nghiệp.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan.jpg\" title=\"Trần Diệu Hương trở thành thủ khoa đầu ra của ngành Tài chính doanh nghiệp và cả Trường ĐH Kinh tế quốc dân năm 2020 trong đợt tốt nghiệp sớm.\"><img alt=\"Bước ngoặt bất ngờ của nữ sinh thủ khoa trường Kinh tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trần Diệu Hương trở thành thủ khoa đầu ra của ngành Tài chính doanh nghiệp và cả Trường ĐH Kinh tế quốc dân năm 2020 trong đợt tốt nghiệp sớm.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>\"Hãy yêu lấy cái nghề đang chọn mình\"</p>\r\n\r\n<p>Song, thực ra Hương đến với ngành học này sau khi lỡ hẹn với chuyên ngành yêu thích là Kế toán – Kiểm toán. Ở kỳ thi THPT quốc gia năm 2016, Hương được tổng 25 điểm tổ hợp khối A00 (Toán, Lý, Hóa), tính cả điểm ưu tiên khu vực là 25,5 điểm.</p>\r\n\r\n<p>Điểm chuẩn của ngành Kế toán -&nbsp; Kiểm toán là 25,5 điểm song lại có thêm tiêu chí phụ là điểm môn Toán phải từ 8,25. Trong khi đó, Hương chỉ đạt 8 điểm môn Toán.</p>\r\n\r\n<p>Cuối cùng,&nbsp;em quyết định chọn đăng ký vào ngành Tài chính doanh nghiệp.</p>\r\n\r\n<p>“Khi biết mình không đỗ được vào Kế toán – Kiểm toán, em đã rất buồn. Nhưng một cô giáo của em đã khuyên rằng, giống như việc đi làm, nếu nghề nào đó không chọn mình thì hãy yêu lấy cái nghề đang chọn mình. Từ câu nói đó em bắt đầu suy nghĩ mình sẽ học ngành Tài chính doanh nghiệp mà vẫn có thể tìm hiểu được ngành Kế toán- Kiểm toán nếu thích”.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan-1.jpg\" title=\"\"><img alt=\"Bước ngoặt bất ngờ của nữ sinh thủ khoa trường Kinh tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan-1.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vào học, vì vẫn mê Kế toán - Kiểm toán, Hương tham gia câu lạc bộ của ngành này. Cũng nhờ đó, Hương có cơ hội gặp một người anh là cựu sinh viên Kế toán - Kiểm toán, hiện làm việc trong ngành Tư vấn tài chính.</p>\r\n\r\n<p>“Qua định hướng của anh, em mới hiểu chuyên ngành mình học cũng rất hay và tại sao không thử tìm hiểu, thay vì ra trường đi làm trái ngành thì uổng phí. Sau đó, em cũng tham gia mấy cuộc thi và có cơ hội gặp gỡ nhiều người, được truyền cảm hứng và rồi ngày càng yêu ngành mình đang theo học hơn”.</p>\r\n\r\n<p>Cũng từ đó, nữ sinh dần hứng thú với định giá doanh nghiệp, dòng tiền – những kiến thức thuộc chuyên ngành Tài chính doanh nghiệp.</p>\r\n\r\n<p>“Tưởng tượng về ngành học qua tên gọi là một chuyện còn vào học cụ thể là một chuyện khác. Em nghĩ rằng nếu mình nỗ lực và tập trung hết sức cho ngành học thì ngành học nào cũng có điểm hay và đều có cơ hội để cho mình phát triển”.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Không ngại hỏi, không giấu dốt</p>\r\n\r\n<p>Để đạt hầu hết điểm A, Hương cho hay không có bí quyết gì đặc biệt, chỉ là không ngại hỏi thật nhiều, không giấu dốt. Trước mỗi kỳ học hay trước khi đăng ký các môn học, Hương thường hỏi kinh nghiệm của các anh chị sinh viên khóa trên về việc nên đăng ký môn nào, nên học ra sao. Trong quá trình học, Hương cũng mạnh dạn hỏi các thầy cô các vấn đề bản thân còn lúng túng.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan-3.jpg\" title=\"\"><img alt=\"Bước ngoặt bất ngờ của nữ sinh thủ khoa trường Kinh tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/14/nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan-3.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Hương cho rằng kiến thức ở bậc đại học không nhiều như kiến thức ở bậc THPT, do đó chỉ cần tập trung một chút thì cũng có thể đạt được kết quả khá tốt.</p>\r\n\r\n<p>“Điều này tùy thuộc vào sự lựa chọn riêng của mỗi người. Em nghĩ mỗi người có định hướng khác nhau, người giỏi chưa chắc đã điểm cao vì dành nhiều thời gian hoạt động ở ngoài. Em muốn dành sự chú tâm cho việc học kiến thức chuyên ngành, tham gia câu lạc bộ để trải nghiệm, xây dựng mạng lưới, còn có thể các bạn khác sẽ tranh thủ đi làm thêm, gia sư,...”, Hương chia sẻ.</p>\r\n\r\n<p>Tính toán nên học tổ hợp môn nào để tìm sự liên quan, bổ trợ tốt nhất cho nhau về kiến thức cũng là một cách theo Hương có thể giúp việc học đỡ vất vả hơn.</p>\r\n\r\n<p>Từ năm thứ 3, Hương còn ôn luyện CFA (Chartered Financial Analyst) - chứng chỉ dành riêng cho các nhà phân tích tài chính chuyên nghiệp trong các lĩnh vực chứng khoán, đầu tư, quản lý rủi ro, ngân hàng và tài chính. Hiện em đã đạt level 1 và chuẩn bị hoàn thành level 2.</p>\r\n\r\n<p>Xác định phải tự lực trong xin việc và tinh thần luôn chủ động “không có cơm thì ăn cháo”, Hương đặt mục tiêu ra trường sớm để có lợi thế về việc làm, bởi tỷ lệ cạnh tranh xin việc sẽ thấp hơn so với thời điểm sinh viên đồng loạt tốt nghiệp.</p>\r\n\r\n<p>Hương có khả năng “gánh tải” đáng nể khi mỗi kỳ học đăng ký 22-25 tín chỉ, qua đó giúp em tốt nghiệp cử nhân Tài chính doanh nghiệp sớm, chỉ trong vòng 3,5 năm.</p>\r\n\r\n<p>Giữa tháng 6 năm nay, Hương đã nộp hồ sơ và trúng tuyển vào một ngân hàng.</p>\r\n\r\n<p>Nhìn lại sau 4 năm, Hương cho rằng việc không trúng tuyển vào được ngành học mà mình kỳ vọng nhất thực tế không phải là điều gì đó quá tồi tệ, ghê gớm.</p>\r\n\r\n<p>“Em cảm thấy may mắn vì đã nhận ra giá trị ngành học, từ đó thấy yêu thích và có động lực cố gắng. Các em khóa tới hoặc có thể thi lại để hiện thực hóa kỳ vọng của mình nhưng cũng có thể thử tìm hiểu xem về ngành học mà mình năm nay trúng tuyển và biết đâu lại phù hợp. Cách hay nhất là tìm đến những người đi trước của ngành học đó để đưa ra cho mình những lời khuyên, thông tin tham khảo”, Hương nói.</p>', 'rhQ3_nham-mat-dua-chan-vao-nganh-hoc-trai-ky-vong-nu-sinh-tro-thanh-thu-khoa-dau-ra-dh-kinh-te-quoc-dan-360x240.jpg', 0, 0, 31, '2020-08-15', '2020-08-15'),
(113, 'Hải Dương cho học sinh nghỉ học từ 14/8', 'hai-duong-cho-hoc-sinh-nghi-hoc-tu-14-8', '<p>Sau khi xuất hiện các ca nhiễm Covid-19, tỉnh Hải Dương quyết định cho học sinh nghỉ học từ hôm nay (14/8) đến khi có thông báo đi học lại để đảm bảo an toàn.</p>', '<p>UBND tỉnh Hải Dương đã yêu cầu&nbsp;tạm dừng tổ chức các hoạt động giáo dục để thực hiện các biện pháp cấp bách phòng chống Covid-19.</p>\r\n\r\n<p>Theo đó, tạm dừng các hoạt động chăm sóc, giáo dục trẻ tại các trường mầm non, các nhóm trẻ, lớp mẫu giáo độc lập tư thục.</p>\r\n\r\n<p><img alt=\"Hải Dương cho học sinh nghỉ học từ 14/8\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/18/hoc-sinh-sinh-vien-hai-duong-nghi-hoc-tu-14-8-vi-covid-19.jpg\" /></p>\r\n\r\n<p>Học sinh, sinh viên Hải Dương nghỉ học từ hôm nay</p>\r\n\r\n<p>UBND tỉnh cũng yêu cầu không tổ chức các hoạt động dạy học trong hè đối với các trường phổ thông, trung tâm giáo dục nghề nghiệp, giáo dục thường xuyên, trung tâm ngoại ngữ, tin học, kỹ năng sống, các lớp học năng khiếu....</p>\r\n\r\n<p>Các cơ sở giáo dục phải thực hiện&nbsp;toàn bộ&nbsp;biện pháp cấp bách để&nbsp;phòng chống dịch Covid-19; rà soát toàn bộ giáo viên, học sinh, sinh viên và người lao động của đơn vị tiếp xúc với những người đã được công bố nhiễm SARS-CoV-2 để yêu cầu nghiêm túc thực hiện cách ly theo hướng dẫn của cơ quan y tế ở địa phương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngoài ra, đối với các cơ sở giáo dục nghề nghiệp, căn cứ tình hình thực tiễn, UBND tỉnh Hải Dương chỉ đạo các cơ sở chủ động quyết định cho học sinh, sinh viên và học viên nghỉ học hay đi học trở lại hoặc có hình thức phù hợp với yêu cầu thực tế.&nbsp;</p>\r\n\r\n<p>Các cơ sở giáo dục nghề nghiệp cũng được yêu cầu phải thực hiện tuyển sinh, đào tạo trong tình hình dịch bệnh&nbsp;Covid-19 bùng phát trở lại theo hướng dẫn của Tổng cục Giáo dục nghề nghiệp. Người đứng đầu các cơ sở giáo dục nghề nghiệp chịu trách nhiệm về công tác phòng, chống dịch&nbsp;Covid-19 của đơn vị.</p>\r\n\r\n<p>Hôm nay (14/8) cũng là ngày đầu tiên Hải Dương&nbsp;thực hiện cách ly xã hội sau khi có kết luận về các ca dương tính với Covid-19.</p>', 'SvmS_hoc-sinh-sinh-vien-hai-duong-nghi-hoc-tu-14-8-vi-covid-19-360x240.jpg', 0, 0, 32, '2020-08-15', '2020-08-15'),
(114, 'Ba điều tối kỵ trong hôn nhân, vợ chồng nào cũng phải biết', 'ba-dieu-toi-ky-trong-hon-nhan-vo-chong-nao-cung-phai-biet', '<p>Không tin tưởng, kiểm soát nửa kia quá mức là hai trong&nbsp;những&nbsp;điều tối kỵ mà bạn cần tránh để giữ cuộc hôn nhân được bền lâu, hạnh phúc.</p>', '<p>1. Kiểm soát quá mức</p>\r\n\r\n<p>Đàn ông giống như một đứa trẻ nổi loạn, bạn càng ngăn cản anh ta thì anh ta càng muốn làm. Bạn có thể kiểm soát ví của anh ấy, kiểm soát lời nói và việc làm của anh ấy cũng như hiểu rõ mọi hành động của anh ấy nhưng không phải lúc nào bạn cũng có thể gọi điện hỏi anh ấy đang ở đâu, làm gì?</p>\r\n\r\n<p>Anh ấy có công việc riêng. Có thể anh ấy đang bận làm nên không nghe điện thoại. Muốn biết anh ta đã làm gì trong thời gian đó, bạn có thể đợi đến khi tan sở. Anh trở về nhà và kiên nhẫn hỏi.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/21/ba-dieu-toi-ky-trong-hon-nhan-cap-doi-nao-cung-phai-biet.jpeg\" title=\"\"><img alt=\"Ba điều tối kỵ trong hôn nhân, vợ chồng nào cũng phải biết\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/21/ba-dieu-toi-ky-trong-hon-nhan-cap-doi-nao-cung-phai-biet.jpeg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>2. Không tin nửa kia</p>\r\n\r\n<p>Điều quan trọng nhất đối với các đối tác làm ăn là sự tin tưởng lẫn nhau, chỉ khi họ tin tưởng thì mới có thể hợp tác. Tin tưởng cũng là điểm quan trọng nhất để hòa hợp trong hôn nhân. Đừng lúc nào cũng đặt câu hỏi bằng giọng điệu thắc mắc. Suy cho cùng, sau một ngày làm việc mệt mỏi, ai cũng kiệt quệ cả về thể chất lẫn tinh thần, mong khi trở về nhà sẽ nhận được lời động viên từ&nbsp;nửa kia&nbsp;và tách trà nóng thay cho vô vàn nghi ngờ, trách móc.</p>\r\n\r\n<p>Nếu việc nghi ngờ thường xuyên diễn ra thì theo thời gian, sẽ có khoảng cách giữa hai bạn, cho đến khi cả hai không thể vượt qua được nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3. Không trân trọng nửa kia</p>\r\n\r\n<p>Là đàn ông, bạn phải hiểu rõ người bạn đời đã làm gì cho mình, cô ấy không chỉ bận bịu với việc kiếm tiền mà ngày nào về nhà cũng phải giặt giũ, nấu nướng, chăm sóc con cái … Nếu bạn không biết ơn, không trân trọng cô ấy hơn mà đối xử với cô ấy bằng thái độ tồi tệ, trái tim cô ấy sẽ nguội lạnh theo thời gian.</p>\r\n\r\n<p>Là phụ nữ, chúng ta cũng phải biết rằng gánh nặng trên vai đàn ông rất nặng nề, họ phải chạy vạy lo toan cho gia đình và nhìn mặt người khác chỉ để đàm phán công việc và làm cho cuộc sống của vợ con tốt hơn.</p>\r\n\r\n<p>Bạn nên chăm sóc tổ ấm của mình thật tốt, để họ không phải lo lắng, là chỗ dựa vững chắc cho họ.</p>', 'nh5v_ba-dieu-toi-ky-trong-hon-nhan-cap-doi-nao-cung-phai-biet-360x240.jpeg', 0, 0, 35, '2020-08-15', '2020-08-15'),
(115, 'Khởi tố nhóm thanh niên kề dao vào cổ tài xế taxi cướp tài sản', 'khoi-to-nhom-thanh-nien-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san', '<p>Do thiếu tiền tiêu xài, 3 thanh niên ở Bình Dương lên kế hoạch thuê taxi chở đến đoạn đường vắng rồi dùng dao khống chế tài xế cướp tài sản.</p>', '<p>Cơ quan CSĐT Công an TP Thủ Dầu Một, tỉnh Bình Dương vừa ra quyết định khởi tố vụ án, khởi tố bị can, thực hiện lệnh bắt tạm giam 3 tháng đối với Phan Thanh Tuấn (SN 1999, quê Cần Thơ), Hoàng Gia Lâm (SN 1999, quê Đắk Lắk) để điều tra về hành vi cướp tài sản.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/khoi-to-nhom-thanh-nien-tao-ton-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san.jpg\" title=\"Phan Thanh Tuấn tại cơ quan công an\"><img alt=\"Khởi tố nhóm thanh niên kề dao vào cổ tài xế taxi cướp tài sản\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/khoi-to-nhom-thanh-nien-tao-ton-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Phan Thanh Tuấn tại cơ quan công an</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngoài ra, cơ quan Công an cũng đang truy bắt đối tượng Đoàn Quốc Vinh (SN 2000, ngụ TP.HCM) cùng tham gia nhóm cướp này.</p>\r\n\r\n<p>Theo điều tra, do thiếu tiền tiêu xài nên Tuấn rủ Lâm và Vinh thuê xe taxi chở đến khu vực vắng để ra tay cướp tài sản.</p>\r\n\r\n<p>Tối ngày 3/8, nhóm này gọi xe taxi Grap do anh Nguyễn Hồng Hà (SN 1984, ngụ Bình Dương) làm tài xế đến khu vực chợ Đông Đô (phường An Phú, TP Thuận An) để chở về TP Thủ Dầu Một.</p>\r\n\r\n<p>Khi đến đoạn đường vắng ở phường Phú Mỹ, TP Thủ Dầu Một, Tuấn bất ngờ rút con dao tự chế kề vào cổ anh Hà uy hiếp, yêu cầu anh này đưa toàn bộ tiền và điện thoại di động.</p>\r\n\r\n<p>Sau khi cướp được hơn 3 triệu đồng cùng điện thoại, nhóm này mở cửa xe chạy bộ tẩu thoát.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/khoi-to-nhom-thanh-nien-tao-ton-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san-1.jpg\" title=\"Đối tượng Hoàng Gia Lâm (trái) và Đoàn Quốc Vinh\"><img alt=\"Khởi tố nhóm thanh niên kề dao vào cổ tài xế taxi cướp tài sản\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/khoi-to-nhom-thanh-nien-tao-ton-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san-1.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đối tượng Hoàng Gia Lâm (trái) và Đoàn Quốc Vinh</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Qua trình báo của tài xế, Công an phường Phú Mỹ, Phú Tân phối hợp với Công an TP Thủ Dầu Một tổ chức truy bắt các đối tượng.</p>\r\n\r\n<p>Đến khoảng 3h sáng, Công an đã bắt giữ được Tuấn khi đang đi bộ trong khu công nghiệp ở phường Phú Tân cùng tang vật gây án</p>\r\n\r\n<p>Ngày 6/8, đối tượng Hoàng Gia Lâm đã đến trụ sở Công an để đầu thú. Riêng Vinh vẫn đang bỏ trốn, bị cơ quan công an truy bắt.</p>', 'wwIk_khoi-to-nhom-thanh-nien-tao-ton-ke-dao-vao-co-tai-xe-taxi-cuop-tai-san-2-360x240.jpg', 0, 0, 42, '2020-08-15', '2020-08-15');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(116, 'Tuyển Việt Nam khó chồng khó, thầy Park tìm kế vượt bão táp', 'tuyen-viet-nam-kho-chong-kho-thay-park-tim-ke-vuot-bao-tap', '<p>Với việc AFF Cup lẫn vòng loại World Cup 2022 dồn vào năm đá SEA Games, thầy Park phải căng óc tính kế cùng các đội tuyển vượt bão táp.</p>', '<p>Năm bão táp…</p>\r\n\r\n<p>Việc FIFA quyết định dời lại các trận đấu tại vòng loại World Cup 2022 khu vực châu Á sang năm 2021 do ảnh hưởng dịch Covid-19 khiến các đội bóng khu vực Đông Nam Á bị động, lo lắng trong đó có Việt Nam.</p>\r\n\r\n<p>Nguyên nhân là trước đó AFF cũng đã quyết định dời giải đấu cao nhất khu vực sang tháng 4/2021 nên<a href=\"https://vietnamnet.vn/tuyen-viet-nam-tag69831.html\">&nbsp;tuyển Việt Nam</a>, Thái Lan, Malaysia – những đội bóng còn cơ hội giành vé vào vòng loại thứ 3 World Cup 2022- đối mặt với hàng loạt khó khăn.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2019/07/17/19/tuyen-viet-nam-de-thi-co-de-nhung-cho-voi-mung.JPG\" title=\"Bóng đá Việt Nam và HLV Park Hang Seo sẽ đối mặt với một năm 2021 đầy chông gai\"><img alt=\"Tuyển Việt Nam khó chồng khó, thầy Park tìm kế vượt bão táp\" src=\"https://vnn-imgs-f.vgcloud.vn/2019/07/17/19/tuyen-viet-nam-de-thi-co-de-nhung-cho-voi-mung.JPG\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bóng đá Việt Nam và HLV Park Hang Seo sẽ đối mặt với một năm 2021 đầy chông gai</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Khó khăn đầu tiên là thời gian thi đấu đến lúc này chưa xác định một cách cụ thể, vì vậy AFF Cup lẫn vòng loại World Cup 2022 cùng rơi vào một thời điểm hoặc sát nhau rất dễ xảy ra khiến các đội không dễ xoay sở.</p>\r\n\r\n<p>Kế đó số lượng các trận đấu tại vòng loại World Cup 2022 lẫn AFF Cup không hề nhỏ. Điều này buộc các đội bóng phải tính toán song song cùng lịch thi đấu tại giải VĐQG nhằm đồng bộ nhất có thể, đảm bảo cho cầu thủ thể trạng, phong độ tốt với 2 giải đấu quan trọng trong năm.</p>\r\n\r\n<p>Nếu các đối thủ trong khu vực chỉ phải lo cho tuyển quốc gia thì công việc năm 2021 đối với HLV Park Hang Seo nhiều gấp bội khi vừa phải chăm sóc cả tuyển Việt Nam lẫn U22 Việt Nam. Trong đó, mục tiêu của thầy Park được nêu ra rất cao như bảo vệ tấm HCV SEA Games 31, vô địch AFF Cup, vào vòng sơ loại thứ 3 World Cup… nên khó chồng khó.</p>\r\n\r\n<p>... thầy Park tính kế gì trước tiên?</p>\r\n\r\n<p>Nhìn vào những giải đấu diễn ra năm sau, rất khó cho&nbsp;<a href=\"https://vietnamnet.vn/hlv-park-hang-seo-tag161113.html\">HLV Park Hang Seo</a>&nbsp;hoàn thành các mục tiêu đã đề ra nếu thiếu sự chuẩn bị kỹ lưỡng ngay từ bây giờ gồm lực lượng cho đến đội ngũ trợ lý.</p>\r\n\r\n<p>Đối với vấn đề lực lượng, dù lúc này còn rất nhiều khó khăn nhưng nhìn chung với khoảng hơn 100 cầu thủ được chấm cho 2 đội tuyển tiến hành sàng lọc dần dần, cộng thêm quỹ thời gian tương đối dư dả thì có lẽ không phải quá lo đối với ông thầy người Hàn Quốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/04/05/22/hlv-park-hang-seo-tinh-duong-cho-tuyen-viet-nam-nhu-the-co-la-du.jpeg\" title=\"nhưng dù thế nào HLV Park Hang Seo cũng có những trợ lý thân cận để cùng tháo gỡ khó khăn\"><img alt=\"Tuyển Việt Nam khó chồng khó, thầy Park tìm kế vượt bão táp\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/04/05/22/hlv-park-hang-seo-tinh-duong-cho-tuyen-viet-nam-nhu-the-co-la-du.jpeg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>nhưng dù thế nào HLV Park Hang Seo cũng có những trợ lý thân cận để cùng tháo gỡ khó khăn</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vấn đề còn lại trước đây vốn dĩ không đơn giản đối với thuyền trưởng tuyển Việt Nam, bởi hầu hết những trợ lý thân cận đang ăn lương các CLB nên chẳng dễ làm nghĩa vụ quốc gia dài ngày thì giờ đã nhẹ gánh đi khá nhiều khi ông Park nhận được sự hỗ trợ từ VFF.</p>\r\n\r\n<p>Hiện tại ngoài nhóm 5 đồng hương gồm ông Lee Young-jin, Kim Han-yoon, chuyên gia thể lực Park Sung-gyun, chuyên gia Choi Ju-young và phiên dịch tiếng Anh, HLV Park Hang Seo còn nhận được sự bổ sung rất lớn từ đội ngũ trợ lý trong nước.</p>\r\n\r\n<p>Cụ thể hơn, đợt tập trung tới đây các trợ lý Lưu Danh Minh, Nguyễn Đức Cảnh, Nguyễn Thế Anh, Tuấn Vinh, Chu Ngọc Cảnh được ông Park mời lên U22 Việt Nam hỗ trợ và giải quyết khối công việc rất nặng trong năm 2021.</p>\r\n\r\n<p>Những sự bổ sung ở cấp thượng tầng này chắc chắn chưa phải là cuối cùng, bởi như đã nói năm sau tuyển Việt Nam, U22 Việt Nam và HLV Park Hang Seo căng mình hoàn tất các mục tiêu đan xen&nbsp;nên ông thầy người Hàn sẽ tiếp tục tìm thêm cho mình những cánh tay nối dài.</p>\r\n\r\n<p>Sắp xếp xong điều này, ít nhất những lo lắng cho HLV Park Hang Seo vơi đi và từ đó có thể tiếp tục kỳ vọng vào tuyển Việt Nam hay U22 Việt Nam có thành công mới trong năm sóng gió 2021.</p>', 'ZGxe_hlv-park-hang-seo-tinh-duong-cho-tuyen-viet-nam-nhu-the-co-la-du-360x240.jpeg', 0, 0, 45, '2020-08-15', '2020-08-15'),
(117, 'Facebook - hãng công nghệ chuyên sao chép ý tưởng', 'facebook-hang-cong-nghe-chuyen-sao-chep-y-tuong', '<p>Sao chép giống hệt tính năng từ công ty đối thủ, Mark Zuckerberg xác nhận chỉ phát triển trên những thứ sẵn có.</p>', '<p>Instagram (ứng dụng được Facebook mua lại từ năm 2012), vừa giới thiệu tính năng chia sẻ video ngắn trên nền nhạc có tên là Reels.</p>\r\n\r\n<p>Theo&nbsp;The Verge, Reels có ngôn ngữ thiết kế \"giống hệt\" như TikTok, một nền tảng chia sẻ video của công ty Trung Quốc ByteDance đang có nguy cơ bị cấm tại thị trường Mỹ.</p>\r\n\r\n<p>Đây không phải lần đầu tiên Facebook sao chép các tính năng của các công ty khởi nghiệp (startup) nhỏ hoặc những đối thủ. Năm 2018, Facebook ra mắt nền tảng chia sẻ video ngắn Lasso nhằm cạnh tranh với TikTok. Đầu năm 2020, Facebook giới thiệu Hobbi với giao diện và tính năng tương tự như Pinterest.</p>\r\n\r\n<p>Một trong những cáo buộc sao chép ồn ào nhất của Facebook là tính năng \"Stories\" trên Instagram. Trước đó, \"Stories\" cho phép người dùng đăng tải video ngắn, chỉ hiển thị trong 24h được xem là tính năng nổi bật nhất của Snapchat.</p>\r\n\r\n<p>Snapchat là nạn nhân bị sao chép nhiều nhất</p>\r\n\r\n<p>Năm 2013, Facebook từng ngỏ lời mua Snapchat với giá&nbsp;3 tỷ USD, lúc này Snapchat chỉ là một ứng dụng nhắn tin mới thành lập được 2 năm. Tuy nhiên, CEO Evan Spiegel đã từ chối đề nghị trên. Thương vụ thất bại, Snapchat chính thức được xem là đối thủ của Facebook trong lĩnh vực mạng xã hội.</p>\r\n\r\n<p><a href=\"https://vietnamnet.vn/snapchat-tag50813.html\">Snapchat</a>&nbsp;giới thiệu tính năng \"Stories\" vào tháng 5/2014, nhanh chóng thu hút sự chú ý của người dùng vì họ có thể đăng tải bất cứ nội dung nào họ thích và yên tâm chúng đó sẽ biến mất sau 24h.</p>\r\n\r\n<p>Tháng 8/2016, Facebook cập nhật tính năng giống hệt như vậy trên Instagram. Tháng 3/2017, tính năng tương tự xuất hiện trên Facebook Messenger.</p>\r\n\r\n<p>Theo&nbsp;Washington Post, Kevin Systrom, Giám đốc Điều hành Instagram cho biết tính năng \"Stories\" do công ty ông phát triển \"giống hệt\" trên phiên bản Snapchat, nhưng khẳng định họ không sao chép.</p>\r\n\r\n<p>\"Chúng tôi không sao chép từ ai cả. Trong trường hợp này, Instagram đã phát triển dựa trên nền tảng sẵn có từ Snapchat\", Kevin nói với&nbsp;The Washington Post.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><a href=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w1024/Uploaded/rptjhun/2020_08_10/rm_instagram_vs_snapchat_stories_1_.png\" title=\"\"><img alt=\"Facebook an cap tinh nang, Facebook thau tom cong ty khac, Facebook mua Snapchat, Facebook mua Instagram, Facebok co sai khi ra mat Reels anh 1\" src=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w1024/Uploaded/rptjhun/2020_08_10/rm_instagram_vs_snapchat_stories_1_.png\" title=\"Facebook ăn cắp tính năng, Facebook thâu tóm công ty khác, Facebook mua Snapchat, Facebook mua Instagram, Facebok có sai khi ra mắt Reels ảnh 1\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Giao diện \"Stories\" trên 2 ứng dụng Instagram (trái) và Snapchat (phải). Ảnh: Social Media Examiner.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong phiên điều trần diễn ra vào ngày 30/7, Chủ tịch Ủy ban Tư pháp Hạ viện Jerry Nadler lập luận việc Facebook mua lại Instagram vào năm 2012 là vi phạm luật chống độc quyền. Các email thu thập được cho thấy việc mua lại Instagram là nhằm loại bỏ \"một đối thủ tiềm năng\".</p>\r\n\r\n<p>Tuy nhiên, nếu Facebook chỉ \"phát triển\" một tính năng giống hệt ứng dụng đối thủ, công ty này sẽ không vi phạm luật tại Mỹ và không bị gán tội vi phạm luật chống độc quyền.</p>\r\n\r\n<p>Điều này đồng nghĩa với việc mua lại toàn bộ công ty đối thủ là sai, nhưng sao chép giống hệt tính năng của người khác thì không sai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>\"<a href=\"https://vietnamnet.vn/facebook-tag22536.html\">Facebook</a>&nbsp;không vi phạm bất kỳ luật kinh tế nào khi ra đời những tính năng tương tự đối thủ, nhưng nếu cứ đi theo hướng sao chép như vậy sẽ không tốt cho tương lai của công ty\", Wendy Johansson, nhà phân tích kỹ thuật số tại Publicis Sapien nhận định.</p>\r\n\r\n<p>Reels ra mắt ngay khi sắc lệnh cấm TikTok ban hành</p>\r\n\r\n<p>Ngày 4/8, Tổng thống Mỹ Donald Trump xác nhận hoãn lệnh cấm và cho phép Microsoft tiếp tục đàm phán mua lại nền tảng chia sẻ video TikTok, thương vụ buộc phải hoàn thành trước ngày 15/9.</p>\r\n\r\n<p>Sau đó 2 ngày, ông Trump ban hành một sắc lệnh mới, cấm mọi giao dịch cá nhân hoặc tổ chức thuộc quyền tài phán của Mỹ với ByteDance (công ty mẹ của TikTok) sau ngày 20/9. Đồng nghĩa với việc \"một công ty Mỹ\" chỉ có khoảng 45 ngày nữa để mua TikTok hoặc ứng dụng này sẽ bị cấm vĩnh viễn.</p>\r\n\r\n<p>Ngay lập tức, Instagram thông báo ra mắt Reels, một tính năng quay, dựng và chia sẻ video ngắn trên nền nhạc tương tự như TikTok. Tính năng Reels được thử nghiệm trước tại 50 quốc gia lớn như Mỹ, Ấn Độ, Brazil, Anh, Pháp, Đức, Nhật Bản, Australia...</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><a href=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w1024/Uploaded/rptjhun/2020_08_10/Picture2.jpg\" title=\"\"><img alt=\"Facebook an cap tinh nang, Facebook thau tom cong ty khac, Facebook mua Snapchat, Facebook mua Instagram, Facebok co sai khi ra mat Reels anh 2\" src=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w1024/Uploaded/rptjhun/2020_08_10/Picture2.jpg\" title=\"Facebook ăn cắp tính năng, Facebook thâu tóm công ty khác, Facebook mua Snapchat, Facebook mua Instagram, Facebok có sai khi ra mắt Reels ảnh 2\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Reels (trái) là tính năng quay và chia sẻ video ngắn, ra mắt ngay sau khi Tổng thống Mỹ ban hành sắc lệnh cấm TikTok (phải). Ảnh: Chụp màn hình.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sự xuất hiện của Reels một lần nữa đặt ra câu hỏi về vai trò của luật chống độc quyền tại thị trường Mỹ.</p>\r\n\r\n<p>Đáng chú ý, Instagram còn thể hiện động thái lôi kéo người dùng, đặc biệt là các nhà sáng tạo nội dung. Theo New York Times, Instagram xác nhận sẵn sàng hợp tác với những nhà sáng tạo nội dung trên TikTok, đồng ý chi trả \"hàng nghìn USD\" với điều kiện họ đăng tải video lên Reels \"trước những nền tảng khác\".</p>\r\n\r\n<p>Tại phiên điều trần của Hạ viện Mỹ về vấn đề chống độc quyền, Mark Zuckerberg được hỏi về việc Facebook có sao chép các tính năng từ công ty đối thủ hay không.</p>\r\n\r\n<p>\"Chúng tôi chỉ điều chỉnh trên các tính năng sẵn có\", CEO Facebook trả lời.</p>\r\n\r\n<p>\"Với những tính năng không được pháp luật bảo hộ như một nền tảng video quá mới, Facebook đang ở vị thế mà họ có thể sao chép y hệt tính năng và phổ cập nó nhanh hơn nhờ lượng người dùng khổng lồ\", Rebecca Allensworth, giáo sư ngành luật và chuyên gia chống độc quyền tại Đại học Vanderbilt nhận định.</p>', 'rVxf_Facebook_CEO_Mark_Zuckerberg_testifies_before_a_House_Committee_hearing_on_Capitol_Hill_in_Washington_Oct._23_2019_AP_Photo_Andrew_Harnik_1280x640-360x240.jpg', 0, 0, 50, '2020-08-15', '2020-08-15'),
(118, 'Hạnh phúc đến với người đàn ông giảm cân nhiều nhất thế giới', 'hanh-phuc-den-voi-nguoi-dan-ong-giam-can-nhieu-nhat-the-gioi', '<p>Sau khi giảm hơn 100 kg, anh Phil có vóc dáng cân đối, dễ dàng đi du lịch khắp nơi và tìm được tình yêu của đời mình.</p>', '<p>Anh Phil Kayes sống ở County Down, Bắc Ireland được&nbsp;mệnh danh&nbsp;“Người giảm cân nhiều nhất thế giới 2020” sau khi sụt 138 kg.</p>\r\n\r\n<p>Đó không phải là niềm vui duy nhất của Phil trong thời gian qua. Người đàn ông 47 tuổi còn tìm thấy tình yêu mới và hạ gục virus nCoV.</p>\r\n\r\n<p><img alt=\"Hạnh phúc đến với người đàn ông giảm cân nhiều nhất thế giới\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/06/29/10/hanh-phuc-nhan-doi-cua-nguoi-dan-ong-tung-bi-canh-bao-kho-song-qua-40-tuoi.jpg\" /></p>\r\n\r\n<p>Phil trước và sau khi giảm 138 kg</p>\r\n\r\n<p>Sự chuyển biến ngoại hình của Phil bắt đầu khi bác sĩ cảnh báo rằng trọng lượng nặng quá mức của anh sẽ khiến anh khó sống qua 40 tuổi. Mẹ của anh bị chẩn đoán mắc ung thư giai đoạn cuối khiến anh thêm lo lắng và quyết tâm giảm cân.&nbsp;</p>\r\n\r\n<p>Lúc cao điểm, anh Phil có số cân lên tới 236 kg nhưng hiện tại, anh chỉ còn nặng 98 kg sau 3 năm nỗ lực theo chương trình giảm cân dựa vào chế độ ăn của một tổ chức tại Anh.</p>\r\n\r\n<p>Trước đó, do ngoại hình quá khổ, Phil rất ít khi ra ngoài đường, trừ lúc phải đi làm. “Tôi tránh mọi bữa tiệc bởi cảm thấy không thoải mái. Tôi biết mình sẽ không ngồi vừa ghế trên máy bay bởi vậy tôi không bao giờ đi du lịch dù tôi rất muốn”, anh Phil tâm sự.</p>\r\n\r\n<p>Do bị béo phì, Phil có rất nhiều vấn đề về&nbsp;<a href=\"https://vietnamnet.vn/vn/suc-khoe/\">sức khỏe</a>&nbsp;trong đó có áp lực lên đầu gối và mất ngủ. Phil cũng lo nếu anh chết khi ngủ, gia đình sẽ không thể di chuyển thi thể của anh từ tầng 3 xuống.</p>\r\n\r\n<p>Việc mua quần áo cũng rất nan giải khi Phil mặc đồ cỡ XXXXXXXXL và eo 172 cm.</p>\r\n\r\n<p><img alt=\"Hạnh phúc đến với người đàn ông giảm cân nhiều nhất thế giới\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/06/29/10/hanh-phuc-nhan-doi-cua-nguoi-dan-ong-tung-bi-canh-bao-kho-song-qua-40-tuoi-1.jpg\" /></p>\r\n\r\n<p>Phil cùng bạn gái Nicole chụp chiếc áo anh từng mặc trước đây</p>\r\n\r\n<p>Khi mẹ qua đời vào tháng 10/2017, Phil đã giảm được 31 kg. “Tôi biết mẹ sẽ thực sự tự hào nếu thấy tôi bây giờ, khi tôi giảm được tổng cộng 138 kg”, Phil tâm sự.</p>\r\n\r\n<p>Không chỉ sở hữu thân hình cân đối hơn, Phil đã có được sự tự tin và độc lập. Hiện tại, anh có thể ra ngoài đi dạo, đạp xe và tập yoga mà không hề đau đớn. “Những năm quá cân khiến đầu gối của tôi đau nhức kéo dài và tôi phải dùng thuốc mỗi ngày. Bác sĩ nói việc phẫu thuật quá nguy hiểm với người có trọng lượng như tôi”, Phil nhớ lại.</p>\r\n\r\n<p>Phil dường như có một cuộc sống hoàn toàn khác hẳn trước đây với rất nhiều cơ hội, bạn bè và những cuộc phiêu lưu. Anh cũng dễ dàng vượt qua căn bệnh Covid-19 nhờ sức khỏe cải thiện hơn nhiều.</p>\r\n\r\n<p>Quan trọng hơn cả, Phil đã tìm được tình yêu qua phần mềm hẹn hò trên mạng. “Khi tôi có vóc dáng to lớn, tôi không thể nghĩ tới ngày sẽ tìm được người yêu và nghĩ sẽ chết trong cô độc”, Phil kể.</p>\r\n\r\n<p>Kể từ khi gặp người phụ nữ có tên Nicole, Phil dành thời gian để đi nghỉ ở biển Địa Trung Hải, đi bộ quanh Berlin (Đức) và đã học lái xe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Hạnh phúc đến với người đàn ông giảm cân nhiều nhất thế giới\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/06/29/10/hanh-phuc-nhan-doi-cua-nguoi-dan-ong-tung-bi-canh-bao-kho-song-qua-40-tuoi-2.jpg\" /></p>\r\n\r\n<p>Việc giảm cân giúp Phil có một cuộc sống mới khỏe mạnh và hạnh phúc</p>\r\n\r\n<p>Chế độ ăn của Phil trước đây:</p>\r\n\r\n<p>Bữa sáng: Xúc xích với phô mai và tương cà; cà phê latte hoặc cappuccino thêm đường</p>\r\n\r\n<p>Bữa trưa: Súp đóng hộp với 3-4 lát bánh mì trắng, một gói lớn khoai tây chiên và thanh chocolate hoặc khoai tây chiên với phô mai và rất nhiều xúc xích</p>\r\n\r\n<p>Bữa tối: Pizza lớn mua ở siêu thị, khoai tây chiên và chocolate hoặc đồ ăn mang đi</p>\r\n\r\n<p>Bữa khuya: Rất nhiều khoai tây chiên, thanh chocolate lớn, kẹo, bánh vòng, bánh quy</p>\r\n\r\n<p>Hiện tại:</p>\r\n\r\n<p>Bữa sáng: Ngũ cốc giảm đường với sữa chua không béo và dâu đông lạnh; cà phê Americano với sữa gầy</p>\r\n\r\n<p>Bữa phụ: Hoa quả</p>\r\n\r\n<p>Bữa trưa: Đồ ăn còn lại của bữa tối hôm trước hoặc đĩa mì với ít hoa quả</p>\r\n\r\n<p>Bữa tối: Gà hoặc cà hồi với khoai tây với nhiều rau</p>\r\n\r\n<p>Bữa khuya: Thanh dinh dưỡng cho người giảm cân.&nbsp;</p>', '2PtV_hanh-phuc-nhan-doi-cua-nguoi-dan-ong-tung-bi-canh-bao-kho-song-qua-40-tuoi-1-240x160.jpg', 0, 0, 56, '2020-08-15', '2020-08-15'),
(119, 'Người đàn ông từ chối 1,7 triệu USD để sống giữa sân bay quốc tế', 'nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te', '<p>Nông trại hữu cơ của nông dân người Nhật Takao Shito nằm gọn bên trong khuôn viên của sân bay quốc tế Narita - sân bay đông đúc thứ 2 Nhật Bản.</p>', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-2.jpg\" title=\"Nông trại của ông Takao nằm giữa sân bay quốc tế Narita, Nhật Bản.\"><img alt=\"Người đàn ông từ chối 1,7 triệu USD để sống giữa sân bay quốc tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-2.jpg\" style=\"height:433px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nông trại của ông Takao nằm giữa sân bay quốc tế Narita, Nhật Bản.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Những chiếc máy bay hạ cánh và cất cánh ngay bên cạnh nông trại của Takao là hình ảnh quen thuộc mỗi ngày.</p>\r\n\r\n<p>“Bạn sẽ quen với tiếng ồn” - người đàn ông 68 tuổi có giọng nói nhẹ nhàng chia sẻ về nông trại của mình.</p>\r\n\r\n<p>“Đây là mảnh đất đã được canh tác qua 3 thế hệ trong gần 1 thế kỷ, bởi chính ông tôi, bố tôi và tôi. Tôi muốn tiếp tục sống và trồng trọt ở đây” - ông giải thích về việc nhất quyết không giao đất cho sân bay.</p>\r\n\r\n<p>Cuộc chiến đất đai của Takao và một số gia đình khác trong suốt mấy chục năm từng là một thử thách lớn với sân bay Narita.</p>\r\n\r\n<p>Sân bay này là cửa ngõ quốc tế chính của Tokyo, đón khoảng 40 triệu khách và 250.000 chuyến bay mỗi năm.</p>\r\n\r\n<p>Dự án xây dựng sân bay Narita từng gây tranh cãi trong khu vực kể từ lần đầu tiên được chính phủ đề xuất năm 1966, làm dấy lên cuộc biểu tình của các nhà hoạt động và nông dân, trong đó có cha của Takao.</p>\r\n\r\n<p>Các cuộc biểu tình dẫn đến bạo lực và kết thúc bằng cái chết của một số cảnh sát và người dân.</p>\r\n\r\n<p>Sân bay được mở cửa vào năm 1978. Nhiều năm sau, các nhà chức trách đã lên tiếng xin lỗi vì những phản ứng mạnh tay với người biểu tình.</p>\r\n\r\n<p>Hiện tại, ông Takao hài lòng với công việc làm nông của mình ở nông trại. Trên mảnh đất này, ông trồng cà rốt, hành, tỏi... - tất cả có khoảng 10 loại rau. Việc trồng rau theo phương pháp hữu cơ khiến ông rất bận rộn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy vậy, ông chia sẻ, ông vẫn có thể tranh thủ thời gian rảnh rỗi để uống bia và hát karaoke.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-1.jpg\" title=\"Ông Takao đang rất hài lòng với công việc của mình. \"><img alt=\"Người đàn ông từ chối 1,7 triệu USD để sống giữa sân bay quốc tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-1.jpg\" style=\"height:510px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ông Takao đang rất hài lòng với công việc của mình.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước khi về nông trại làm nông, ông Takao từng làm việc trong một nhà hàng. Ông cho biết, ông chưa từng nghĩ đến việc rời bỏ nơi này. &nbsp;</p>\r\n\r\n<p>Có khoảng 10 tình nguyện viên đang giúp ông công việc của nông trại. Một số người trong đó từng tham gia biểu tình trước đây.</p>\r\n\r\n<p>Ông kể, trước đây ông được đề nghị nhận số tiền bồi thường lên tới gần 1,7 triệu USD để chuyển đi. Số tiền ấy tương đương với thu nhập của một người nông dân làm việc trong vòng 150 năm.</p>\r\n\r\n<p>Nhưng ông không thích tiền, mà muốn tiếp tục làm nông nghiệp. Ông nói, đất của ông rất tốt bởi vì nó đã được trồng trọt suốt 100 năm.</p>\r\n\r\n<p>“Trồng trọt, thu hoạch, giao hàng cho khách - chẳng có gì làm tôi vui hơn điều đó\" - ông nói.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-3.jpg\" title=\"Vị trí nông trại của ông Takao giữa sân bay quốc tế Narita.\"><img alt=\"Người đàn ông từ chối 1,7 triệu USD để sống giữa sân bay quốc tế\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/17/nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-3.jpg\" style=\"height:435px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vị trí nông trại của ông Takao giữa sân bay quốc tế Narita.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'd5jV_nguoi-dan-ong-tu-choi-1-7-trieu-usd-de-song-giua-san-bay-quoc-te-2-360x240.jpg', 0, 0, 36, '2020-08-15', '2020-08-15'),
(120, 'Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng', 'nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong', '<p>Họa sĩ Yaoyao Ma Van As đã khắc họa những điều thú vị chỉ những ai độc thân mới được tận hưởng qua nét vẽ đầy sinh động.&nbsp;</p>', '<p>Cuộc sống độc thân có nhiều điều thú vị hơn bạn nghĩ&nbsp;nếu bạn thấy được những lợi ích và học cách tận hưởng từng giây phút.&nbsp;</p>\r\n\r\n<p>Họa sĩ Yaoyao Ma Van As, nữ hoạ sĩ đang sinh sống tại California, Mỹ đã khắc họa những điều thú vị chỉ những ai độc thân mới được tận hưởng. Các cô nàng độc thân&nbsp;có nhiều thời gian hơn để theo đuổi đam mê, sự nghiệp... Vì vậy, nếu bạn đang độc thân hãy bình tĩnh tận hưởng những đặc quyền&nbsp;này nhé.</p>\r\n\r\n<p>Theo đuổi đam mê</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong.jpg\" title=\"Khi chưa vướng bận chuyện chồng con, bạn thỏa sức theo đuổi đam mê của bản thân như: &quot;Học guitar, vẽ, chụp ảnh … thậm chí là những gì mạo hiểm như leo núi hoặc lấy bằng phi công chẳng hạn&quot;.\"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khi chưa vướng bận chuyện chồng con, bạn thỏa sức theo đuổi đam mê của bản thân như: \"Học guitar, vẽ, chụp ảnh … thậm chí là những gì mạo hiểm như leo núi hoặc lấy bằng phi công chẳng hạn\".</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trải nghiệm những điều mới mẻ</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-1.jpg\" title=\"Cuộc sống không bị ràng buộc trách nhiệm với bất cứ ai. Bạn có thể đi bất kỳ đâu bạn muốn, làm những gì bạn thích, tha hồ thử những điều mới mẻ hay đơn giản là nấu món ăn theo cách riêng của mình, không phải quan tâm đến khẩu vị của mọi người.\"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-1.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cuộc sống không bị ràng buộc trách nhiệm với bất cứ ai. Bạn có thể đi bất kỳ đâu bạn muốn, làm những gì bạn thích, tha hồ thử những điều mới mẻ hay đơn giản là nấu món ăn theo cách riêng của mình, không phải quan tâm đến khẩu vị của mọi người.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chăm sóc bản thân</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-2.jpg\" title=\"Khi chưa kết hôn, bạn sẽ có nhiều thời gian chăm sóc cơ thể, làm đẹp cho mình. Đầu tóc hợp mốt, trang phục sang chảnh... và không phải mang bộ mặt mệt mỏi, đầu tóc rối bù như những bà mẹ bỉm sữa.\"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-2.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khi chưa kết hôn, bạn sẽ có nhiều thời gian chăm sóc cơ thể, làm đẹp cho mình. Đầu tóc hợp mốt, trang phục sang chảnh... và không phải mang bộ mặt mệt mỏi, đầu tóc rối bù như những bà mẹ bỉm sữa.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo đuổi nghề nghiệp mình yêu thích</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-3.jpg\" title=\"Phụ nữ hiện đại luôn lấy công việc và sự thăng tiến trong nghề nghiệp là điều quan trọng. Bạn sẽ không bị ảnh hưởng bởi những căng thẳng trong hôn nhân. Lúc này, bạn càng có nhiều thời gian tập trung phát triển kỹ năng làm việc, xây dựng những mối quan hệ mới.\"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/10/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-3.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Phụ nữ hiện đại luôn lấy công việc và sự thăng tiến trong nghề nghiệp là điều quan trọng. Bạn sẽ không bị ảnh hưởng bởi những căng thẳng trong hôn nhân. Lúc này, bạn càng có nhiều thời gian tập trung phát triển kỹ năng làm việc, xây dựng những mối quan hệ mới.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dọn dẹp nhà cửa tùy hứng</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong.jpg\" title=\"Sống 1 mình, bạn có thể cho phép bản thân lười biếng một chút và dọn dẹp nhà cửa bất cứ khi nào bạn thích.\"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Sống 1 mình, bạn có thể cho phép bản thân lười biếng một chút và dọn dẹp nhà cửa bất cứ khi nào bạn thích.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Dành toàn bộ thời gian cho chính mình vào buổi sáng</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-1.jpg\" title=\"Kết hôn là bước ngoặt của cuộc sống, giúp bạn có những trải nghiệm tuyệt vời. Tuy nhiên, nếu chưa tìm được một nửa, bạn hãy tranh thủ tận hưởng những giây phút bình yên vào mỗi buổi sáng. Bạn có thể thức dậy vào lúc 8 giờ, trang điểm lộng lẫy và nhâm nhi tách cà phê nóng hổi một cách thư thái. \"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-1.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kết hôn là bước ngoặt của cuộc sống, giúp bạn có những trải nghiệm tuyệt vời. Tuy nhiên, nếu chưa tìm được một nửa, bạn hãy tranh thủ tận hưởng những giây phút bình yên vào mỗi buổi sáng. Bạn có thể thức dậy vào lúc 8 giờ, trang điểm lộng lẫy và nhâm nhi tách cà phê nóng hổi một cách thư thái.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Không phải lo lắng quá nhiều đến ngoại hình</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-2.jpg\" title=\"Khi ở 1 mình, không cần phải trang điểm cầu kỳ. Lúc này, xấu hay đẹp với bạn không phải mối bận tâm hàng đầu. \"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-2.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Khi ở 1 mình, không cần phải trang điểm cầu kỳ. Lúc này, xấu hay đẹp với bạn không phải mối bận tâm hàng đầu.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Làm bất cứ điều gì mình thích</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-3.jpg\" title=\"Độc thân không có nghĩa là cô độc. Bạn luôn tìm được nềm vui cho mình và làm mọi điều mình thích. \"><img alt=\"Những điều thú vị chỉ cô nàng độc thân mới được tận hưởng\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/11/nhung-dieu-thu-vi-chi-co-nang-doc-than-moi-duoc-tan-huong-3.jpg\" style=\"height:1125px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Độc thân không có nghĩa là cô độc. Bạn luôn tìm được nềm vui cho mình và làm mọi điều mình thích.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'cltW_cuoc-song-1-minh-cua-quy-co-doc-than-360x240.jpg', 0, 0, 37, '2020-08-15', '2020-08-15'),
(121, 'Tất cả đã là quá khứ, mong anh hãy quên em đi', 'tat-ca-da-la-qua-khu-mong-anh-hay-quen-em-di', '<p>Năm lớp 9, tôi và anh thích nhau. Rất thích nhau. Chúng tôi cùng thi lên một trường tốt của huyện. Kết quả, tôi đỗ chuyên Anh, anh học lớp thường.</p>', '<p>Thế rồi từ đó anh im lặng, không hỏi tôi một lời. Gặp giữa sân trường cũng coi như không quen.</p>\r\n\r\n<p>Tôi không hiểu vì sao anh lại thế. Nhưng vốn cái tôi cao, tôi cũng mặc kệ anh. Anh muốn lơ thì mình cần gì phải sáp vào...</p>\r\n\r\n<p>Thi đại học. Tôi đỗ một trường ở Sài Gòn. Anh học ở trường đại học tỉnh. Chúng tôi ngày càng xa nhau. Chỉ có nhà ở quê là vẫn gần nhau, cách có 1 cây cầu.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/11/ava0.jpg\" title=\"Ảnh: Đức Liên\"><img alt=\"Tất cả đã là quá khứ, mong anh hãy quên em đi\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/11/ava0.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ảnh: Đức Liên</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ra trường, tôi đi làm. Công việc mưu sinh vất vả cuốn tôi đi. Đầy mệt nhọc. Tôi cũng trải qua vài mối tình, nhưng không đi đến đâu. Kết quả, 30 tuổi tôi vẫn ế. Chỉ có công việc là thăng tiến một chút.</p>\r\n\r\n<p>Tôi không hề quên anh, nhưng cũng không kì vọng. Mỗi lần về quê, bà tôi vẫn bảo, thằng Tuấn nó thích mày lắm đấy. Bà nó cứ bảo với bà là muốn nhận mày làm cháu dâu. Thằng Tuấn vẫn chờ mày đấy.</p>\r\n\r\n<p>Tôi nghe xong chỉ cười, chứ không tin. Chờ, sao không nói. Mười mấy năm qua rồi, tiếng “yêu”, “chờ” khó đến thế sao...</p>\r\n\r\n<p>32 tuổi tôi lấy chồng. Rồi chuyển sang Pháp sinh sống. Chồng tôi yêu tôi. Tôi yêu chồng tôi. Tôi đã chẳng còn chút mảy may gì nhớ đến quá khứ, kể cả Tuấn. Tôi chỉ yêu gia đình hiện tại của mình. Những cái đã qua, tôi coi nó là điều tất yếu. Cái gì phải đến sẽ đến. Cái gì cần quên sẽ quên...</p>\r\n\r\n<p>Tại Pháp, tôi gặp em họ của Tuấn. Thật khó tin, nhưng là sự thật. Chúng tôi đi uống trà với nhau. Nguyệt bảo, sao chị phũ với anh ấy thế? Không một lời hỏi han. Anh ấy vẫn luôn yêu chị.</p>\r\n\r\n<p>Tôi có chút bất ngờ, là Tuấn không hỏi tôi trước kia mà...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>“Trong phòng anh ấy, vẫn còn ảnh của chị. Ảnh kỷ yếu lớp 9 của anh chị. Ảnh cắm trại lớp 11 của trường chị. Ảnh thẻ của chị... Anh ấy giữ hết. Anh còn bảo cả đời này sẽ không yêu ai ngoài chị”.</p>\r\n\r\n<p>“Vậy sao anh ấy không nói? Chị cũng 32 tuổi mới lấy chồng kia mà”. Tôi cười. Cố chêm một câu hài hước.</p>\r\n\r\n<p>“Anh ấy tự ti trước chị. Anh bảo chị là người luôn nhìn về phía trước, luôn tiến về phía trước. Từ lớp 10, khi anh phải vào học lớp thường, chị lớp chuyên, anh ấy đã biết từ giờ chỉ dám đi phía sau nhìn chị rồi.</p>\r\n\r\n<p>Vốn lớp 9 anh học rất giỏi. Anh cũng định nếu đỗ vào chuyên sẽ tỏ tình với chị. Nhưng anh lại thất bại. Anh ấu trĩ thì đúng rồi. Nhưng chị cũng phũ không kém mà. Chị vào lớp chuyên, học giỏi, lại hát hay, đàn hay... Chị có nhiều bạn mới ở các lớp chuyên. Chị có bao giờ nhìn đến anh ấy thui thủi ở lớp thường đâu...</p>\r\n\r\n<p>Nửa năm trước. Anh ấy lấy vợ. Trước ngày cưới, anh ấy uống say, rồi ôm đống ảnh của chị khóc. Tỉnh rượu, anh cất hết ảnh chị vào thùng. Em biết, anh ấy vẫn chưa quên được chị, dù vợ anh ấy rất tốt. Anh ấy cũng tốt với vợ, nhưng yêu thì chắc không trọn vẹn”...</p>\r\n\r\n<p>Tôi nghe xong, thấy nhói lòng. Tôi bảo Nguyệt, em nói với anh ấy, chị vẫn ổn. Cuộc sống của chị ổn. Từ lâu, chị vẫn coi anh là một người bạn tốt. Dù im lặng bao nhiêu năm qua, chị vẫn rất trân trọng anh. Nhưng tất cả đã là quá khứ rồi. Cứ mãi ôm những cái đã qua, làm sao hạnh phúc được. Anh không hạnh phúc, chị cũng đau lòng. Nhưng chỉ thế thôi. Nếu anh không tự cứu anh, thì không ai cứu được. Chị mong anh trân trọng hiện tại - gia đình nhỏ của anh. Đó mới là thứ đáng quý. Quá khứ chỉ khiến cho hiện tại rối rắm thêm, không giúp gì được.</p>\r\n\r\n<p>“Quả thật là chị rất phũ, rất lạnh. Bảo sao anh ấy không dám đến gần chị, dù rất yêu. Em sẽ chuyển lời. Nếu về Việt Nam, mong anh chị sẽ gặp nhau một lần. Đi uống với nhau như hai người bạn. Đừng im lặng nữa. Bỏ qua hết những hiểu lầm đi”...</p>\r\n\r\n<p>P/S: Anh à, tình yêu chỉ là những cảm xúc. Kiểm soát được nó hay không là do bản thân mình. Mong anh hãy quên em đi.</p>', 'yCWK_ava0-360x240.jpg', 0, 0, 38, '2020-08-15', '2020-08-15'),
(122, 'Ngôi sao mạng vẽ bậy lên di sản thế giới', 'ngoi-sao-mang-ve-bay-len-di-san-the-gioi', '<p>Một nhân vật có ảnh hưởng trên mạng xã hội đã nhận nhiều chỉ trích vì hành vi phá hoại Di sản Thế giới do UNESCO công nhận.</p>', '<p>Sáng 13/8, tờ Independent đưa tin, Alexandra Milam sở hữu 12.000 lượt theo dõi trên mạng xã hội đã phải xin lỗi sau khi vẽ bậy lên Durdle Door - một vòm đá cổ 180 triệu năm tuổi ở Dorset (Anh). Cụ thể, ngôi sao mạng này đã viết tên tài khoản cá nhân cùng biểu tượng mạng xã hội lên mặt đá.</p>\r\n\r\n<p>Sau khi nhận nhiều chỉ trích từ cộng đồng mạng, Milam đã quay lại điểm du lịch này để khắc phục hậu quả. Hình ảnh cô đang cố xóa hình vẽ bậy đã được một người dân địa phương chụp lại.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><a href=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w660/Uploaded/kbd_bcvi/2020_08_13/117256657_134176448353740_3954682393588015077_n.jpg\" title=\"\"><img alt=\"di san the gioi anh 1\" src=\"https://vnn-imgs-a1.vgcloud.vn/znews-photo.zadn.vn/w660/Uploaded/kbd_bcvi/2020_08_13/117256657_134176448353740_3954682393588015077_n.jpg\" style=\"height:963px; width:770px\" title=\"di sản thế giới ảnh 1\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Ngôi sao mạng xã hội phải xin lỗi và khắc phục hậu quả sau khi vẽ bậy trên đá. Ảnh: Independent.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>\"Tôi thấy rất tệ vì những thiệt hại mình gây ra khi viết tên lên đá ở bờ biển Kỷ Jura (Anh). Tôi đã có một bài học để ghi nhớ mãi. Các bạn cần hiểu rõ tầm quan trọng của địa điểm này. Hy vọng sẽ không còn ai phạm sai lầm như tôi\", cô chia sẻ với Dorset Echo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Milam cũng hứa quyên góp&nbsp;40 USD/tháng để ủng hộ đội tình nguyện làm sạch các mặt đá bị vẽ bẩn. \"Có lẽ không điều gì khắc phục được hậu quả tôi đã gây ra. Tuy nhiên, tôi sẽ cố cải thiện và mong mọi người hãy cho tôi cơ hội để làm điều đó\", cô chia sẻ.</p>\r\n\r\n<p>Ngôi sao mạng này không phải người duy nhất vẽ bậy lên vách đá cổ ở Dorset. Mỗi cuối tuần, một đội tình nguyện đều phải đến vệ sinh những hình vẽ bậy mà hàng trăm du khách để lại. Công việc dọn dẹp hậu quả của họ thường kéo dài hàng giờ.</p>', 'PXfj_117256657_134176448353740_3954682393588015077_n_1-360x240.jpg', 0, 0, 39, '2020-08-15', '2020-08-15'),
(123, 'Tài xế khoắng hàng tỷ đồng của công ty để trả nợ và cho người thân', 'tai-xe-khoang-hang-ty-dong-cua-cong-ty-de-tra-no-va-cho-nguoi-than', '<p>\"Cuỗm\" được hơn 3 tỷ đồng của công ty, Thiện mang đi trả nợ, sắm xe máy SH và mang cho người thân.</p>', '<p>TAND TP.HCM vừa xử sơ thẩm đối với bị cáo Lê Viết Thiện (33 tuổi, quê Đắk Nông) về tội “trộm cắp tài sản”.</p>\r\n\r\n<p>Theo cáo trạng, do gặp khó khăn về tài chính nên Thiện phải mang nhiều tài sản đi cầm đồ mà không có tiền để chuộc lại.</p>\r\n\r\n<p>Vốn làm tài xế tại Công ty cổ phần Quốc Huy Anh nên Thiện biết công ty dự trữ số lượng lớn tiền mặt để giao dịch, thanh toán. Trong lúc bí bách, anh ta nảy sinh ý định đột nhập vào công ty để trộm cắp.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/16/tai-xe-khoang-hang-ty-dong-cua-cong-ty-de-tra-no-va-cho-nguoi-than-1.jpg\" title=\"Bị cáo Lê Viết Thiện tại tòa\"><img alt=\"Tài xế khoắng hàng tỷ đồng của công ty để trả nợ và cho người thân\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/16/tai-xe-khoang-hang-ty-dong-cua-cong-ty-de-tra-no-va-cho-nguoi-than-1.jpg\" style=\"height:462px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bị cáo Lê Viết Thiện tại tòa</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sau khi chuẩn bị dụng cụ gây án như xà beng, ba lô, khuya ngày 5/10/2019, bị cáo ngụy trang kín mít, mang găng tay rồi đột nhập vào Công ty Quốc Huy Anh.</p>\r\n\r\n<p>Để tránh bị phát hiện, Thiện dùng sơn đen mang theo quét phủ lên \"mắt thần\" camera rồi dùng xà beng cạy cửa phòng tài chính.</p>\r\n\r\n<p>Sau 1 tiếng đục phá chiếc két, Thiện cũng mở được cửa két và khoắng đi một lượng lớn tiền mặt. Sáng hôm sau, anh ta gom phần lớn số tiền bỏ vào vali rồi mang tới gửi bạn gái.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Số còn lại, Thiện dùng trả nợ, cho người thân, chuộc các tài sản đã cầm đồ trước đó,&nbsp; gửi em trai, mua xe gắn máy SH và nhiều tài sản đắt tiền khác.</p>\r\n\r\n<p>Nhận được tin báo về việc mất trộm của Công ty cổ phần Quốc Huy Anh, cơ quan công an vào cuộc điều tra và xác định Thiện là “tác giả” của vụ trộm. Gần 1 tuần sau, anh ta bị bắt giữ.</p>\r\n\r\n<p>Tại CQĐT và tại phiên tòa, bị cáo đã thừa nhận hành vi phạm tội. Bị cáo khai, không biết chính xác trộm được bao nhiêu tiền.</p>\r\n\r\n<p>Theo lời khai của Công ty Quốc Huy Anh, tổng số tiền mà công ty này bị mất là 3,2 tỷ đồng. Tuy nhiên, CQĐT chỉ thu hồi được hơn 2,9 tỷ đồng, số còn lại bị cáo khai đã tiêu xài cá nhân và cho người thân.</p>\r\n\r\n<p>Xét thấy, hành vi phạm tội của bị cáo đã gây mất trật tự xã hội, cần có bản án nghiêm khắc để răn đe, phòng ngừa chung. Sau khi xem xét, HĐXX đã tuyên phạt bị cáo Thiện 14 năm tù.</p>', '0iQR_tai-xe-khoang-hang-ty-dong-cua-cong-ty-de-tra-no-va-cho-nguoi-than-2-360x240.jpg', 0, 0, 42, '2020-08-15', '2020-08-15'),
(124, 'Hiệp định dẫn độ giữa Việt Nam và Pháp là cơ sở phòng chống tội phạm bỏ trốn', 'hiep-dinh-dan-do-giua-viet-nam-va-phap-la-co-so-phong-chong-toi-pham-bo-tron', '<p>Hiệp định về dẫn độ giữa Việt Nam với Cộng hòa Pháp với mong muốn thiết lập hợp tác hiệu quả hơn giữa hai nước trong đấu tranh, phòng chống tội phạm.</p>', '<p>Bộ Ngoại giao vừa có thông báo số 33 về việc điều ước quốc tế có hiệu lực. Theo đó, Hiệp định về dẫn độ giữa&nbsp;Cộng&nbsp;hòa xã hội chủ nghĩa Việt Nam và Cộng hoà Pháp (Hiệp định) ký tại Hà Nội ngày 6/9/2016 có hiệu lực từ 1/5/2020&nbsp;gồm 24 điều.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/07/21/10/hiep-dinh-dan-do-giua-viet-nam-va-phap-co-so-phong-chong-toi-pham-bo-tron-1.jpg\" title=\"Cơ quan Cảnh sát điều tra Bộ Công an ra quyết định truy nã cựu Thứ trưởng Bộ Công thương Hồ Thị Kim Thoa\"><img alt=\"Hiệp định dẫn độ giữa Việt Nam và Pháp là cơ sở phòng chống tội phạm bỏ trốn\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/21/10/hiep-dinh-dan-do-giua-viet-nam-va-phap-co-so-phong-chong-toi-pham-bo-tron-1.jpg\" style=\"height:874px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cơ quan Cảnh sát điều tra Bộ Công an ra quyết định truy nã&nbsp;cựu Thứ trưởng Bộ Công thương Hồ Thị Kim Thoa</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Người nào bị truy nã có thể bị dẫn độ</p>\r\n\r\n<p>Điều 1 của Hiệp định nêu rõ, các bên cam kết chuyển giao cho nhau, theo quy định của Hiệp định này, bất cứ người nào đang có mặt trên lãnh thổ của một trong các bên, bị các cơ quan tư pháp của bên kia truy tố vì đã thực hiện một tội phạm hoặc truy nã để thi hành hình phạt tù đối với một hành vi có thể bị dẫn độ.</p>\r\n\r\n<p>Điều 2 quy định về các hành vi có thể bị dẫn độ. Cụ thể là các hành vi bị xử phạt tù từ một năm trở lên hoặc nặng hơn theo pháp luật của bên yêu cầu và bên được yêu cầu. Ngoài ra, nếu việc dẫn độ được yêu cầu nhằm thi hành một hình phạt tù được tuyên bởi cơ quan tư pháp có thẩm quyền của bên yêu cầu, thời hạn chấp hành hình phạt còn lại ít nhất phải là sáu tháng.</p>\r\n\r\n<p>Nếu yêu cầu dẫn độ liên quan đến nhiều hành vi khác nhau mà mỗi hành vi đều có thể bị xử phạt theo pháp luật của hai bên nhưng một số hành vi không đáp ứng các điều kiện quy định tại các khoản 1 và 2 Điều này thì bên được yêu cầu cũng có thể đồng ý dẫn độ đối với các hành vi đó.</p>\r\n\r\n<p>Về thủ tục, Điều 6 Hiệp định quy định, trừ trường hợp Hiệp định này có quy định khác, pháp luật của bên được yêu cầu là hệ thống pháp luật duy nhất được áp dụng đối với thủ tục bắt khẩn cấp, dẫn độ và quá cảnh.</p>\r\n\r\n<p>Theo Điều 7 của Hiệp định, mỗi bên chỉ định một cơ quan trung ương để thực hiện hiệp định này. Theo đó, đối với Cộng hòa xã hội chủ nghĩa Việt Nam, cơ quan trung ương là Bộ Công an; đối với Cộng hòa Pháp, cơ quan trung ương là Bộ Tư pháp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Có thể yêu cầu bắt khẩn cấp người bị yêu cầu dẫn độ</p>\r\n\r\n<p>Đáng chú ý là Điều 16 của Hiệp định quy định về bắt khẩn cấp. Cụ thể, trong trường hợp khẩn cấp, các cơ quan có thẩm quyền của bên yêu cầu có thể yêu cầu bắt khẩn cấp người bị yêu cầu dẫn độ.</p>\r\n\r\n<p>Yêu cầu bắt khẩn cấp phải bằng văn bản, nêu rõ có một trong số các giấy tờ thay thế quy định tại các điểm b và c Điều 8 Hiệp định này và cho biết ý định gửi yêu cầu dẫn độ.&nbsp;</p>\r\n\r\n<p>Theo điểm b và c Điều 8, trong trường hợp yêu cầu dẫn độ nhằm truy cứu trách nhiệm hình sự, yêu cầu dẫn độ phải kèm theo bản gốc hoặc bản sao xác thực lệnh bắt của cơ quan tư pháp có thẩm quyền của bên yêu cầu.</p>\r\n\r\n<p>Trong trường hợp yêu cầu dẫn độ nhằm thi hành hình phạt tù, yêu cầu dẫn độ phải kèm theo bản gốc hoặc bản sao xác thực của bản án kết tội có hiệu lực thi hành và quyết định về mức hình phạt được tuyên và thời hạn chấp hành hình phạt còn lại.</p>\r\n\r\n<table style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Những năm gần đây, Bộ Công an liên tục phát lệnh truy nã toàn quốc và quốc tế đối với một số bị can từng là quan chức, cán bộ nhà nước và một số ông chủ DN bỏ trốn khi bị&nbsp;cơ quan chức năng phát hiện sai phạm. Điển hình như trường hợp của Vũ Đình Duy, nguyên TGĐ PVTEX, ông chủ Nhật Cường Bùi Quang Huy, Trịnh Xuân Thanh...</p>\r\n\r\n			<p>Gần đây nhất là trường hợp của cựu Thứ trưởng Bộ Công thương&nbsp;Hồ Thị Kim Thoa vừa bị Cơ quan&nbsp;cảnh sát điều tra ra quyết định khởi tố bị can về tội Vi phạm quy định về quản lý, sử dụng tài sản Nhà nước gây thất thoát, lãng phí&nbsp;vào ngày 10/7. Đến ngày 13/7,&nbsp;Cơ quan Cảnh sát điều tra Bộ Công an ra quyết định truy nã bà Thoa.&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'CsKb_hiep-dinh-dan-do-giua-viet-nam-va-phap-co-so-phong-chong-toi-pham-bo-tron-360x240.jpg', 0, 0, 44, '2020-08-15', '2020-08-15'),
(125, 'Dịch bệnh Covid-19, những người nào sẽ dính tội hình sự?', 'dich-benh-covid-19-nhung-nguoi-nao-se-dinh-toi-hinh-su-', '<p>Hội đồng Thẩm phán TAND Tối cao vừa có hướng dẫn áp dụng pháp luật đối với những hành vi vi phạm pháp luật phổ biến, có đủ yếu tố cấu thành tội phạm trong phòng chống dịch bệnh.</p>', '<p>Theo đó, người đã được thông báo mắc bệnh; nghi ngờ mắc bệnh hoặc trở về từ vùng có dịch bệnh Covid-19 đã được thông báo cách ly nhưng thực hiện một trong các hành vi sau đây, gây lây truyền dịch bệnh Covid-19 sẽ bị xử lý theo điều 240 bộ luật Hình sự và bị xử lý về tội Làm lây lan dịch bệnh truyền nhiễm cho người:</p>\r\n\r\n<p>Trốn khỏi nơi cách ly; Không tuân thủ quy định về cách ly; Từ chối, trốn tránh việc áp dụng biện pháp cách ly, cưỡng chế cách ly và không khai báo y tế, khai báo không đầy đủ hoặc khai báo gian dối.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/03/30/18/dich-benh-covid-19-nhung-nguoi-nao-se-dinh-toi-hinh-su.jpg\" title=\"Ảnh minh họa\"><img alt=\"Dịch bệnh Covid-19, những người nào sẽ dính tội hình sự?\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/03/30/18/dich-benh-covid-19-nhung-nguoi-nao-se-dinh-toi-hinh-su.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ảnh minh họa</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Người chưa bị xác định mắc bệnh Covid-19, nhưng sống trong khu vực đã có quyết định cách ly, quyết định phong tỏa mà thực hiện một trong các hành vi sau đây (gây thiệt hại từ 100 triệu đồng trở lên do phát sinh chi phí phòng, chống dịch bệnh) thì bị xử lý về tội Vi phạm quy định về an toàn ở nơi đông người:</p>\r\n\r\n<p>Trốn khỏi khu vực bị cách ly, khu vực bị phong tỏa; Không tuân thủ quy định cách ly; Từ chối, trốn tránh việc áp dụng biện pháp cách ly, cưỡng chế cách ly; Không khai báo y tế, khai báo không đầy đủ hoặc khai báo gian dối.</p>\r\n\r\n<p>Điều này được quy định điều 295 bộ luật Hình sự.</p>\r\n\r\n<p>Chủ cơ sở kinh doanh, người quản lý cơ sở kinh doanh dịch vụ (quán bar, vũ trường, karaoke, dịch vụ mát-xa, cơ sở thẩm mỹ...) vẫn kinh doanh khi đã có quyết định tạm đình chỉ hoạt động kinh doanh để phòng chống dịch bệnh Covid-19 mà gây thiệt hại từ 100 triệu đồng trở lên (do phát sinh chi phí phòng, chống dịch bệnh) sẽ bị xử lý về tội Vi phạm quy định về an toàn ở nơi đông người, theo quy định tại điều 295.</p>\r\n\r\n<p>Người có hành vi đưa lên mạng máy tính, mạng viễn thông thông tin giả mạo, thông tin sai sự thật, thông tin xuyên tạc về tình hình dịch bệnh Covid-19, gây dư luận xấu sẽ bị xử lý về tội Đưa hoặc sử dụng trái phép thông tin mạng máy tính, mạng viễn thông (quy định tại điều 288).</p>\r\n\r\n<p>Người có hành vi đưa trái phép thông tin cá nhân, bí mật đời tư, xúc phạm nghiêm trọng nhân phẩm, danh dự của nhân viên y tế, người tham gia phòng, chống dịch bệnh Covid-19, người mắc bệnh, người nghi ngờ mắc bệnh Covid-19 sẽ bị xử lý về tội Làm nhục người khác (quy định tại điều 155).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người có hành vi lợi dụng dịch bệnh Covid-19 đưa ra thông tin không đúng sự thật về công dụng của thuốc, vật tư y tế về phòng, chống dịch bệnh nhằm chiếm đoạt tài sản của người khác sẽ bị xử lý về tội Lừa đảo chiếm đoạt tài sản (quy định tại điều 174).</p>\r\n\r\n<p>Người có hành vi đã, đang hoặc nhằm đưa trái phép thuốc, vật tư y tế dùng vào việc phòng, chống dịch bệnh Covid-19 ra khỏi biên giới Việt Nam nhằm thu lợi bất chính sẽ bị xử lý về tội Buôn lậu (theo quy định tại điều 188).</p>\r\n\r\n<p>Hành vi đầu cơ sẽ bị xử lý hình sự</p>\r\n\r\n<p>Người có hành vi lợi dụng sự khan hiếm hoặc tạo sự khan hiếm giả tạo trong tình hình dịch bệnh Covid-19 để mua vét hàng hóa đã được cơ quan nhà nước có thẩm quyền công bố là mặt hàng bình ổn giá hoặc hàng hóa được Nhà nước định giá nhằm bán lại để thu lợi bất chính sẽ bị xử lý về tội Đầu cơ (quy định tại điều 196).</p>\r\n\r\n<p>Người có hành vi dùng vũ lực, đe dọa dùng vũ lực, hoặc thủ đoạn khác cản trở người thi hành công vụ trong phòng, chống dịch bệnh Covid-19 thì bị xử lý về tội Chống người thi hành công vụ (quy định tại điều 330).</p>\r\n\r\n<p>Người có trách nhiệm trong phòng, chống dịch bệnh Covid-19, nhưng không triển khai hoặc triển khai không kịp thời, không đầy đủ các biện pháp phòng, chống dịch bệnh theo quy định, gây hậu quả nghiêm trọng thì bị xử lý về tội Thiếu trách nhiệm, gây hậu quả nghiêm trọng (quy định tại điều 360).</p>\r\n\r\n<p>Theo hướng dẫn của Hội đồng thẩm phán TAND Tối cao, trong thời gian có dịch bệnh Covid-19, phải đưa các vụ án liên quan đến phòng, chống dịch bệnh Covid-19 ra xét xử, nhưng bảo đảm quy định về phòng, chống dịch.</p>', 'Ze2j_dich-benh-covid-19-nhung-nguoi-nao-se-dinh-toi-hinh-su-240x160.jpg', 0, 0, 44, '2020-08-15', '2020-08-15');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(126, 'Lời khuyên của luật sư về hợp đồng thuê mặt bằng thời dịch bệnh', 'loi-khuyen-cua-luat-su-ve-hop-dong-thue-mat-bang-thoi-dich-benh', '<h1>Luật sư đưa ra lời khuyên về việc thương thảo hợp đồng thuê mặt bằng khi dịch Covid-19 ảnh hưởng lớn tới hoạt động kinh doanh.&nbsp;</h1>', '<p>Đại dịch Covid-19 khiến nhiều hoạt động kinh doanh bị ảnh hưởng nghiêm trọng. Nhiều nhà hàng, khách sạn, quán bar, karaoke… phải đóng cửa, hoặc hoạt động cầm chừng. Nguồn thu giảm, thậm chí không có nhưng tiền thuê mặt bằng kinh doanh vẫn phải trả.</p>\r\n\r\n<p>Luật sư có thể tư vấn về hợp đồng thuê mặt bằng kinh doanh và hướng giải quyết trong đại dịch Covid-19?</p>\r\n\r\n<p>(Hà Anh Tuấn, Hà Nội)</p>\r\n\r\n<p>Luật sư Nguyễn Đạt, Đoàn Luật sư Hà Nội trả lời:&nbsp;Trước tiên phải xác định dịch Covid-19 có phải là sự kiện bất khả kháng, nó có thể được viện dẫn và áp dụng để miễn trừ trách nhiệm thực hiện nghĩa vụ hay không.</p>\r\n\r\n<p>1. Sự kiện bất khả kháng là gì?</p>\r\n\r\n<p>Theo quy định tại bộ luật Dân sự 2015: “Sự kiện bất khả kháng là sự kiện xảy ra một cách khách quan, không thể lường trước được và không thể khắc phục được, dù đã áp dụng mọi biện pháp cần thiết và khả năng cho phép”.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/03/17/09/nguoi-kinh-doanh-gap-kho-thoi-covid-19-can-lam-gi.jpg\" title=\"Luật sư Nguyễn Đạt\"><img alt=\"Lời khuyên của luật sư về hợp đồng thuê mặt bằng thời dịch bệnh\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/03/17/09/nguoi-kinh-doanh-gap-kho-thoi-covid-19-can-lam-gi.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Luật sư Nguyễn Đạt</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>2. Cần có những điều kiện nào để được áp dụng sự kiện bất khả kháng?</p>\r\n\r\n<p>Thứ nhất cần căn cứ quy định tại hợp đồng đã ký giữa các bên. Đối với những hợp đồng có điều khoản quy định về sự kiện bất khả kháng và hậu quả pháp lý khi xảy ra sự kiện đó thì các bên căn cứ quy định đã thỏa thuận để thực hiện.</p>\r\n\r\n<p>Trường hợp hợp đồng không quy định điều khoản này thì các bên có thể viện dẫn quy định của pháp luật để giải thích và áp dụng.</p>\r\n\r\n<p>Theo đó, để được áp dụng quy định về sự kiện bất khả kháng, cần đáp ứng đủ 3 điều kiện sau: Xảy ra một cách khách quan; Không thể lường trước được; Không thể khắc phục được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với sự kiện đại dịch Covid-19, có thể khẳng định đã thỏa mãn điều kiện là xảy ra một cách khách quan và không thể lường trước được.</p>\r\n\r\n<p>Tuy nhiên, điều kiện không thể khắc phục được thì cần phải được xem xét và phân tích trong các trường hợp cụ thể.</p>\r\n\r\n<p>Điều kiện này được thỏa mãn và Covid-19 được coi là sự kiện bất khả kháng nếu các bên chứng minh được là không thể khắc phục được tình hình, mặc dù đã áp dụng mọi biện pháp cần thiết và trong khả năng của mình.</p>\r\n\r\n<p>Nếu chứng minh được đại dịch Covid-19 thuộc trường hợp bất khả kháng trong trường hợp của mình thì các bên có thể được miễn trừ nghĩa vụ nếu vi phạm hợp đồng theo quy định.</p>\r\n\r\n<p>Theo luật sư, áp dụng quy định về sự kiện bất khả kháng trong hợp đồng thuê mặt bằng kinh doanh như sau: Nếu bên thuê khoán tài sản chứng minh được hoa lợi, lợi tức bị mất ít nhất một phần ba do sự kiện bất khả kháng thì có quyền yêu cầu giảm hoặc miễn tiền thuê, căn cứ quy định tại khoản 3, điều 488 BLDS 2015.</p>\r\n\r\n<p>Luật sư khuyên nên xem xét lại hợp đồng thuê để xác định rõ tình trạng pháp lý làm căn cứ giải quyết.</p>\r\n\r\n<p>Thứ hai, cần thu thập chứng cứ chứng minh là đã áp dụng mọi biện pháp cần thiết và khả năng cho phép nhưng không thể khắc phục được để thỏa mãn điều kiện của sự kiện bất khả kháng.</p>\r\n\r\n<p>Thứ ba, thống kê lại số liệu doanh thu trước khi xảy ra đại dịch và những thiệt hại phải gánh chịu kể từ khi xảy ra đại dịch...</p>', 'YuQy_nguoi-kinh-doanh-gap-kho-thoi-covid-19-can-lam-gi-240x160.jpg', 0, 0, 44, '2020-08-15', '2020-08-15'),
(127, 'Muller: \"Bayern thắng Barca đẹp hơn Đức hạ Brazil 7-1\"', 'muller-bayern-thang-barca-dep-hon-duc-ha-brazil-7-1', '<p>Người hùng Thomas Muller tuyên bố, Bayern Munich thắng Barca 8-2 ấn tượng hơn khi Đức hạ Brazil 7-1 ở World Cup 2014.</p>', '<p>\"Một đêm tuyệt vời! Về mặt kết quả, cảm giác trên sân, và diễn biến của trận đấu\",&nbsp;<a href=\"https://vietnamnet.vn/thomas-muller-tag55997.html\" target=\"_blank\">Thomas Muller</a>&nbsp;phấn khích khi Bayern Munich nghiền nát Barca 8-2, trong trận tứ kết Champions League.</p>\r\n\r\n<p>Muller được bầu chọn là cầu thủ xuất sắc nhất trận tứ kết, trên trang chủ UEFA.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/12/muller-bayern-munich-8-2-barca-3.jpg\" title=\"Muller là nỗi ám ảnh của Barca, được bầu cầu thủ xuất sắc nhất trận\"><img alt=\"Muller: \'Bayern thắng Barca đẹp hơn Đức hạ Brazil 7-1\'\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/12/muller-bayern-munich-8-2-barca-3.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Muller là nỗi ám ảnh của Barca, được bầu cầu thủ xuất sắc nhất trận</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngôi sao 30 tuổi này trực tiếp ghi 2 bàn (trong vòng 31 phút), và có 1 pha kiến tạo. Anh có tổng cộng 6 bàn thắng, trong 5 lần gặp Barca ở Champions League.</p>\r\n\r\n<p>Không một ai khiến Barca bị ám ảnh như Muller trên sân chơi danh giá nhất châu Âu (Andriy Shevchenko xếp sau, với 5 bàn sau 11 trận).</p>\r\n\r\n<p>Khi được hỏi về cảm giác chiến thắng Barca, và trận bán kết World Cup 2014 mà Đức thắng Brazil 7-1, Muller cho rằng màn trình diễn&nbsp;của&nbsp;<a href=\"https://vietnamnet.vn/bayern-munich-tag4520.html\" target=\"_blank\">Bayern Munich</a>&nbsp;ấn tượng hơn.</p>\r\n\r\n<p>\"Vào thời điểm 2014, chúng tôi không hoàn toàn kiểm soát mọi thứ. Kết quả diễn ra một phần vì Brazil tự mắc sai lầm.</p>\r\n\r\n<p>Ở trận này, Bayern Munich buộc Barca phải chơi thứ bóng đá mà chúng tôi muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bayern áp đảo cuộc chơi rõ nét hơn nhiều so với Đức ở Brazil. Chúng tôi thống trị trận đấu một cách tàn bạo, đặc biệt trên khía cạnh bóng bổng\".</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/12/muller-bayern-munich-8-2-barca-2.jpg\" title=\"Muller trở thành cầu thủ người Đức có nhiều trận đấu ở Champions League nhất\"><img alt=\"Muller: \'Bayern thắng Barca đẹp hơn Đức hạ Brazil 7-1\'\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/12/muller-bayern-munich-8-2-barca-2.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Muller trở thành cầu thủ người Đức có nhiều trận đấu ở Champions League nhất</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chiến thắng của Bayern Munich trước Barca càng ngọt ngào hơn với Muller, khi anh trở thành cầu thủ người Đức đá nhiều trận Champions League nhất.</p>\r\n\r\n<p>Muller vừa có trận thứ 113 ở&nbsp;<a href=\"https://vietnamnet.vn/vn/the-thao/bong-da-quoc-te/champions-league/\" target=\"_blank\">Champions League</a>, vượt qua kỷ lục cũ 112 trận của Philipp Lahm.</p>\r\n\r\n<p>\"Nó không liên quan gì đến bóng đá, chỉ là số liệu thống kê\", Muller nói về kỷ lục.</p>\r\n\r\n<p>\"Điều tuyệt vời nhất là chúng tôi cùng chiến đấu, đều thể hiện được động lực như nhau. Quan trọng hơn khi chúng tôi làm được điều mình muốn trên sân cỏ\".</p>\r\n\r\n<p>Muller đang cùng Bayern Munich hướng đến cú ăn ba lần thứ hai trong lịch sử CLB, sau mùa 2012-13. Mùa này, họ đã có Bundesliga và Cúp Đức.</p>', '34D4_muller-bayern-munich-8-2-barca-2-150x100.jpg', 0, 0, 46, '2020-08-15', '2020-08-15'),
(128, 'Sao Man City, Kyle Walker và những scandal tình ái', 'sao-man-city-kyle-walker-va-nhung-scandal-tinh-ai', '<p>Kyle Walker, ngôi sao của Man City, gắn với không ít bê bối ngoại tình và chuyện trăng hoa như trở thành thói quen xấu không thể bỏ.</p>', '<p>Liên tục phản bội bạn gái</p>\r\n\r\n<p>Tháng trước, sau khi Premier League kết thúc và Man City tạm nghỉ ngơi trong lúc chuẩn bị cho sự trở lại của Champions League,&nbsp;<a href=\"https://vietnamnet.vn/kyle-walker-tag92936.html\" target=\"_blank\">Kyle Walker</a>&nbsp;bỏ ra 24.000 bảng thuê máy bay đưa bạn gái Annie Kilner đi du lịch Tây Ban Nha.</p>\r\n\r\n<p>Một chuyến đi với mục đích hàng gắn mối quan hệ giữa hai người. Trước đó, trong tháng 6/2020, Walker từng cầu hôn Annie bằng với một trái tim khổng lồ được kết bằng những bông hoa trắng.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-annie-kilner.JPG\" title=\"Kyle Walker và người yêu Annie Kilner\"><img alt=\"Sao Man City, Kyle Walker và những scandal tình ái\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-annie-kilner.JPG\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kyle Walker&nbsp;và người yêu Annie Kilner</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Kyle Walker và Annie cặp với nhau đã 12 năm nay. Rất nhiều lần mối quan hệ của họ tưởng như đổ vỡ, khi chia tay rồi tái hợp.</p>\r\n\r\n<p>Tất cả đều bởi vì thói trăng hoa của Kyle Walker. Ngôi sao đang khoác áo&nbsp;<a href=\"https://vietnamnet.vn/man-city-tag35500.html\" target=\"_blank\">Man City</a>&nbsp;thường xuyên trốn Annie đi ngoại tình.</p>\r\n\r\n<p><strong>Ngôi sao ngoại tình</strong></p>\r\n\r\n<p>Vụ ngoại tình đình đám của Walker liên quan đến ngôi sao truyền hình Laura Brown.</p>\r\n\r\n<p>Lần đầu tiên gặp nhau vào tháng 9/2018 ở thành phố Manchester, Walker bị Laura hớp hồn.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-annie-laura-brown.jpg\" title=\"Walker trốn Annie cặp với Laura Brown trong 4 tháng\"><img alt=\"Sao Man City, Kyle Walker và những scandal tình ái\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-annie-laura-brown.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Walker&nbsp;trốn Annie&nbsp;cặp với Laura Brown trong 4 tháng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chỉ cần Annie không chú ý, Walker liền tìm đến Laura. Có lần, ngôi sao sinh năm 1990 này biến chiếc xe sang trọng của mình thành \"phòng ngủ di động\" để cùng người đẹp \"vui vẻ\".</p>\r\n\r\n<p>Cuộc tình vụng trôm kéo dài được 4 tháng, trước khi Annie phát hiện và làm ầm ĩ.</p>\r\n\r\n<p>Annie \"đá\" Kyle Walker, tháo nhẫn đính hôn sau sự cố với Laura. Vì 3 đứa con giữa hai người, không lâu sau cô đồng ý cho người bạn trai trở lại bên cạnh.</p>\r\n\r\n<p>Chứng nào tật nấy, Kyle tiếp tục vụng trộm sau lưng Annie. Lần này là bê bối với người mẫu Lauryn Goodman.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đáng chú ý, cựu cầu thủ Tottenham làm cho Annie và Lauryn mang thai gần như trong cùng một thời điểm.</p>\r\n\r\n<p>Một lần nữa Annie đuổi kẻ ngoại tình ra khỏi nhà. Có điều, cái giá cho scandal lần này quá đắt: cô bị sẩy thai.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-lauryn-goodman.jpg\" title=\"Kyle Walker có con với người mẫu Lauryn Goodman\"><img alt=\"Sao Man City, Kyle Walker và những scandal tình ái\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-lauryn-goodman.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kyle Walker&nbsp;có con với người mẫu Lauryn Goodman</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Hồi tháng 4/2020, Lauryn Goodman sinh con với Walker. Nhưng hai người cũng sớm kết thúc mối quan hệ.</p>\r\n\r\n<p>Walker bỏ rơi Lauryn để trở về bên cạnh Annie, để rồi có vụ cầu hôn và chuyến du lịch Tây Ban Nha như đề cập ở trên.</p>\r\n\r\n<p><strong>Giá đắt cho bê bối với gái gọi</strong></p>\r\n\r\n<p>Cũng trong tháng Tư vừa qua, cùng với việc Lauryn sinh con, Kyle Walker dính đến scandal rúng động nước Anh.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-scandal-covid-19.jpg\" title=\"Kyle Walker là nhân vật chính trong scandal gái gọi giữa dịch Covid-19\"><img alt=\"Sao Man City, Kyle Walker và những scandal tình ái\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/19/kyle-walker-scandal-covid-19.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kyle Walker&nbsp;là nhân vật chính trong scandal&nbsp;gái gọi giữa dịch Covid-19</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong lúc cả nước Anh kêu gọi giãn cách xã hội để hạn chế lây nhiễm Covid-19, cầu thủ có giá&nbsp;<a href=\"https://vietnamnet.vn/vn/the-thao/tin-chuyen-nhuong/\" target=\"_blank\">chuyển nhượng</a>&nbsp;50 triệu bảng (từ Tottenham sang Man City, năm 2017), đưa gái gọi Louise McNamara về nhà.</p>\r\n\r\n<p>Louise còn dẫn thêm một \"đồng nghiệp\" người Brazil để phục vụ Walker.</p>\r\n\r\n<p>Kyle Walker hứng chịu nhiều chỉ trích khi sự cố bị phanh phui. Anh sớm lên tiếng xin lỗi gia đình, CLB và người hâm mộ.</p>\r\n\r\n<p>Man City - đội đang mơ vô địch Champions League (trước mắt, phải thắng Lyon ở tứ kết, đá 2h ngày 16/8) - đưa ra án phạt với Walker. Trong khi đó, HLV Gareth Southgate cấm tiệt cầu thủ này lên đội tuyển Anh, bất chấp tầm quan trọng của anh với \"Tam sư\".</p>\r\n\r\n<p>Hiện tại, ngoài việc cố gắng lấy lòng Annie, Walker buộc phải chấp nhận làm khán giả EURO 2020 (đá mùa Hè 2021), cũng như World Cup 2022.</p>', 'Muqn_kyle-walker-annie-laura-brown-2-360x240.jpg', 0, 0, 47, '2020-08-15', '2020-08-15'),
(129, 'Collin Morikawa: Người kế tục Tiger Woods', 'collin-morikawa-nguoi-ke-tuc-tiger-woods', '<p>Collin Morikawa giành PGA Championship ở tuổi 23, với lối đánh hiện đại và được kỳ vọng tạo nên kỷ nguyên mới như Tiger Woods.</p>', '<p><strong>Chiến thắng của tư duy</strong></p>\r\n\r\n<p>\"Hãy vượt qua hình ảnh một vận động viên đơn thuần. Bạn phải trở thành một vận động viên biết biết phản ứng với mục tiêu của mình\",&nbsp;<a href=\"http://vietnamnet.vn/collin-morikawa-tag283669.html\" target=\"_blank\">Collin Morikawa</a>&nbsp;lên tiếng trước khi bước vào PGA Championship 2020.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-pga-championship-2020.jpg\" title=\"Collin Morikawa giành PGA Championship 2020 nhờ thay đổi tư duy\"><img alt=\"Collin Morikawa: Người kế tục Tiger Woods\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-pga-championship-2020.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Collin Morikawa&nbsp;giành PGA Championship 2020 nhờ thay đổi tư duy</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo tâm sự của Morikawa, kể từ sau thất bại ở Colonial (Colonial National Invitation, hay còn được gọi là Charles Schwab Challenge) - giải đấu đánh dấu sự trở lại của golf sau thời gian ngưng trệ vì Covid-19, anh cố gắng thay đổi cách chơi.</p>\r\n\r\n<p>Sự thay đổi của Morikawa không thuộc về kỹ thuật, mà là khía cạnh tư duy.</p>\r\n\r\n<p>\"Bạn nên nhìn vào những môn thể thao khác. Ví dự như bóng rổ. Các cầu thủ bóng rổ luôn nhìn vào mục tiêu của mình, cố gắng phản ứng để đạt điểm\".</p>\r\n\r\n<p>Golf không giống các môn thể thao khác, với nhiều nét rất đặc thù. Đối với một golfer chuyên nghiệp, xử lý những động tác khó trong các môn thể thao khác thực sự không dễ dàng.</p>\r\n\r\n<p>Morikawa cũng vậy. Nhưng anh mượn thể thao đối kháng để thay đổi tư duy của mình.</p>\r\n\r\n<p>Ở&nbsp;<a href=\"http://vietnamnet.vn/pga-championship-tag207493.html\" target=\"_blank\">PGA Championship</a>&nbsp;2020, người ta thấy một Morikawa rất khác. Giới truyền thông quốc tế ấn tượng với màn trình diễn của anh trên sân TPC Harding Park, California (Mỹ).</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-1.jpg\" title=\"Collin Morikawa có động tác uốn cổ tay được đánh giá cao\"><img alt=\"Collin Morikawa: Người kế tục Tiger Woods\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-1.jpg\" style=\"height:433px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Collin&nbsp;Morikawa có động tác uốn cổ tay được đánh giá cao</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vì thế, nhiều hãng truyền thông gọi Collin là \"athlete\" (vận động viên), thay vì \"golfer\" đơn thuần.</p>\r\n\r\n<p><strong>Người sở hữu cú đánh hiện đại</strong></p>\r\n\r\n<p>Với thay đổi về tư duy, Morikawa cho thấy khả năng thích ứng tuyệt vời trước điều kiện thời tiết không dễ chịu ở California.</p>\r\n\r\n<p>Nhờ đó, dù khởi đầu bình thường, nhưng Morikawa bứt phá ngoạn mục ở PGA Championship 2020. Anh đánh bại nhà vô địch 2 năm liên tiếp trước đó là Brooks Koepka, cùng hai golfer kỳ cựu Dustin Johnson và Paul Casey để vô địch.</p>\r\n\r\n<p>Một điểm nổi bật ở Morikawa, khiến cho các HLV và chuyên gia golf mê mẩn là những cú đánh hiện đại.</p>\r\n\r\n<p>Các golfer thực hiện cách vung gậy khác nhau để phù hợp với bản thân. Morikawa thì có động tác uốn cổ tay, giống như Dustin Johnson.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-2.jpg\" title=\"Những cú đánh của Collin Morikawa rất đẹp\"><img alt=\"Collin Morikawa: Người kế tục Tiger Woods\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-2.jpg\" style=\"height:519px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Những cú đánh của Collin&nbsp;Morikawa rất đẹp</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Động tác uốn cổ tay thuận hướng xuống của Morikawa giúp toàn bộ cơ thể uốn dẻo hơn khi thực hiện downswing (nôm na là động tác xoay người đánh gậy, cho đến khi đầu gậy tiếp xúc vào bóng).</p>\r\n\r\n<p>Theo các HLV golf chuyên nghiệp, động tác uốn cong cổ tay tốt sẽ hạn chế tối đa mặt gậy bị xoay. Điều này giúp ổn định mặt gậy, và cú đánh chắc chắn hơn.</p>\r\n\r\n<p>Cổ tay uốn cong giúp ổn định gậy, và cũng cần phải xoay cơ thể hợp lý nhất. Động tác này đòi hỏi thể lực cao, mà nhiều golfer không đạt được.</p>\r\n\r\n<p>Morikawa thì khác. Những pha xoay cơ thể được xem là lợi thế của anh trong cuộc tranh tài ở PGA Championship 2020.</p>\r\n\r\n<p>Kết quả là những cú đánh gần như hoàn hảo, từ vẻ đẹp đến tốc độ và hiệu quả.</p>\r\n\r\n<p><strong>Morikawa sẽ kế tục Tiger Woods?</strong></p>\r\n\r\n<p>23 tuổi, chơi&nbsp;<a href=\"http://vietnamnet.vn/golf-tag25283.html\" target=\"_blank\">golf</a>&nbsp;chuyên nghiệp 1 năm (chính xác là 14 tháng), mới chỉ tham gia 27 Tour, nhưng Morikawa giành được 3 danh hiệu. 1 trong đó là Major - người thứ 4 vô địch giải lớn khi chưa quá 23 tuổi, sau Jack Nicklaus (1963), Tiger Woods (1999) và Rory McIlroy (2012).</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-pga-championship-2020-2.jpg\" title=\"Collin Morikawa có 3 danh hiệu chỉ sau 1 năm chơi chuyên nghiệp\"><img alt=\"Collin Morikawa: Người kế tục Tiger Woods\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/11/11/collin-morikawa-pga-championship-2020-2.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Collin Morikawa có 3 danh hiệu chỉ sau 1 năm chơi chuyên nghiệp</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong 27 sự kiện đã tham dự, cũng chỉ một lần golfer gốc Nhật Bản lỡ cắt. Kết quả này phản ánh sự ổn định tuyệt vời của anh.</p>\r\n\r\n<p>Ở vòng đấu chung kết quyết định ngôi vô địch PGA Championship 2020, Morikawa chỉ cần 64 gậy - kỷ lục trong lịch sử (ngang với Tiger Woods năm 2018). Trước đó, ở vòng 3, anh cũng chỉ cần 65 gậy.</p>\r\n\r\n<p>129 gậy trong 36 lỗ cuối cùng ở một kỳ PGA Championship, Morikawa phá kỷ lục mà Tiger Woods lập năm 2018 - với 130 gậy (66 ở vòng 3, 64 vòng cuối).</p>\r\n\r\n<p>Hành trình chuyên nghiệp của Morikawa mới chỉ vừa&nbsp;rời khỏi&nbsp;vạch xuất phát, nhưng đã tạo hiệu ứng lớn với thế giới golf, nhờ tư duy và cách đánh hiện đại.</p>\r\n\r\n<p>Người hâm mộ đang chờ đợi Morikawa kế tục Tiger Woods, mở ra kỷ nguyên mới cho mình và golf.</p>', 'H9n2_collin-morikawa-pga-championship-2020-360x240.jpg', 0, 0, 48, '2020-08-15', '2020-08-15'),
(130, 'Trực tiếp Man City vs Lyon: Thầy trò Guardiola ra oai', 'truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai', '<p>VietNamNet gửi đến quý độc giả diễn biến vòng tứ kết Champiosn League giữa Man City vs Lyon, diễn ra lúc 2h ngày 16/8.</p>', '<p>Đội hình dự kiến</p>\r\n\r\n<p>Man City:&nbsp;Ederson; Walker, Fernandinho, Laporte, Cancelo; De Bruyne, Rodrigo, Gundogan; Mahrez, Jesus, Sterling.<br />\r\n<br />\r\nLyon:&nbsp;Lopes; Denayer, Marcelo, Marcal; Dubois, Caqueret, Guimaraes, Aouar, Cornet; Dembele, Depay.</p>\r\n\r\n<p>* Liên tục cập nhật và chỉnh sửa</p>\r\n\r\n<p>DIỄN BIẾN</p>\r\n\r\n<p>MỚI NHẤTCŨ NHẤT</p>\r\n\r\n<p>18h30</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-2.jpg\" title=\"Đoàn quân HLV Guardiola khá thoải mái trước trận tứ kết\"><img alt=\"{keywords}\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-2.jpg\" style=\"height:770px; width:770px\" /></a></p>\r\n\r\n			<p><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-1.jpg\" title=\"Đoàn quân HLV Guardiola khá thoải mái trước trận tứ kết\"><img alt=\"{keywords}\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-1.jpg\" style=\"height:770px; width:770px\" /></a></p>\r\n\r\n			<p><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai.jpg\" title=\"Đoàn quân HLV Guardiola khá thoải mái trước trận tứ kết\"><img alt=\"{keywords}\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai.jpg\" style=\"height:531px; width:770px\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đoàn quân HLV Guardiola khá thoải mái trước trận tứ kết</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>18h</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thông tin lực lượng</p>\r\n\r\n<p>Man City:&nbsp;Aguero chấn thương nhưng Mendy đã trở lại sau án treo giò.<br />\r\n<br />\r\nLyon:&nbsp;Đội bóng nước Pháp có được đội hình hùng hậu nhất.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-3.jpg\" title=\"Man City được đánh giá nhỉnh hơn đối thủ\"><img alt=\"{keywords}\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/18/truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-3.jpg\" style=\"height:465px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Man City được đánh giá nhỉnh hơn đối thủ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'yvlI_truc-tiep-man-city-vs-lyon-thay-tro-guardiola-ra-oai-3-360x240.jpg', 0, 0, 49, '2020-08-15', '2020-08-15'),
(131, 'Đối tác Apple lên tiếng về sự cố ống kính camera dành cho iPhone 12', 'doi-tac-apple-len-tieng-ve-su-co-ong-kinh-camera-danh-cho-iphone-12', '<h1>Sau báo cáo của nhà phân tích Ming-Chi Kuo về chất lượng ống kính trang bị cho iPhone 12, hãng cung cấp ống kính camera cho Apple đã lên tiếng.</h1>', '<p>Theo&nbsp;Macrumors, Genius Electronic Optical, đối tác cung cấp ống kính camera cho Apple, đã lên tiếng phủ nhận báo cáo của nhà phân tích Ming-Chi Kuo về vấn đề chất lượng ống kính dành cho iPhone 12.</p>\r\n\r\n<p>Hãng này cho biết quá trình phát triển ống kính camera iPhone 12 vẫn đang diễn ra bình thường, mà không có bất cứ sự cố nào.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/doi-tac-apple-len-tieng-ve-su-co-ong-kinh-camera-danh-cho-iphone-12.jpg\" title=\"Mẫu iPhone 12 của Apple\"><img alt=\"Đối tác Apple lên tiếng về sự cố ống kính camera dành cho iPhone 12\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/14/08/doi-tac-apple-len-tieng-ve-su-co-ong-kinh-camera-danh-cho-iphone-12.jpg\" style=\"height:519px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mẫu&nbsp;iPhone 12 của Apple</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tuần trước, nhà phân tích Ming-Chi Kuo tiết lộ rằng hãng cung cấp ống kính camera cho iPhone, Genius Electronic Optical đã phát hiện ra vấn đề nứt lớp phủ trên ống kính góc rộng dành cho các mẫu ‌iPhone 12‌ 5.4 inch và 6.1 inch.</p>\r\n\r\n<p>Sự cố được cho là xảy ra khi kiểm tra độ bền ống kính camera ở môi trường có nhiệt độ và độ ẩm cao. Tuy nhiên, ông Kuo cho biết vấn đề này không ảnh hưởng đến lịch trình lên kệ của iPhone 12.</p>\r\n\r\n<p>Genius Electronic Optical đã bác bỏ báo cáo này. Hãng này khẳng định mọi hoạt động sản xuất vẫn đang diễn ra bình thường theo nhu cầu của đối tác, theo&nbsp;DigiTimes.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngay cả khi có sự cố,&nbsp;<a href=\"https://vietnamnet.vn/apple-tag2320.html\">Apple</a>&nbsp;vẫn có Largan Precision, một trong các nhà cung cấp ống kính camera khác của Táo khuyết, nên sẽ không ảnh hưởng các mẫu iPhone 12 sắp ra mắt.</p>\r\n\r\n<p>Apple dự kiến ​​sẽ công bố 4 mẫu&nbsp;<a href=\"https://vietnamnet.vn/iphone-12-tag216619.html\">‌iPhone 12‌</a>&nbsp;trong năm nay, bao gồm 1 mẫu 5,4 inch, 2 mẫu 6,1 inch và 1 mẫu 6,7 inch, tất cả đều được trang bị màn hình OLED và kết nối 5G.</p>\r\n\r\n<p>Theo một số nguồn tin rò rỉ, iPhone 12 năm nay có thể lên kệ trễ hơn so với mọi năm do đại dịch&nbsp;Covid-19 bùng phát trên toàn cầu. Việc đóng cửa nhà máy, hạn chế đi lại khiến dây chuyền sản xuất bị đình trệ. Ngoài ra, thế hệ iPhone mới nhất này cũng có những thay đổi thiết kế bên trong vào những phút cuối.</p>', 'txJQ_doi-tac-apple-len-tieng-ve-su-co-ong-kinh-camera-danh-cho-iphone-12-360x240.jpg', 0, 0, 51, '2020-08-15', '2020-08-15'),
(132, 'Bluezone giúp tiết kiệm nhiều nguồn lực phòng chống Covid-19', 'bluezone-giup-tiet-kiem-nhieu-nguon-luc-phong-chong-covid-19', '<p>Nếu được triển khai trên phạm vi đủ rộng, Bluezone sẽ giúp tiết kiệm rất nhiều nguồn lực xã hội và chi phí phòng chống Covid-19, cũng như giúp nền kinh tế khôi phục hoạt động theo trạng thái bình thường mới.</p>', '<p>Theo thông tin từ Cục Tin học hóa (Bộ TT&amp;TT), trong ngày 13/8, từ 17 ca nhiễm và nghi nhiễm Covid-19 có cài ứng dụng Bluezone, Đà Nẵng và Hà Nội đã truy vết được 82 trường hợp F1 và F2 nằm ngoài danh sách truy vết theo cách truyền thống là điều tra lịch trình người bệnh. Ngay sau đó, những trường hợp F1, F2 mới được phát hiện thêm qua Bluezone này đã được các Trung tâm kiểm soát bệnh tật (CDC) của Đà Nẵng và Hà Nội tiến hành điều tra dịch tễ bổ sung.</p>\r\n\r\n<p>Dù mới chỉ chiếm một phần nhỏ trong tổng số các ca cần truy vết, nhưng kết quả này cho thấy Bluezone - giải pháp sử dụng kết nối Bluetooth để lưu lịch sử tiếp xúc trong cộng đồng - thực sự có hiệu quả trong việc phòng chống đại dịch Covid-19.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/08/cai-bluezone-xong-co-phai-khai-bao-y-te-nua-khong.jpg\" title=\"Đến nay, từ ứng dụng Bluezone, các cơ quan chức năng đã truy vết được 82 trường hợp F1 và F2 nằm ngoài danh sách truy vết theo cách truyền thống là điều tra lịch trình người bệnh\"><img alt=\"Bluezone giúp tiết kiệm nhiều nguồn lực phòng chống Covid-19\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/08/cai-bluezone-xong-co-phai-khai-bao-y-te-nua-khong.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đến nay, từ ứng dụng Bluezone, các cơ quan chức năng đã truy vết được 82 trường hợp F1 và F2 nằm ngoài danh sách truy vết theo cách truyền thống là điều tra lịch trình người bệnh</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Về lý thuyết, nếu tỉ lệ sử dụng trong cộng đồng đạt mức từ 60% số lượng smartphone trở lên, ứng dụng này còn có thể giúp xã hội và nền kinh tế duy trì hoạt động ngay cả khi chưa hết dịch Covid-19. Đó là bởi khi hầu hết người dân cùng có ý thức bảo vệ bản thân và cộng đồng, họ sẽ cùng sử dụng&nbsp;<a href=\"https://vietnamnet.vn/bluezone-tag271218.html\">Bluezone</a>&nbsp;trên điện thoại để nhận được cảnh báo sớm nếu chẳng may tiếp xúc với ca F0 hay F1 nào đó. Nhờ vậy, các ca nhiễm mới đều được kiểm soát lịch sử tiếp xúc đầy đủ và chính xác, nên các ca lây nhiễm bị mất dấu trong cộng đồng sẽ được loại trừ dần. Người dân có thể giao tiếp hàng ngày ngoài xã hội mà không phải quá lo lắng về nguy cơ lây nhiễm, cơ quan chức năng cũng không phải áp dụng các biện pháp chống dịch như giãn cách xã hội hay phong tỏa cả một địa phương.</p>\r\n\r\n<p>Bluezone giúp tiết kiệm chi phí phòng chống Covid-19</p>\r\n\r\n<p>Nhờ ứng dụng công nghệ Bluetooth để nhận diện các tiếp xúc gần (dưới 2 mét) và trong thời gian đủ lâu (15 phút), Bluezone giúp truy vết chính xác những người từng tiếp xúc với các ca nhiễm và nghi nghiễm (F0, F1). Đây là công nghệ hiện đang được nhiều quốc gia trên thế giới triển khai để phòng chống&nbsp;<a href=\"https://vietnamnet.vn/covid-19-tag260671.html\">Covid-19</a>.</p>\r\n\r\n<p>Trước đó, để xác định những người có khả năng tiếp xúc với các ca F0, cơ quan chức năng phải dùng biện pháp khoanh vùng qua các trạm phát sóng di động, và các thuê bao trong cùng một ô (cell) với F0 đều được tính là có thể đã tiếp xúc. Tuy nhiên, do độ chính xác của biện pháp này không cao, phạm vi khoanh vùng tại thành thị là 200m, còn ở nông thôn là 400m, nên số trường hợp bị xác định nhầm \"có tiếp xúc với F0\" là rất lớn.</p>\r\n\r\n<p>Một phép tính đơn giản có thể cho thấy sai số xác định nhầm này lớn đến mức nào. Nếu kết nối Bluetooth nhận biết tiếp xúc chính xác trong khoảng cách 2m, còn trạm di động là 200m, sai số đã cao gấp 100 lần. Nhưng khi khoanh vùng người có khả năng tiếp xúc 2m x 2m trên diện tích phủ sóng di động 200m x 200m, sai số sẽ nhân theo tỉ lệ bình phương thành 10.000 lần. Điều này dẫn tới số người trong diện cần điều tra dịch tễ trở nên quá lớn, và hầu hết là chưa từng tiếp xúc với F0 ở khoảng cách dưới 2m.</p>\r\n\r\n<p>Đây cũng là công nghệ đã được sử dụng trong đợt bùng phát Covid-19 vừa qua tại Đà Nẵng. Theo đó, khoảng 800.000 người được xác định có liên quan tới các khu vực phong tỏa. Ngoài ra, khoảng 1,4 triệu người được xác định đã đến Đà Nẵng ở thời điểm này, sau đó tỏa đi các tỉnh. Trong trường hợp mỗi người này tiếp xúc với 10 người khác khi về đến địa phương, sẽ có khoảng 14 triệu người nằm trong diện nghi ngờ do có khả năng lây nhiễm. Con số này khiến rất nhiều tỉnh thành phải tiến hành cách ly người nghi nhiễm, tiến hành xét nghiệm nhiều lần với chi phí tốn kém, gây lãng phí thời gian, của cải vật chất, nguồn lực của cả xã hội.</p>\r\n\r\n<p>Với kịch bản lý tưởng, khi mọi người dân đều sử dụng Bluezone, việc truy vết lịch sử tiếp xúc những ca F0 tại Đà Nẵng sẽ chính xác hơn rất nhiều. Số người trong diện nghi nhiễm cũng sẽ giảm xuống hàng ngàn lần, từ 14 triệu người có thể xuống chỉ còn vài ngàn người. Các tỉnh thành sẽ giảm được rất nhiều nguồn lực của cơ quan chức năng huy động vào quá trình điều tra truy vết dịch tễ, vào cơ sở cách ly, cũng như chi phí xét nghiệm cho hàng chục triệu người.</p>\r\n\r\n<p>Duy trì hoạt động xã hội và nền kinh tế dù còn dịch Covid-19</p>\r\n\r\n<p>Không chỉ giúp tiết kiệm nguồn lực và chi phí khổng lồ cho việc phòng chống dịch, nếu được triển khai đủ rộng, Bluezone còn có vai trò giúp duy trì hoạt động xã hội và nền kinh tế ngay cả khi chưa hết dịch Covid-19.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu sử dụng các biện pháp khoanh vùng dựa vào mạng di động như đã đề cập ở trên, khi số người trong diện nghi nhiễm tại một địa phương trở nên quá lớn, thì giải pháp duy nhất để \"thà xác định nhầm còn hơn bỏ sót\" chính là cách ly \"nội bất xuất, ngoại bất nhập\".</p>\r\n\r\n<p>Sau một thời gian đủ lâu (2-3 tuần), nếu số ca nhiễm không bùng phát và sàng lọc được hết các nguồn lây nhiễm thì địa phương mới tiến hành dỡ cách ly.</p>\r\n\r\n<p>Tuy nhiên, biện pháp này gây thiệt hại rất lớn khi mọi hoạt động kinh tế xã hội bị ngưng trệ, người dân phải ở trong nhà, hạn chế ra đường.</p>\r\n\r\n<p>Hiện tại, Singapore đang là quốc gia triển khai giải pháp phòng chống Covid-19 sử dụng công nghệ Bluetooth hiệu quả nhất trên thế giới, với hơn 45% dân số cài đặt ứng dụng có tên TraceTogether trên smartphone.</p>\r\n\r\n<p>Tương tự như Bluezone, TraceTogether giúp người dân Singapore ghi nhớ họ đã đi đến những đâu, đã tiếp xúc với ai trong 25 ngày gần nhất. Nếu chẳng may từng tiếp xúc với một người dùng TraceTogether khác được xác định là ca nhiễm hoặc nghi nhiễm Covid-19, họ sẽ được cảnh báo lập tức để giảm thiểu khả năng vô tình lây nhiễm tiếp cho người thân hoặc bạn bè.</p>\r\n\r\n<p>Nhờ vậy, dù còn các quy định hạn chế về số lượng người tập trung cùng nhau, nhưng đời sống hàng ngày tại Singapore vẫn diễn ra khá bình thường.</p>\r\n\r\n<p>Nếu Việt Nam triển khai được ứng dụng Bluezone với tỉ lệ cao như Singapore, chúng ta hoàn toàn có thể nghĩ tới khả năng khôi phục dịch vụ du lịch tại Đà Nẵng, nền kinh tế phục hồi hoạt động, xã hội vận hành theo trạng thái bình thường mới một cách an toàn, ngăn chặn dịch bệnh Covid-19 hiệu quả.</p>\r\n\r\n<p>Tuy nhiên, để có được tỉ lệ triển khai Bluezone cao như TraceTogether của Singapore, điều quan trọng nhất vẫn chính là ý thức và trách nhiệm của từng người dân trong việc phòng chống dịch bệnh.</p>\r\n\r\n<p>Ý thức ở đây bao gồm cả ý thức bảo vệ sức khỏe bản thân, sức khỏe người thân và sức khỏe cả cộng đồng. Trách nhiệm thể hiện ở việc mọi người dân sử dụng Bluezone nghiêm túc và hiệu quả để chống dịch thực sự, tiết kiệm nguồn lực và của cải vật chất cho xã hội, chứ không chỉ là cài đặt lên smartphone rồi tắt Bluetooth đi vì lý do tốn pin.</p>', 'DlPS_cai-bluezone-xong-co-phai-khai-bao-y-te-nua-khong-360x240.jpg', 0, 0, 52, '2020-08-15', '2020-08-15'),
(133, 'Gia hạn thử nghiệm 5G cho Viettel, VNPT và MobiFone', 'gia-han-thu-nghiem-5g-cho-viettel-vnpt-va-mobifone', '<h2>Cục Viễn thông cho biết, giấy phép cũ của VNPT đã hết&nbsp;hạn, đang làm thủ tục gia hạn mới. Tập đoàn Viettel và MobiFone được gia hạn giấy phép thử nghiệm 5G đến tháng 1/2021 và tháng 5/2021.</h2>', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://ict-imgs.vgcloud.vn/2020/08/14/09/bo-tt.jpg\" title=\"Cục Viễn thông cho biết, Viettel và MobiFone đã được gia hạn giấy phép thử nghiệm 5G đến tháng 1/2021 và tháng 5/2021.\"><img alt=\"Gia hạn thử nghiệm 5G cho Viettel, VNPT và MobiFone\" src=\"https://ict-imgs.vgcloud.vn/2020/08/14/09/bo-tt.jpg\" style=\"height:616px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cục Viễn thông cho biết, Viettel và MobiFone đã được gia hạn giấy phép thử nghiệm 5G đến tháng 1/2021 và tháng 5/2021.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đại diện Cục Viễn thông cho biết, trong năm 2019, Bộ TT&amp;TT đã cấp phép thử nghiệm&nbsp;<a href=\"https://ictnews.vietnamnet.vn/tim-kiem/?k=5G\">mạng 5G&nbsp;</a>cho 3 doanh nghiệp gồm: Viettel, VNPT và MobiFone tại 4 tỉnh thành Hà Nội, TP Hồ Chí Minh, Đà Nẵng và Hải Phòng. Doanh nghiệp được cấp phép thử nghiệm đối với các thuê bao nội bộ để đánh giá tính năng kỹ thuật và khả năng triển khai cơ sở hạ tầng viễn thông sử dụng công nghệ mới. Đến nay, cả 3 doanh nghiệp đều đã triển khai thực tế thử nghiệm 5G tại các địa bàn được cấp phép.</p>\r\n\r\n<p>Cục Viễn thông cho biết, thời hạn giấy phép thử nghiệm cũ của VNPT đã hết và doanh nghiệp này đang làm thủ tục gia hạn mới. Trước đó, Viettel và MobiFone đã được gia hạn giấy phép thử nghiệm đến tháng 1/2021 và tháng 5/2021.</p>\r\n\r\n<p>Theo báo cáo của Hiệp hội các nhà cung cấp di động toàn cầu (GSA), tính đến cuối tháng 5/2020, có 386 nhà mạng tại 125 quốc gia tuyên bố đầu tư phát triển mạng 5G với 81 nhà mạng đã triển khai ít nhất một dịch vụ 5G; 73 nhà mạng tại 38 quốc gia/vùng lãnh thổ triển khai công nghệ di động 5G và 39 nhà mạng tại 24 quốc gia/vùng lãnh thổ triển khai công nghệ cố định băng rộng vô tuyến.</p>\r\n\r\n<p>Hiện số lượng thiết bị đầu cuối 5G được công bố tiếp tục tăng nhanh và tương đối sẵn sàng cho 5G thương mại. Đến nay, có 81 nhà cung cấp thiết bị đã công bố các thiết bị đầu cuối 5G có sẵn hoặc chuẩn bị ra mắt và 283 thiết bị đầu cuối được công bố. Trong đó, có 108 mẫu điện thoại (ít nhất 95 mẫu điện thoại có sẵn trên thị trường), 79 thiết bị CPE (trong nhà và ngoài trời), 47 module, 05 laptop, 19 hotspot, 20 thiết bị khác bao gồm (03 robot, 02 bộ định tuyến IoT, 02 máy bay không người lái…). Các nhà cung cấp thiết bị bao gồm Huawei, Mediatek, Qualcomm và Samsung đã công bố những mẫu Chipset mới dành cho 5G.</p>\r\n\r\n<p>Tại Việt Nam, theo thống kê của Viettel thì số lượng thiết bị đầu cuối 5G có rất ít, 2 thành phố lớn là Hà Nội, TP Hồ Chí Minh mới có khoảng 2.000 thiết bị/thành phố.</p>\r\n\r\n<p>Để thúc đẩy các doanh nghiệp viễn thông tiếp tục thử nghiệm hiệu quả và sớm cung cấp thử nghiệm thương mại, Cục Viễn thông đề nghị các Sở TT&amp;TT căn cứ điều kiện kinh tế - xã hội tại địa bàn, phối hợp với các doanh nghiệp viễn thông để đưa địa phương mình vào kế hoạch thử nghiệm kỹ thuật, thử nghiệm thương mại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cục Viễn thông cho biết, do đặc điểm kỹ thuật nên không thể phủ sóng 5G như 4G mà các địa phương cần phối hợp với các nhà mạng xem xét ưu tiên phủ 5G ở những địa điểm nào trước. Đây là căn cứ để Bộ TT&amp;TT cấp phép thử nghiệm cho doanh nghiệp. Hiện Bộ TT&amp;TT đã sẵn sàng bố trí tài nguyên và cấp phép cho các doanh nghiệp thử nghiệm 5G.</p>\r\n\r\n<p>Bên cạnh đó, Cục Viễn thông yêu cầu các doanh nghiệp phối hợp với các Sở TT&amp;TT triển khai việc dùng chung cơ sở hạ tầng viễn thông và xây dựng kế hoạch về dùng chung cơ sở hạ tầng kỹ thuật liên ngành (điện, nước, giao thông, chiếu sáng đô thị …) do công nghệ 5G đòi hỏi số trạm phát sóng lớn hơn rất nhiều lần so với các công nghệ thế hệ trước. Theo ông Lê Văn Tuấn, Phó Cục trưởng Cục Viễn thông, khi thiết lập mạng 5G, các nhà mạng phải thiết kế lại mạng cáp truyền dẫn nên cần sự hỗ trợ của các Sở TT&amp;TT.</p>\r\n\r\n<p>“Cục Viễn thông sẽ phối hợp với các đơn vị liên quan kiểm tra thực tế và đánh giá kết quả thử nghiệm kỹ thuật của doanh nghiệp. Tiếp tục nghiên cứu việc chuẩn hóa công nghệ, xu hướng triển khai 5G trên thế giới để xây dựng điều kiện cấp phép và lộ trình triển khai 5G thương mại phù hợp đáp ứng sự phát triển của xã hội”, đại diện Cục Viễn thông nói.</p>\r\n\r\n<p>Bộ TT&amp;TT cho rằng, hạ tầng viễn thông đang có sự chuyển dịch quan trọng từ hạ tầng tần số làm nền tảng nền kinh tế trở thành hạ tầng nền tảng quốc gia quan trọng. Triển khai 5G tại Việt Nam đang thuận lợi khi các doanh nghiệp điện tử viễn thông Việt Nam đã chủ động sản xuất nhiều thiết bị thông tin, hạ tầng viễn thông, trong khi trước đây chúng ta phải đi mua, phụ thuộc vào các nhà sản xuất thiết bị nước ngoài về tần số. Mục tiêu đến tháng 10/2020, Việt Nam sẽ tiến hành thương mại 5G bằng 100% thiết bị trong nước. Đây là một bước tiến quan trọng để vừa phát triển kinh tế - xã hội vừa đảm bảo tốt về an ninh - quốc phòng.</p>', 'dXjG_bo-tt-360x240.jpg', 0, 0, 53, '2020-08-15', '2020-08-15'),
(134, 'Chiêu “hút máu\" bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz', 'chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz', '<p>Chỉ bằng những lời hô hào lãi khủng, ứng dụng huy động vốn đa cấp My Aladdinz đang làm mưa làm gió tại Việt Nam dù chẳng hề tạo ra giá trị hay có tư cách pháp nhân chính thức.&nbsp;</p>', '<p>Ứng dụng thanh toán đa cấp My Aladdinz là gì?</p>\r\n\r\n<p>Thời gian gần đây, trên các diễn đàn, mạng xã hội đã xuất hiện không ít bài viết, video quảng cáo, thậm chí tổ chức các buổi hội thảo để giới thiệu về ứng dụng (app) thanh toán hộ có tên My Aladdinz.&nbsp;</p>\r\n\r\n<p>MyAladdinz được giới thiệu là sản phẩm của Success Resources (Singapore) - một công ty chuyên tổ chức các sự kiện đào tạo quốc tế quy mô lớn với sự tham gia của những diễn giả nổi tiếng.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz.jpg\" title=\"Giao diện với đồ họa &quot;cổ lỗ sĩ&quot; của ứng dụng My Aladdinz. Ảnh: Trọng Đạt\"><img alt=\"Chiêu “hút máu\' bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Giao diện với đồ họa \"cổ lỗ sĩ\" của ứng dụng My Aladdinz. Ảnh: Trọng Đạt</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chức năng của ứng dụng My Aladdinz là giúp thanh toán các hóa đơn điện, nước, học phí, bảo hiểm, chi phí sinh hoạt, trả góp ngân hàng, mua nhà, mua xe… với khả năng hoàn tiền lên đến 80% giá trị hóa đơn mua hàng.&nbsp;</p>\r\n\r\n<p>Người dùng thanh toán càng nhiều thông qua ứng dụng My Aladdinz sẽ được hoàn trả càng nhiều tiền hơn. Chính vì vậy, My Aladdinz đã thu hút được một lượng đông đảo thành viên tham gia sử dụng.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-1.jpg\" title=\"My Aladdinz được giới thiệu là một ứng dụng thanh toán với khả năng hoàn tiền cao cho người sử dụng. Ảnh: Trọng Đạt\"><img alt=\"Chiêu “hút máu\' bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-1.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>My Aladdinz được giới thiệu là một ứng dụng thanh toán với khả năng hoàn tiền cao cho người sử dụng. Ảnh: Trọng Đạt</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Để là thành viên của My Aladdinz, người dùng phải đăng ký tài khoản, điền các thông tin cá nhân và thông tin về người giới thiệu, đồng thời nạp vào tài khoản của mình số tiền ít nhất 100 USD (khoảng 2,4 triệu đồng). Số tiền khi nạp vào tài khoản được quy đổi thành “gem” (đơn vị thanh toán trong ứng dụng). Mỗi “gem” có giá trị tương ứng với 1 USD.</p>\r\n\r\n<p>Với hệ thống đại lý, họ sẽ phải bỏ tiền mua 300 “gem” với giá 300 USD (khoảng 7 triệu đồng). Đổi lại, chủ cửa hàng có quyền đăng ký nhiều dịch vụ như thu hộ điện nước, bán thẻ cào điện thoại, thanh toán hộ lãi vay tiêu dùng, học phí, viện phí…</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/my-aladdinz.jpg\" title=\"Để thu hút người dùng, My Aladdinz sử dụng hệ thống phân cấp 15 bậc với mức trả hoa hồng cao cho những người tạo dựng hệ thống.  \"><img alt=\"Chiêu “hút máu\' bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/my-aladdinz.jpg\" style=\"height:434px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Để thu hút người dùng, My Aladdinz sử dụng hệ thống phân cấp 15 bậc với mức trả hoa hồng cao cho những người tạo dựng hệ thống.&nbsp;&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo tìm hiểu của&nbsp;VietNamNet, My Aladdinz chia mức thưởng hoa hồng theo tỷ lệ phần trăm với tối đa 15 cấp bậc. Nếu lôi kéo được nhiều người khác cùng sử dụng, đầu tư vào My Aladdin, người dùng sẽ được chiết khấu hoa hồng theo từng cấp tương tự&nbsp;<a href=\"https://vietnamnet.vn/vn/cong-nghe/bao-mat/mo-mat-vi-lai-khung-nha-dau-tu-viet-bi-hut-mau-boi-tien-ao-da-cap-661737.html\" target=\"_blank\">mô hình đa cấp</a>…</p>\r\n\r\n<p>Mô hình này có tỷ lệ ăn chia hoa hồng rất cao. Bởi vậy, không ít hội nhóm đa cấp đã tích cực quảng cáo, “tô hồng” cho My Aladdinz và lôi kéo người dùng tham gia dù ứng dụng này chẳng hề tạo ra giá trị.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rủi ro mất tiền lớn khi đầu tư vào My Aladdinz</p>\r\n\r\n<p>Ngoài hình thức thanh toán bằng “gem”, My Aladdinz còn dụ dỗ người tham gia đầu tư bằng cách cho ứng dụng này vay số “gem” họ có. Đổi lại, My Aladdinz sẽ trả lãi từ 0,1-0,2% mỗi ngày. Số tiền này được app chi trả liên tục cho tới khi người dùng nhận tổng số gem bằng 5 lần mức đã cho vay trước đó.&nbsp;</p>\r\n\r\n<p>Thực tế cho thấy, khi trải nghiệm thử My Aladdinz, ứng dụng này sở hữu các tính năng tương tự như một ví điện tử. Trong khi đó, các chức năng khác không thể sử dụng bởi không được đầu tư phát triển. Do vậy, My Aladdinz không tạo ra giá trị nào để có thể mang tới mức lãi suất khủng như những gì đã quảng cáo.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-6.jpg\" title=\"Những khóa học của Success Resources được rao bán với giá của nghìn USD trên ứng dụng My Aladdinz. Ảnh: Trọng Đạt\"><img alt=\"Chiêu “hút máu\' bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-6.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Những khóa học của&nbsp;Success Resources được rao bán với giá của nghìn USD trên ứng dụng My Aladdinz. Ảnh: Trọng Đạt</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tuy được giới thiệu là ứng dụng quốc tế, My Aladdinz không hề có tư cách pháp nhân hay văn phòng đại diện tại Việt Nam. Điều này mang tới những rủi ro rất lớn cho người dùng My Aladdinz, nhất là khi họ mang tiền thật của mình để đổi lấy tiền ảo trong khi khả năng thanh khoản của đồng “gem” do My Aladdinz cung cấp không thực sự rõ ràng.&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-7.jpg\" title=\"Hệ thống gian hàng nghèo nàn, thường xuyên bị treo cho thấy sự thiếu đầu tư nghiêm túc vào sản phẩm của My Aladdinz. Vì vậy, mô hình kinh doanh của ứng dụng này khó có thể tìm được lợi nhuận để trả lãi suất cao cho những người đầu tư như những gì đã hứa hẹn. Ảnh: Trọng Đạt\"><img alt=\"Chiêu “hút máu\' bằng lãi khủng của ứng dụng thanh toán đa cấp My Aladdinz\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/13/14/chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-7.jpg\" style=\"height:513px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hệ thống gian hàng nghèo nàn, thường xuyên bị treo cho thấy sự thiếu đầu tư nghiêm túc vào sản phẩm của My Aladdinz. Vì vậy, mô hình kinh doanh của ứng dụng này khó có thể tìm được lợi nhuận để trả lãi suất cao cho những người đầu tư như những gì đã hứa hẹn. Ảnh: Trọng Đạt</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Lo ngại này chính thức được khẳng định khi mới đây, Công an tỉnh Bình Phước cũng đã ra văn bản cảnh báo người dân khi nạp tiền vào ứng dụng My Aladdinz.&nbsp;</p>\r\n\r\n<p>Theo Công an tỉnh Bình Phước, về bản chất, ứng dụng My Aladdinz đang hoạt động theo mô hình đa cấp. My Aladdinz lấy tiền của người sau trả cho người trước, và khi không có người mua “gem\", hệ thống sẽ sụp đổ do mất khả năng thanh khoản. Lúc này, những người sử dụng My Aladdinz để kiếm lãi sẽ khó có thể lấy lại số tiền mà họ đã đầu tư.&nbsp;</p>\r\n\r\n<p>Mặt khác, ứng dụng My Aladdinz chưa được các cơ quan Nhà nước có thẩm quyền cấp phép. Do đó, việc người dân đăng ký tham gia ứng dụng có thể dẫn đến mất tiền hoặc bị đánh cắp dữ liệu cá nhân để sử dụng vào các mục đích bất hợp pháp.&nbsp;</p>\r\n\r\n<p>Trước những rủi ro rất lớn bởi hình thức huy động vốn đa cấp của My Aladdinz, người dân cần thực sự cảnh giác và cân nhắc kỹ lưỡng khi tham gia đầu tư vào ứng dụng này.&nbsp;</p>', 'wj9Y_chieu-hut-mau-bang-lai-khung-cua-ung-dung-thanh-toan-da-cap-my-aladdinz-360x240.jpg', 0, 0, 54, '2020-08-15', '2020-08-15');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(135, 'Bốn dấu hiệu khi thức dậy giúp phát hiện sớm ung thư gan', 'bon-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan', '<p>Những bệnh về gan luôn trở nên nặng hơn một cách âm thầm và khó phát hiện, vì thế, bác sĩ khuyên chúng ta đi kiểm tra sức khỏe khi gặp phải 4 triệu chứng dưới đây.</p>', '<p>Gan là một cơ quan hoạt động âm thầm trong cơ thể và không có dây thần kinh ngoại biên. Vì thế, các triệu chứng ban đầu của ung thư gan cũng biểu hiện rất muộn và không rõ ràng. Các chuyên gia từng gọi gan là bộ phận chỉ biết “kêu cứu trong im lặng”.</p>\r\n\r\n<p>Nhiều bệnh nhân&nbsp;<a href=\"https://vietnamnet.vn/ung-thu-gan-tag62587.html\">ung thư gan</a>&nbsp;phát triển từ bệnh gan mạn tính. Các triệu chứng của bệnh gan mạn tính dễ nhầm lẫn với ung thư gan, do đó, tỷ lệ chẩn đoán sớm ung thư gan trên lâm sàng không cao.</p>\r\n\r\n<p>Điều này thể hiện rõ nhất khi gần 80% bệnh nhân ung thư gan bắt đầu xâm nhiễm và di căn đến các mô xung quanh khi đi khám. Thậm chí, có một số bệnh nhân đã bỏ lỡ cơ hội phẫu thuật và chỉ có thể kéo dài sự sống thông qua phương pháp xạ trị và hóa trị.</p>\r\n\r\n<p>Dù vậy,&nbsp;vẫn có những dấu hiệu&nbsp;để nhận biết ung thư gan. Nếu có các triệu chứng sau đây khi thức dậy vào buổi sáng, bạn hãy cẩn thận với bệnh ung thư gan và nên đi khám càng sớm càng tốt:</p>\r\n\r\n<p>1. Chán ăn vào buổi sáng</p>\r\n\r\n<p><img alt=\"Bốn dấu hiệu khi thức dậy giúp phát hiện sớm ung thư gan\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/10/4-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan.jpg\" style=\"height:578px; width:770px\" /></p>\r\n\r\n<p>Sau một đêm tiêu hóa và hấp thụ, dạ dày trở nên trống rỗng nên khi thức dậy và hoạt động, chúng ta thường có cảm giác đói. Tuy nhiên, ở bệnh nhân ung thư gan, chính gan sẽ phá hủy chức năng tiết mật, tổng hợp và dự trữ dinh dưỡng của gan, đồng thời tăng áp lực tĩnh mạch cửa, dẫn đến niêm mạc đường tiêu hóa xung huyết nhiều.&nbsp;</p>\r\n\r\n<p>Vì vậy, bệnh nhân ung thư gan không chỉ chán ăn mà còn có các biểu hiện như: chướng bụng, buồn nôn, váng đầu, nôn mửa, tiêu chảy. Suy giảm chức năng gan càng phát triển nặng thì các triệu chứng về đường tiêu hóa càng rõ ràng.</p>\r\n\r\n<p>2. Mệt mỏi dai dẳng</p>\r\n\r\n<p><img alt=\"Bốn dấu hiệu khi thức dậy giúp phát hiện sớm ung thư gan\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/10/4-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan-1.jpg\" style=\"height:548px; width:770px\" /></p>\r\n\r\n<p>Đối với người lao động trí óc, những người thức khuya lâu, buổi sáng ngủ dậy mệt mỏi, buồn ngủ là biểu hiện sinh lý bình thường. Sau một vài ngày nghỉ ngơi, điều chỉnh, tình trạng mệt mỏi này sẽ nhanh chóng biến mất.</p>\r\n\r\n<p>Tuy nhiên, nếu đột nhiên cảm thấy mệt mỏi dai dẳng, chóng mặt và các triệu chứng diễn ra không dứt, bạn nên cảnh giác với sự xuất hiện của ung thư gan.</p>\r\n\r\n<p>Như đã nói ở trên, ung thư gan có thể gây ra những bất thường về hệ tiêu hóa và làm suy yếu khả năng tiêu hóa thức ăn của người bệnh, khiến cơ thể rơi vào tình trạng suy dinh dưỡng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi các cơ và mô khác nhau thiếu nguồn cung cấp dinh dưỡng, các biểu hiện mệt mỏi sẽ tự nhiên xảy ra. Đồng thời, bị ảnh hưởng do thiếu dinh dưỡng, cân nặng của bệnh nhân cũng sẽ giảm nhanh chóng trong một thời gian ngắn.</p>\r\n\r\n<p>3. Màu nước tiểu bất thường</p>\r\n\r\n<p><img alt=\"Bốn dấu hiệu khi thức dậy giúp phát hiện sớm ung thư gan\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/10/4-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan-2.jpg\" style=\"height:512px; width:770px\" /></p>\r\n\r\n<p>Nếu sau khi ngủ dậy, nước tiểu có màu vàng bất thường, đôi khi có mùi tanh nồng thì đây có thể là triệu chứng của ung thư gan. Thông thường sau một đêm, nồng độ nước tiểu quá cao, tích trữ lâu trong bàng quang sẽ khiến nước tiểu buổi sáng chuyển sang màu vàng sậm. Sau khi ăn uống bình thường, màu sắc của nước tiểu sẽ tự nhiên thay đổi về màu vàng nhạt.&nbsp;</p>\r\n\r\n<p>Nhưng trong một thời gian dài mà nước tiểu luôn có màu vàng ngả nâu, thậm chí là màu trà đậm, thì đây là dấu hiệu có thể liên quan đến ung thư gan, đặc biệt khi bạn có thêm biểu hiện vàng mắt và vàng da.</p>\r\n\r\n<p>Ung thư gan phá hủy khả năng chuyển hóa bilirubin và bài tiết mật của gan, do đó tạo ra bilirubin, chúng tích tụ gây vàng da, tròng mắt và đổi màu nước tiểu.</p>\r\n\r\n<p>4. Chảy máu nướu khi đánh răng</p>\r\n\r\n<p><img alt=\"Bốn dấu hiệu khi thức dậy giúp phát hiện sớm ung thư gan\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/15/11/4-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan.jpg\" style=\"height:578px; width:770px\" /></p>\r\n\r\n<p>Trong hầu hết các trường hợp, chảy máu nướu có liên quan đến bệnh răng miệng, do bàn chải đánh răng quá cứng và chải mạnh.&nbsp;</p>\r\n\r\n<p>Nhưng nếu bạn liên tục bị chảy máu nướu mà không phải từ các nguyên nhân trên, có thể là biểu hiện của rối loạn đông máu, liên quan tới ung thư gan.</p>\r\n\r\n<p>Gan đóng vai trò quan trọng trong chức năng đông máu của cơ thể, chịu trách nhiệm sản xuất các yếu tố đông máu. Khi các yếu tố đông máu không được sản xuất bình thường, các mao mạch bề mặt có thể bị vỡ và xuất huyết, gây ra hiện tượng chảy máu thường xuyên một cách bất thường</p>', 'CW2Y_4-dau-hieu-khi-thuc-day-giup-phat-hien-som-ung-thu-gan-3-360x240.jpg', 0, 0, 57, '2020-08-15', '2020-08-15'),
(136, 'Hàng trăm người ‘góp ô’ xây thư viện tóc giả cho bệnh nhân ung thư', 'hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu', '<p>Gần đây, bên cạnh những thông tin cập nhật về tình hình Covid-19, một chiến dịch có tên gọi “Sớm bảo vệ, Tự tin sống” góp ô xây dựng thư viện tóc giả cho các bệnh nhân ung thư đang nhận được sự hưởng ứng nồng nhiệt từ cộng đồng.</p>', '<p>Chiến dịch “Sớm bảo vệ, Tự tin sống” do Vietcombank, FWD và Mạng lưới ung thư vú Việt Nam phối hợp triển khai nhằm đưa ra cảnh báo về tình trạng trẻ hóa ung thư tại Việt Nam, giúp mọi người nhận thức được tầm quan trọng của việc “sớm bảo vệ” bằng cách chuẩn bị cho mình một nguồn tài chính kịp thời trong trường hợp không may mắc bệnh. Chiến dịch đồng thời hướng đến tiếp thêm sự tự tin cho các bệnh nhân ung thư bằng cách chung tay xây dựng thư viện tóc giả.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu.jpg\" title=\"Các đại sứ của chiến dịch “Sớm bảo vệ, Tự tin sống” giúp cảnh báo tình trạng trẻ hóa bệnh ung thư tại Việt Nam\"><img alt=\"Hàng trăm người ‘góp ô’ xây thư viện tóc giả cho bệnh nhân ung thư\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu.jpg\" style=\"height:729px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Các đại sứ của chiến dịch “Sớm bảo vệ, Tự tin sống” giúp cảnh báo tình trạng trẻ hóa bệnh ung thư tại Việt Nam</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Với hình ảnh biểu tượng là chiếc ô, tượng trưng cho sự bảo vệ và sẻ chia cùng những đại sứ là những chiến binh ung thư trẻ tuổi gồm Lê Hồ Ngọc Diễm, Trần Bảo Ngọc và Triệu Thị Thanh Trúc, chiến dịch “Sớm bảo vệ, Tự tin sống” đã nhận được hàng trăm chia sẻ trên mạng xã hội, một hành động thể hiện sự thấu hiểu và chia sẻ của cộng động dành cho bệnh nhân ung thư.</p>\r\n\r\n<p>Sự ủng hộ đặc biệt có ý nghĩa vì cứ mỗi chia sẻ ở chế độ công khai trên Facebook cá nhân hình ảnh hoặc tranh vẽ chiếc ô - biểu tượng của chiến dịch, viết thêm lời nhắn, lời chúc đến các bệnh nhân ung thư kèm 4 hashtag: #SớmBảoVệ #TựTinSống #VCBFWD #BCNV, thư viện tóc giả cho các bệnh nhân ung thư sẽ nhận được số tiền hỗ trợ là 9.900 đồng.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu-1.jpg\" title=\"Nhiều người đã tham gia chiến dịch “Sớm bảo vệ, Tự tin sống”\"><img alt=\"Hàng trăm người ‘góp ô’ xây thư viện tóc giả cho bệnh nhân ung thư\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu-1.jpg\" style=\"height:425px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nhiều người đã tham gia chiến dịch “Sớm bảo vệ, Tự tin sống”</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Với nhiều bệnh nhân ung thư, việc mất đi mái tóc trong quá trình xạ trị khiến họ cảm thấy tuyệt vọng, đánh mất sự tự tin của chính mình. Việc thu mình lại và ít tiếp xúc với bên ngoài ảnh hưởng lớn đến tinh thần và sức khỏe của bệnh nhân ung thư trong quá trình điều trị bệnh. Do đó, hành động lan tỏa thông điệp ý nghĩa của chiến dịch sẽ mang đến những giá trị không chỉ về tinh thần mà còn về vật chất cho những bệnh nhân ung thư có hoàn cảnh khó khăn trên cả nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu-2.jpg\" title=\"Không chỉ với người lớn, nhiều em nhỏ cũng đã tham gia lan tỏa tinh thần “Sớm bảo vệ, Tự tin sống”\"><img alt=\"Hàng trăm người ‘góp ô’ xây thư viện tóc giả cho bệnh nhân ung thư\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/08/06/15/hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu-2.jpg\" style=\"height:409px; width:770px\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Không chỉ với người lớn, nhiều em nhỏ cũng đã tham gia lan tỏa tinh thần “Sớm bảo vệ, Tự tin sống”</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bên cạnh đó, khi mua FWD Bảo hiểm bệnh ung thư trên trang thương mại điện tử iFWD-Vietcombank (http://bit.ly/FWDVCB) trước ngày 20/8/2020, chiến dịch “Sớm bảo vệ, Tự tin sống” sẽ thay bạn đóng góp 99.000 đồng vào quỹ xây dựng thư viện tóc giả cho các bệnh nhân ung thư.&nbsp;</p>\r\n\r\n<p>Như vậy, ngoài mục đích ý nghĩa là giúp lan tỏa sự tự tin đến với bệnh nhân ung thư, việc tham gia FWD Bảo hiểm bệnh ung thư còn là cơ hội để tất cả mọi người có thể chủ động về mặt tài chính, an tâm về mặt tinh thần để chiến đấu và chiến thắng bệnh ung thư trong trường hợp không may mắc bệnh.</p>\r\n\r\n<p>Với mức phí chỉ từ 99.000 đồng/mỗi năm, FWD Bảo hiểm bệnh ung thư chi trả 100% số tiền bảo hiểm ngay khi phát hiện bệnh ung thư cho dù bệnh đang ở giai đoạn nào với quyền lợi nhận được lên đến 500 triệu đồng.</p>\r\n\r\n<p>Đặc biệt, FWD Bảo hiểm bệnh ung thư có thể mua hoàn toàn online với chỉ 3 bước đơn giản và chưa đến 3 phút thao tác trên trang thương mại điện tử iFWD-Vietcombank.</p>', '3hfv_hang-tram-nguoi-gop-o-xay-thu-vien-toc-gia-cho-benh-nhan-ung-thu-3-240x160.jpg', 0, 0, 58, '2020-08-15', '2020-08-15'),
(137, 'Lời khuyên ngược đời để có sức khỏe tốt của bác sĩ Nhật', 'loi-khuyen-nguoc-doi-de-co-suc-khoe-tot-cua-bac-si-nhat', '<p>Nếu muốn khỏe mạnh, bạn không nên đi chạy, không dùng thuốc ngay khi ốm hay cố gắng làm người tốt.&nbsp;</p>', '<p>Phải mất rất nhiều thời gian và nỗ lực để có một thói quen tốt cho&nbsp;<a href=\"https://vietnamnet.vn/vn/suc-khoe/\">sức khỏe</a>. Ngoài ra, bạn có một cách khác để nâng cao thể lực là từ bỏ những thói quen xấu bạn đang làm hàng ngày. Điều này chắc chắn dễ dàng hơn nhiều so với việc xây dựng những điều mới. Dưới đây là ba việc các bác sĩ Nhật khuyên bạn nên chấm dứt:&nbsp;</p>\r\n\r\n<p>1. Không tập luyện, đặc biệt là chạy</p>\r\n\r\n<p><img alt=\"Lời khuyên ngược đời để có sức khỏe tốt của bác sĩ Nhật\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/28/16/loi-khuyen-nguoc-doi-de-co-suc-khoe-tot-cua-bac-si-nhat.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Chạy bộ có thể dẫn tới một số chấn thương. Ảnh minh họa: Verywell Fit</p>\r\n\r\n<p>Với nhiều người, môn thể thao phổ biến nhất là chạy nhưng họ không biết rằng hoạt động này không phù hợp với tất cả mọi người. Khi bạn chạy, các cơ quan phải hoạt động tích cực hơn, nhịp tim tăng nhanh, thở gấp. Cơ thể đột ngột bước vào trạng thái vận động mạnh, năng lượng đạt mức cao.</p>\r\n\r\n<p>Nói cách khác, cơ thể bạn sẽ bị sốc, rất phấn khích, rơi vào trạng thái hoảng loạn khi hăng hái chạy quá mức. Nếu có thói quen chạy thường xuyên, bạn còn có thể gặp hiện tượng giảm oxy huyết.</p>\r\n\r\n<p>Ngược lại, về cơ bản, việc đi bộ hầu như không có nguy cơ gì, không có giới hạn độ tuổi. Bất cứ ai cũng có thể đi bộ bất cứ khi nào, ở đâu mà không cần chuẩn bị gì quá đặc biệt.</p>\r\n\r\n<p>Do đó, nếu bạn chỉ muốn tập trung cải thiện sức khỏe, hạn chế tối đa các rủi ro chấn thương, đi bộ là đủ.</p>\r\n\r\n<p>2. Không dùng thuốc ngay khi bị ốm</p>\r\n\r\n<p><img alt=\"Lời khuyên ngược đời để có sức khỏe tốt của bác sĩ Nhật\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/28/16/loi-khuyen-nguoc-doi-de-co-suc-khoe-tot-cua-bac-si-nhat-1.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Bạn cần cân nhắc kỹ trước khi uống thuốc. Ảnh minh họa: New scientist</p>\r\n\r\n<p>Nhiều người có thói quen sẽ mua thuốc uống khi chỉ hơi ốm, mệt. Tuy nhiên, thuốc có thể gây độc cho bạn. Bởi vậy, bạn cần tránh hết mức việc sử dụng thuốc thay vì tùy tiện mua và uống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những bác sĩ có tâm cũng sẽ cố hết sức để điều trị cho bệnh nhân với ít thuốc và ít thời gian nhất có thể.</p>\r\n\r\n<p>Tất nhiên, có những trường hợp bạn buộc phải uống thuốc ngay, không trì hoãn. Đó là khi bạn bị dị ứng thực phẩm, hen suyễn cấp, tiểu đường type 1.</p>\r\n\r\n<p>Mọi người nên rèn luyện sức khỏe, nâng cao sức đề kháng để cơ thể không phụ thuộc vào thuốc.</p>\r\n\r\n<p>3. Không là người tốt</p>\r\n\r\n<p><img alt=\"Lời khuyên ngược đời để có sức khỏe tốt của bác sĩ Nhật\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/28/16/loi-khuyen-nguoc-doi-de-co-suc-khoe-tot-cua-bac-si-nhat-2.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Luôn cố gắng giúp đỡ tất cả mọi người sẽ khiến bạn bị áp lực. Ảnh minh họa: Idea scale</p>\r\n\r\n<p>Bạn có thường được những người xung quanh khen là người tốt bụng không? Tốt nhất bạn nên thay đổi cách sống của mình ngay lập tức.</p>\r\n\r\n<p>Những người tốt thường có các nét chung như trung thành, rất trách nhiệm, luôn lắng nghe, tốt với mọi người, cố gắng làm hài lòng, giúp đỡ tất cả…</p>\r\n\r\n<p>Các chuyên gia tâm lý thường đưa ra lời khuyên “bạn không nên cố gắng là người tốt\". Nếu cố gắng, bạn sẽ dễ dàng rơi vào bẫy trầm cảm, áp lực. Đáp ứng mọi tiêu chí trên và có được sự vui vẻ, thoải mái là điều không thể.</p>\r\n\r\n<p>“Bệnh từ tâm” là câu nói phổ biến và được chứng minh trong thực tế. Nhiều căn bệnh mạn tính liên quan tới máu, thần kinh có liên quan trực tiếp tới stress.</p>', '0Ta0_loi-khuyen-nguoc-doi-de-co-suc-khoe-tot-cua-bac-si-nhat-240x160.jpg', 0, 0, 58, '2020-08-15', '2020-08-15'),
(138, 'Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được', 'bi-quyet-song-tho-nam-o-ba-viec-don-gian-nhung-it-nguoi-lam-duoc', '<p>Chỉ cần bạn quản lý tốt miệng, tinh thần và giấc ngủ, chắc chắn bạn sẽ giảm thiểu được nhiều bệnh tật, sống khỏe mạnh trong thời gian dài.&nbsp;</p>', '<p>Ai trong số chúng ta cũng đều hy vọng tuổi thọ của mình sẽ dài hơn. Trên thực tế, có rất nhiều thói quen tốt trong cuộc sống hàng ngày giúp bạn sống lâu, khỏe mạnh. Tuy nhiên, không phải ai cũng thực hiện được những hành động đó mỗi ngày:&nbsp;</p>\r\n\r\n<p>1.&nbsp;Kiểm soát&nbsp;tốt miệng của mình</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Ảnh minh họa: Forkoverknife</p>\r\n\r\n<p>Bạn nên ăn ít thực phẩm có chứa chất bảo quản, đồ chiên, hun khói, nhiều chất béo, quá ngọt. Trong chế độ ăn uống, bạn cần chú ý bổ sung rau quả tươi.</p>\r\n\r\n<p>Tuy nhiên, nhiều người không thể kiểm soát miệng của họ, ngay cả khi đặt quyết tâm nhưng ngay sau đó họ lại ăn những thứ gì mình muốn.</p>\r\n\r\n<p>Mức sống ngày càng tốt hơn nhưng cơ thể bạn sẽ không thể chịu đựng được nếu bạn suốt ngày ăn thịt cá. Chế độ ăn nhiều chất béo không chỉ gây ra mỡ máu, huyết áp cao, tiểu đường mà còn dẫn tới ung thư,&nbsp;giảm&nbsp;tuổi thọ.&nbsp;</p>\r\n\r\n<p>2. Không thức khuya</p>\r\n\r\n<p>Trong xã hội ngày nay, nhịp sống nhanh và áp lực công việc ngày càng nặng nề. Nhiều người gặp rắc rối với giấc ngủ trong một thời gian dài. Những biểu hiện phổ biến của họ là khó ngủ, dễ tỉnh giấc, mơ nhiều…</p>\r\n\r\n<p>Không ít người biết rằng thiếu ngủ triền miên sẽ khiến khả năng miễn dịch của cơ thể bị suy giảm, gây ra một số bệnh mạn tính như bệnh tim, tiểu đường, u bướu...</p>\r\n\r\n<p>Thức khuya là một thói quen xấu, có hại cho&nbsp;<a href=\"https://vietnamnet.vn/vn/suc-khoe/\">sức khỏe</a>&nbsp;của bạn. Thường xuyên ngủ muộn không chỉ gây ra suy nhược mà còn khiến bạn mắc một số bệnh, thậm chí dẫn đến tử vong đột ngột.&nbsp;Bạn nên đi ngủ trước 23h để cơ thể có thể phục hồi sau một ngày dài.&nbsp;</p>\r\n\r\n<p>3. Tâm trạng thoải mái</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-1.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Ảnh minh họa: Freepik</p>\r\n\r\n<p>Tuổi thọ của một người cả ngày buồn rầu sẽ kém hơn nhiều so với một người cười nói vui vẻ mỗi ngày, bởi tâm lý thoải mái khiến phiền não trong lòng tiêu tan.</p>\r\n\r\n<p>Tính cách của mỗi người có liên quan mật thiết đến sự hình thành của bệnh tật. Những người dễ bị kích động, nóng nảy, hẹp hòi dễ mắc bệnh tim, cao huyết áp, ung thư. Những người có tâm hồn tươi vui, cơ thể cũng trẻ trung hơn.&nbsp;</p>\r\n\r\n<p>Cảm xúc tiêu cực của con người sẽ gây ra các rối loạn nội tiết của cơ thể, dẫn đến suy giảm sức đề kháng.</p>\r\n\r\n<p>Ngoài thực hiện&nbsp;ba điều trên, bạn có thể ăn&nbsp;bốn&nbsp;loại thực phẩm tốt cho sức khỏe:&nbsp;</p>\r\n\r\n<p>Tỏi</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-2.jpg\" style=\"height:513px; width:770px\" /></p>\r\n\r\n<p>Tỏi không chỉ ngăn ngừa cảm lạnh mà còn làm giảm nguy cơ ung thư dạ dày và ruột, tăng cường chức năng tiêu hóa. Ngoài ra, tỏi cũng có thể thanh lọc các mạch máu, phòng ngừa tắc nghẽn mạch máu.</p>\r\n\r\n<p>Đậu phụ</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-3.jpg\" style=\"height:515px; width:770px\" /></p>\r\n\r\n<p>Ảnh minh họa: Medium</p>\r\n\r\n<p>Thành phần chính của đậu phụ là protein và isoflavone. Đậu phụ có tác dụng lợi khí, giảm suy nhược, giảm nồng độ chì trong máu, bảo vệ gan và thúc đẩy quá trình trao đổi chất của cơ thể.</p>\r\n\r\n<p>Ăn đậu phụ thường xuyên có lợi cho sức khỏe và phát triển trí tuệ. Người cao tuổi hay ăn đậu phụ có tác dụng điều trị tốt đối với chứng xơ cứng động mạch và loãng xương.</p>\r\n\r\n<p>Sữa</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-4.jpg\" style=\"height:578px; width:770px\" /></p>\r\n\r\n<p>Ảnh minh họa: Patch</p>\r\n\r\n<p>Uống sữa mỗi ngày làm giảm nguy cơ trúng gió và bảo vệ xương. Uống sữa là thói quen phổ biến của người già sống thọ. Kali trong sữa giữ cho các động mạch máu ổn định khi bạn bị huyết áp cao, giảm nguy cơ trúng gió.</p>\r\n\r\n<p>Canxi trong sữa giúp xương và răng chắc khỏe, giảm sự xuất hiện của bệnh thoái hóa xương khớp. Uống sữa thường xuyên có thể ngăn ngừa xơ cứng động mạch.</p>\r\n\r\n<p>Táo</p>\r\n\r\n<p><img alt=\"Bí quyết sống thọ nằm ở ba việc đơn giản nhưng ít người làm được\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/07/23/16/bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-5.jpg\" style=\"height:404px; width:770px\" /></p>\r\n\r\n<p>Ảnh minh họa: MSN</p>\r\n\r\n<p>Táo là một trong những trái cây tốt cho sức khỏe và các chất dinh dưỡng trong loại quả này tương đối toàn diện. Táo rất giàu vitamin C, axit amin và khoáng chất.</p>\r\n\r\n<p>Táo có thể nuôi dưỡng cơ thể và cải thiện chức năng miễn dịch, ngăn ngừa sự xuất hiện của các bệnh, kéo dài tuổi thọ.</p>', '9MIN_bi-quyet-song-tho-nam-trong-3-viec-don-gian-nhung-it-nguoi-lam-duoc-240x160.jpg', 0, 0, 58, '2020-08-15', '2020-08-15');

